import React, { useState } from 'react';

const MedecinDashboard = () => {
  const [activeTab, setActiveTab] = useState('ordonnances');
  const [ordonnance, setOrdonnance] = useState({
    patient: '',
    medicaments: [
      { nom: 'ParacÃ©tamol 1g', dose: '1cp', frequence: '3x/jour', duree: '5 jours' },
      { nom: 'Amoxicilline 500mg', dose: '1cp', frequence: '2x/jour', duree: '7 jours' }
    ]
  });

  // âœ… FONCTIONS SIMPLIFIÃ‰ES ET FONCTIONNELLES
  const addMedicament = () => {
    setOrdonnance({
      ...ordonnance,
      medicaments: [...ordonnance.medicaments, { nom: '', dose: '', frequence: '', duree: '' }]
    });
  };

  const updateMedicament = (index, field, value) => {
    const newMeds = [...ordonnance.medicaments];
    newMeds[index][field] = value;
    setOrdonnance({ ...ordonnance, medicaments: newMeds });
  };

  const deleteMedicament = (index) => {
    const newMeds = ordonnance.medicaments.filter((_, i) => i !== index);
    setOrdonnance({ ...ordonnance, medicaments: newMeds });
  };

  const handlePatientChange = (e) => {
    setOrdonnance({ ...ordonnance, patient: e.target.value });
  };

  const saveOrdonnance = () => {
    if (!ordonnance.patient) {
      alert('âŒ SÃ©lectionnez un patient');
      return;
    }
    alert(`âœ… ORDONNANCE VALIDÃ‰E\n\n${ordonnance.patient}\n\n` + 
      ordonnance.medicaments.map((m, i) => 
        `${i+1}. ${m.nom || 'Non renseignÃ©'}`
      ).join('\n')
    );
  };

  return (
    <div style={{ 
      minHeight: '100vh', 
      background: '#f8fafc',
      padding: '24px',
      fontFamily: '-apple-system, BlinkMacSystemFont, sans-serif'
    }}>
      <div style={{ maxWidth: '1400px', margin: '0 auto' }}>
        
        {/* HEADER PROFESSIONNEL */}
        <div style={{
          background: 'white',
          borderRadius: '16px',
          boxShadow: '0 4px 20px rgba(0,0,0,0.08)',
          border: '1px solid #e2e8f0',
          padding: '32px',
          marginBottom: '32px'
        }}>
          <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', flexWrap: 'wrap', gap: '24px' }}>
            <div>
              <h1 style={{
                fontSize: '36px',
                fontWeight: '700',
                color: '#1e293b',
                margin: '0 0 8px 0'
              }}>
                ðŸ©º Espace MÃ©decin
              </h1>
              <p style={{ fontSize: '18px', color: '#64748b', margin: 0 }}>
                Dr Ndiaye Mamadou - Clinique Dakar
              </p>
            </div>
            <div style={{ textAlign: 'right' }}>
              <div style={{ fontSize: '28px', fontWeight: '700', color: '#059669' }}>
                1 250 000 FCFA
              </div>
              <div style={{ fontSize: '14px', color: '#64748b' }}>Consultations fÃ©vrier</div>
            </div>
          </div>
        </div>

        {/* NAVIGATION PROFESSIONNELLE */}
        <div style={{
          background: 'white',
          borderRadius: '16px',
          boxShadow: '0 4px 20px rgba(0,0,0,0.08)',
          border: '1px solid #e2e8f0',
          padding: '24px',
          marginBottom: '32px',
          display: 'flex',
          gap: '12px',
          flexWrap: 'wrap'
        }}>
          <TabButton 
            label="ðŸ’Š Ordonnances" 
            active={activeTab === 'ordonnances'}
            onClick={() => setActiveTab('ordonnances')}
          />
          <TabButton label="ðŸ‘¥ Patients" active={activeTab === 'patients'} onClick={() => setActiveTab('patients')} />
          <TabButton label="ðŸ“… RDV" active={activeTab === 'rdv'} onClick={() => setActiveTab('rdv')} />
          <TabButton label="ðŸ“Š Statistiques" active={activeTab === 'stats'} onClick={() => setActiveTab('stats')} />
        </div>

        {/* ORDONNANCES - STYLE PROPRE */}
        {activeTab === 'ordonnances' && (
          <div style={{
            background: 'white',
            borderRadius: '16px',
            boxShadow: '0 4px 20px rgba(0,0,0,0.08)',
            border: '1px solid #e2e8f0',
            padding: '32px'
          }}>
            <h2 style={{ 
              fontSize: '28px', 
              fontWeight: '600', 
              color: '#1e293b', 
              margin: '0 0 32px 0' 
            }}>
              Nouvelle ordonnance
            </h2>

            {/* PATIENT */}
            <div style={{ marginBottom: '28px' }}>
              <label style={{ 
                display: 'block', 
                fontSize: '16px', 
                fontWeight: '600', 
                color: '#374151', 
                marginBottom: '12px' 
              }}>
                Patient
              </label>
              <select 
                value={ordonnance.patient}
                onChange={handlePatientChange}
                style={inputStyle}
              >
                <option value="">SÃ©lectionner un patient</option>
                <option>Diop Aminata - 32 ans</option>
                <option>Ndiaye Fatou - 45 ans</option>
                <option>Sow Mamadou - 28 ans</option>
              </select>
            </div>

            {/* MÃ‰DICAMENTS */}
            <div style={{ marginBottom: '32px' }}>
              <div style={{ 
                display: 'flex', 
                justifyContent: 'space-between', 
                alignItems: 'center', 
                marginBottom: '20px',
                flexWrap: 'wrap',
                gap: '16px'
              }}>
                <h3 style={{ 
                  fontSize: '20px', 
                  fontWeight: '600', 
                  color: '#1e293b', 
                  margin: 0 
                }}>
                  MÃ©dicaments
                </h3>
                <button 
                  onClick={addMedicament}
                  style={btnPrimary}
                >
                  âž• Ajouter mÃ©dicament
                </button>
              </div>

              <div style={{ display: 'flex', flexDirection: 'column', gap: '16px' }}>
                {ordonnance.medicaments.map((med, index) => (
                  <MedicamentRow 
                    key={index}
                    medicament={med}
                    index={index}
                    onUpdate={updateMedicament}
                    onDelete={() => deleteMedicament(index)}
                  />
                ))}
              </div>
            </div>

            {/* ACTIONS */}
            <div style={{ 
              display: 'flex', 
              gap: '16px', 
              justifyContent: 'flex-end',
              flexWrap: 'wrap'
            }}>
              <button onClick={saveOrdonnance} style={btnSuccess}>
                âœ… Finaliser ordonnance
