import React, { useState, useEffect } from 'react';
import api from '../../api/endpoint'; // ✅ CORRIGÉ
import { FiCalendar, FiClock, FiUser, FiSearch, FiEye, FiCheckCircle, FiClock as FiClockLoading } from 'react-icons/fi';

const MesRendezVous = () => {
  const [rendezVous, setRendezVous] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');

  useEffect(() => {
    loadRendezVous();
  }, []);

  const loadRendezVous = async () => {
    try {
      setLoading(true);
      setError(null);
      
      // ✅ API CORRIGÉE + ROUTE OPTIMISÉE MÉDECIN
      const data = await api.getData('/rendezvous/medecin');
      setRendezVous(Array.isArray(data) ? data : []);
    } catch (err) {
      console.error('Erreur rendez-vous:', err);
      setError(err.message || 'Impossible de charger les rendez-vous.');
      setRendezVous([]);
    } finally {
      setLoading(false);
    }
  };

  const filteredRendezVous = rendezVous.filter(rdv => {
    const patientName = rdv.patientId?.fullName || '';
    const motif = rdv.motif || '';
    return patientName.toLowerCase().includes(searchTerm.toLowerCase()) ||
           motif.toLowerCase().includes(searchTerm.toLowerCase());
  });

  const getStatusColor = (statut) => {
    switch (statut) {
      case 'CONFIRME': return 'bg-green-100 text-green-800';
      case 'EN_ATTENTE': return 'bg-yellow-100 text-yellow-800';
      case 'ANNULE': return 'bg-red-100 text-red-800';
      default: return 'bg-gray-100 text-gray-800';
    }
  };

  const formatDateTime = (dateString) => {
    return new Date(dateString).toLocaleString('fr-FR', {
      weekday: 'long',
      year: 'numeric',
      month: 'long',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center min-h-[500px] p-8">
        <div className="text-center animate-pulse">
          <FiClockLoading className="animate-spin mx-auto mb-4" size={48} />
          <p className="text-xl text-gray-600">Chargement de vos rendez-vous...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="p-6 max-w-6xl mx-auto">
      {/* HEADER */}
      <div className="flex flex-col lg:flex-row justify-between items-start lg:items-center mb-12 gap-6">
        <div>
          <h1 className="text-4xl font-bold text-gray-900 flex items-center gap-4 mb-2">
            <FiCalendar size={48} className="text-blue-600" />
            Mes Rendez-vous
          </h1>
          <p className="text-xl text-gray-600">({filteredRendezVous.length} rendez-vous)</p>
        </div>
        
        <div className="relative w-full lg:w-80">
          <FiSearch className="absolute left-4 top-1/2 -translate-y-1/2 text-gray-400" size={20} />
          <input
            type="text"
            placeholder="Rechercher patient ou motif..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            className="w-full pl-12 pr-6 py-4 border-2 border-gray-200 rounded-2xl focus:ring-4 focus:ring-blue-100 focus:border-blue-500 shadow-sm"
          />
        </div>
      </div>

      {/* ERROR */}
      {error && (
        <div className="bg-red-50 border-2 border-red-200 text-red-800 px-8 py-6 rounded-3xl mb-12 shadow-lg">
          <div className="flex items-center gap-3">
            <FiClock size={24} />
            <span>{error}</span>
          </div>
        </div>
      )}

      {/* AUCUN RENDEZ-VOUS */}
      {filteredRendezVous.length === 0 ? (
        <div className="bg-gradient-to-br from-gray-50 to-gray-100 rounded-3xl p-16 text-center border border-gray-200 shadow-xl">
          <FiCalendar size={72} className="mx-auto mb-8 text-gray-400 opacity-75" />
          <h2 className="text-3xl font-bold text-gray-900 mb-4">Aucun rendez-vous trouvé</h2>
          <p className="text-xl text-gray-600 mb-8 max-w-md mx-auto leading-relaxed">
            {searchTerm 
              ? `Aucun rendez-vous ne correspond à "${searchTerm}".`
              : "Vos prochains rendez-vous apparaîtront ici dès qu'ils seront planifiés."
            }
          </p>
          <div className="bg-white rounded-2xl p-6 shadow-md inline-block">
            <FiCalendar size={24} className="inline mr-2 text-blue-600" />
            <span className="text-lg font-semibold text-gray-800">Restez à l'écoute !</span>
          </div>
        </div>
      ) : (
        /* LISTE CARDS RESPONSIVE */
        <div className="grid grid-cols-1 lg:grid-cols-2 xl:grid-cols-1 gap-6">
          {filteredRendezVous.map((rdv) => (
            <div 
              key={rdv._id} 
              className="group bg-white rounded-3xl shadow-xl p-8 border border-gray-100 hover:shadow-2xl hover:-translate-y-2 transition-all duration-300 hover:border-blue-200"
            >
              <div className="flex items-start justify-between mb-6">
                <div className="flex items-center gap-4 flex-1 min-w-0">
                  <div className="p-3 bg-gradient-to-br from-blue-500 to-blue-600 rounded-2xl text-white shadow-lg">
                    <FiCalendar size={24} />
                  </div>
                  <div>
                    <h3 className="text-2xl font-bold text-gray-900 truncate group-hover:text-blue-600 transition-colors">
                      {rdv.motif || 'Consultation médicale'}
                    </h3>
                    <div className="flex items-center gap-6 mt-2 text-sm text-gray-500">
                      <div className="flex items-center gap-2">
                        <FiUser size={16} />
                        <span className="font-medium">
                          {rdv.patientId?.fullName || 'Patient non spécifié'}
                        </span>
                      </div>
                      <div className="flex items-center gap-2">
                        <FiClock size={16} />
                        <span>{formatDateTime(rdv.dateRendezVous)}</span>
                      </div>
                    </div>
                  </div>
                </div>
                
                <span className={`px-4 py-2 rounded-2xl text-sm font-bold shadow-md whitespace-nowrap ${
                  getStatusColor(rdv.statut)
                }`}>
                  {rdv.statut === 'CONFIRME' && <FiCheckCircle className="inline mr-1" size={16} />}
                  {rdv.statut}
                </span>
              </div>

              {/* ACTIONS */}
              <div className="flex flex-col sm:flex-row gap-3 pt-4 border-t border-gray-100">
                <button className="flex-1 bg-gradient-to-r from-blue-500 to-blue-600 text-white py-3 px-6 rounded-xl font-semibold shadow-lg hover:shadow-xl hover:scale-[1.02] transition-all text-center">
                  Voir Détails
                </button>
                {rdv.statut === 'EN_ATTENTE' && (
                  <button className="px-6 py-3 bg-green-500 text-white rounded-xl font-semibold shadow-lg hover:shadow-xl hover:scale-[1.02] transition-all">
                    Confirmer
                  </button>
                )}
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default MesRendezVous;
