import React, { useEffect, useState } from "react";

const mockRendezVous = [
  { id: 1, date: "2026-02-10", heure: "10:00", medecin: "Dr. Ndiaye", service: "Cardiologie" },
  { id: 2, date: "2026-02-15", heure: "14:00", medecin: "Dr. Diop", service: "Dermatologie" },
];

const mockDossiers = [
  { id: 1, titre: "Analyse sanguine", date: "2026-01-20", medecin: "Dr. Ndiaye" },
  { id: 2, titre: "Radiographie thorax", date: "2026-01-25", medecin: "Dr. Diop" },
];

const PatientDashboard = () => {
  const [rendezVous, setRendezVous] = useState([]);
  const [dossiers, setDossiers] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Simuler un fetch avec un délai
    const fetchData = async () => {
      setLoading(true);
      // Ici tu pourrais utiliser un getData si le backend était disponible
      await new Promise((r) => setTimeout(r, 500)); // délai fictif
      setRendezVous(mockRendezVous);
      setDossiers(mockDossiers);
      setLoading(false);
    };

    fetchData();
  }, []);

  if (loading) return <p className="text-center mt-10">Chargement des données...</p>;

  return (
    <div className="p-6">
      <h1 className="text-3xl font-bold mb-6">Bienvenue sur votre espace patient</h1>

      <section className="mb-8">
        <h2 className="text-2xl font-semibold mb-4">Rendez-vous à venir</h2>
        {rendezVous.length === 0 ? (
          <p>Aucun rendez-vous à venir.</p>
        ) : (
          <ul className="border rounded p-4">
            {rendezVous.map((rdv) => (
              <li key={rdv.id} className="mb-2">
                <strong>Date :</strong> {rdv.date} <br />
                <strong>Heure :</strong> {rdv.heure} <br />
                <strong>Médecin :</strong> {rdv.medecin} <br />
                <strong>Service :</strong> {rdv.service}
              </li>
            ))}
          </ul>
        )}
      </section>

      <section>
        <h2 className="text-2xl font-semibold mb-4">Dossiers médicaux</h2>
        {dossiers.length === 0 ? (
          <p>Aucun dossier disponible.</p>
        ) : (
          <ul className="border rounded p-4">
            {dossiers.map((dossier) => (
              <li key={dossier.id} className="mb-2">
                <strong>Titre :</strong> {dossier.titre} <br />
                <strong>Date :</strong> {dossier.date} <br />
                <strong>Médecin :</strong> {dossier.medecin}
              </li>
            ))}
          </ul>
        )}
      </section>
    </div>
  );
};

export default PatientDashboard;
