import { useState, useEffect } from 'react';

// ✅ MOCK DATA (remplacez par vraies API plus tard)
const mockProfile = {
  nom: 'Aminata DIOP', prenom: 'Aminata', email: 'aminata.diop@email.com',
  telephone: '+221 78 123 4567', dateNaissance: '15/03/1990', age: 35,
  sexe: 'Féminin', groupeSanguin: 'O+', allergies: 'Aucune connue'
};

const mockRDV = [
  { _id: '1', medecin: { nom: 'Dr. Moustapha NDIAYE' }, specialite: 'Cardiologue', 
    date: '15/02/2026 14:00', statut: 'Confirmé', pharmacie: null },
  { _id: '2', medecin: { nom: 'Dr. Awa SOW' }, specialite: 'Gynécologue', 
    date: '20/02/2026 16:30', statut: 'Confirmé', pharmacie: null }
];

const mockDossier = {
  derniereConsultation: { date: '08/02/2026', medecin: 'Dr. NDIAYE' },
  totalConsultations: 12, antecedents: 'Hypertension familiale'
};

const mockOrdonnances = [
  { _id: '1', medecin: 'Dr. NDIAYE', date: '08/02/2026', medicaments: 'Paracétamol 500mg',
    dosage: '1 cp x 3/jour', duree: '7 jours', statut: 'Active', pharmacie: { nom: 'Pharmacie du Centre' } },
  { _id: '2', medecin: 'Dr. SOW', date: '01/02/2026', medicaments: 'Vitamine D3 1000UI',
    dosage: '1 cp/jour', duree: '3 mois', statut: 'Expirée', pharmacie: { nom: 'Pharmacie du Centre' } }
];

// ✅ HOOKS SIMPLIFIÉS (100% fonctionnels)
export const usePatientProfile = () => {
  const [profile, setProfile] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Simule API call (2 secondes)
    const timer = setTimeout(() => {
      setProfile(mockProfile);
      setLoading(false);
    }, 1500);
    return () => clearTimeout(timer);
  }, []);

  return { profile, loading };
};

export const usePatientRDV = () => {
  const [rdv, setRDV] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const timer = setTimeout(() => {
      setRDV(mockRDV);
      setLoading(false);
    }, 1200);
    return () => clearTimeout(timer);
  }, []);

  return { rdv, loading };
};

export const usePatientDossier = () => {
  const [dossier, setDossier] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const timer = setTimeout(() => {
      setDossier(mockDossier);
      setLoading(false);
    }, 1000);
    return () => clearTimeout(timer);
  }, []);

  return { dossier, loading };
};

export const usePatientOrdonnances = () => {
  const [ordonnances, setOrdonnances] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const timer = setTimeout(() => {
      setOrdonnances(mockOrdonnances);
      setLoading(false);
    }, 800);
    return () => clearTimeout(timer);
  }, []);

  return { ordonnances, loading };
};
