import React, { useState, useEffect } from 'react';
import { getData, postData } from '../api/endpoint';
import CreateDossierModal from './CreateDossierModal';
import PatientsList from './PatientsList';

const DoctorDashboard = () => {
  const [dossiers, setDossiers] = useState([]);
  const [patients, setPatients] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showCreateModal, setShowCreateModal] = useState(false);
  const [activeTab, setActiveTab] = useState('dossiers'); // 'dossiers' | 'patients'

  const loadDossiers = async () => {
    try {
      setLoading(true);
      const data = await getData('/dossier/mes-dossiers');
      setDossiers(data || []);
    } catch (err) {
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  const loadPatients = async () => {
    try {
      const data = await getData('/patients');
      setPatients(data || []);
    } catch (err) {
      console.error(err);
    }
  };

  useEffect(() => {
    loadDossiers();
    loadPatients();
  }, []);

  return (
    <div className="doctor-dashboard" style={{ padding: '2rem', maxWidth: '1200px', margin: '0 auto' }}>
      <header style={{ marginBottom: '2rem' }}>
        <h1>üè• Espace M√©decin</h1>
        <p>Bienvenue Dr. {localStorage.getItem('userName') || 'Utilisateur'}</p>
      </header>

      {/* onglets */}
      <div style={{ marginBottom: '2rem' }}>
        <button 
          className={activeTab === 'dossiers' ? 'active' : ''}
          onClick={() => setActiveTab('dossiers')}
          style={{
            padding: '1rem 2rem', marginRight: '1rem',
            background: activeTab === 'dossiers' ? '#1976d2' : '#e3f2fd',
            color: activeTab === 'dossiers' ? 'white' : '#1976d2',
            border: 'none', borderRadius: '8px', cursor: 'pointer'
          }}
        >
          üìÅ Mes Dossiers ({dossiers.length})
        </button>
        <button 
          className={activeTab === 'patients' ? 'active' : ''}
          onClick={() => setActiveTab('patients')}
          style={{
            padding: '1rem 2rem',
            background: activeTab === 'patients' ? '#1976d2' : '#e3f2fd',
            color: activeTab === 'patients' ? 'white' : '#1976d2',
            border: 'none', borderRadius: '8px', cursor: 'pointer'
          }}
        >
          üë• Patients ({patients.length})
        </button>
      </div>

      {/* BOUTON CR√âER DOSSIER - Toujours visible */}
      <div style={{ marginBottom: '2rem' }}>
        <button 
          onClick={() => setShowCreateModal(true)}
          style={{
            padding: '1rem 2rem',
            background: '#4caf50', color: 'white',
            border: 'none', borderRadius: '8px',
            fontSize: '1.1rem', cursor: 'pointer',
            boxShadow: '0 4px 12px rgba(76,175,80,0.3)'
          }}
        >
          ‚ûï Nouveau Dossier Patient
        </button>
      </div>

      {/* Contenu onglets */}
      {activeTab === 'dossiers' && (
        <DossiersTable dossiers={dossiers} loading={loading} />
      )}
      
      {activeTab === 'patients' && (
        <PatientsList patients={patients} />
      )}

      {showCreateModal && (
        <CreateDossierModal 
          onClose={() => setShowCreateModal(false)}
          onSuccess={() => {
            loadDossiers();
            setShowCreateModal(false);
          }}
        />
      )}
    </div>
  );
};

// Composant tableau dossiers
const DossiersTable = ({ dossiers, loading }) => (
  <div>
    {loading ? (
      <div style={{ textAlign: 'center', padding: '4rem' }}>Chargement...</div>
    ) : dossiers.length === 0 ? (
      <div style={{ textAlign: 'center', padding: '4rem', color: '#666' }}>
        Aucun dossier. <br />
        <strong>Cr√©ez le premier dossier patient !</strong>
      </div>
    ) : (
      <table style={{ 
        width: '100%', borderCollapse: 'collapse', 
        background: 'white', boxShadow: '0 4px 20px rgba(0,0,0,0.1)'
      }}>
        <thead>
          <tr style={{ backgroundColor: '#f8f9fa' }}>
            <th style={{ padding: '1.5rem 1rem', textAlign: 'left' }}>Patient</th>
            <th style={{ padding: '1.5rem 1rem', textAlign: 'left' }}>Sexe</th>
            <th style={{ padding: '1.5rem 1rem', textAlign: 'left' }}>Naissance</th>
            <th style={{ padding: '1.5rem 1rem', textAlign: 'left' }}>Groupe Sanguin</th>
            <th style={{ padding: '1.5rem 1rem', textAlign: 'left' }}>R√©sum√©</th>
          </tr>
        </thead>
        <tbody>
          {dossiers.map(dossier => (
            <tr key={dossier._id} style={{ borderBottom: '1px solid #eee' }}>
              <td style={{ padding: '1.5rem 1rem' }}>
                <strong>{dossier.patientId?.fullName || 'N/A'}</strong>
              </td>
              <td style={{ padding: '1.5rem 1rem' }}>{dossier.patientId?.sexe}</td>
              <td style={{ padding: '1.5rem 1rem' }}>
                {dossier.patientId?.date_of_birth && 
                  new Date(dossier.patientId.date_of_birth).toLocaleDateString('fr-FR')}
              </td>
              <td style={{ padding: '1.5rem 1rem' }}>{dossier.groupeSanguin}</td>
              <td style={{ padding: '1.5rem 1rem', maxWidth: '300px' }}>
                {dossier.resumeMedical}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    )}
  </div>
);

export default DoctorDashboard;
