import React, { useState, useEffect } from 'react';
import { Link, Routes, Route, useLocation, useNavigate } from 'react-router-dom';

const PatientDashboard = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const [profile, setProfile] = useState(null);
  const [prochainsRDV, setProchainsRDV] = useState([]);
  const [dossier, setDossier] = useState(null);
  const [ordonnances, setOrdonnances] = useState([]);
  const [loading, setLoading] = useState(true);

  // ‚úÖ SIMULATION API (stable)
  useEffect(() => {
    const timer = setTimeout(() => {
      setProfile({
        nom: 'Aminata DIOP', prenom: 'Aminata', email: 'aminata.diop@email.com',
        telephone: '+221 78 123 4567', age: 35, sexe: 'F√©minin', 
        groupeSanguin: 'O+', allergies: 'Aucune connue'
      });
      setProchainsRDV([
        { _id: '1', medecin: { nom: 'Dr. Moustapha NDIAYE' }, specialite: 'Cardiologue', 
          date: '15/02/2026 14:00', statut: 'Confirm√©' },
        { _id: '2', medecin: { nom: 'Dr. Awa SOW' }, specialite: 'Gyn√©cologue', 
          date: '20/02/2026 16:30', statut: 'Confirm√©' }
      ]);
      setDossier({ totalConsultations: 12, derniereConsultation: { date: '08/02/2026', medecin: 'Dr. NDIAYE' } });
      setOrdonnances([
        { _id: '1', medecin: 'Dr. NDIAYE', medicaments: 'Parac√©tamol 500mg', statut: 'Active' }
      ]);
      setLoading(false);
    }, 1000);
    return () => clearTimeout(timer);
  }, []);

  if (loading) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-blue-50 to-indigo-100 flex items-center justify-center p-8">
        <div className="text-center max-w-md mx-auto">
          <div className="w-32 h-32 border-4 border-blue-100 border-t-4 border-blue-500 rounded-full animate-spin mx-auto mb-12 shadow-2xl"></div>
          <h1 className="text-5xl font-black text-gray-900 mb-6">Chargement Dashboard</h1>
          <div className="space-y-3">
            <div className="flex items-center gap-4 text-xl text-blue-600">
              <div className="w-4 h-4 bg-blue-500 rounded-full animate-bounce"></div>
              Profil patient...
            </div>
            <div className="flex items-center gap-4 text-xl text-emerald-600">
              <div className="w-4 h-4 bg-emerald-500 rounded-full animate-bounce" style={{animationDelay: '0.1s'}}></div>
              Rendez-vous...
            </div>
            <div className="flex items-center gap-4 text-xl text-purple-600">
              <div className="w-4 h-4 bg-purple-500 rounded-full animate-bounce" style={{animationDelay: '0.2s'}}></div>
              Dossier m√©dical...
            </div>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 via-indigo-50 to-purple-50 overflow-x-hidden">
      {/* Sidebar fixe */}
      <aside className="hidden lg:block lg:w-72 lg:h-screen lg:fixed lg:inset-y-0 lg:z-40 bg-white/95 backdrop-blur-xl shadow-2xl border-r-4 border-blue-200/60">
        <div className="p-8 sticky top-0 bg-gradient-to-b from-blue-50/90 to-white/80 border-b-2 border-blue-100/50">
          <div className="w-24 h-24 bg-gradient-to-br from-blue-500 to-indigo-600 rounded-2xl flex items-center justify-center mx-auto mb-6 shadow-xl ring-4 ring-white/50 hover:scale-105 transition-all">
            <span className="text-3xl text-white">üë§</span>
          </div>
          <h2 className="text-3xl font-black text-gray-900 text-center mb-4 truncate">
            {profile?.nom}
          </h2>
          <div className="text-center text-xl space-y-1 bg-blue-50/80 p-4 rounded-xl border border-blue-200/50">
            <p className="font-bold text-blue-700">{profile?.age} ans ‚Ä¢ {profile?.sexe}</p>
            <p className="text-blue-600 text-sm flex items-center justify-center gap-1">
              <span>{profile?.groupeSanguin}</span>
              <span>‚Ä¢</span>
              <span className="truncate max-w-[150px]">{profile?.allergies}</span>
            </p>
          </div>
        </div>
        
        <nav className="p-6 mt-8 space-y-3 overflow-y-auto h-[calc(100vh-300px)]">
          <NavLink href="/patient" icon="üè†" label="Dashboard" active={location.pathname === '/patient'} color="blue" />
          <NavLink href="/patient/rdv" icon="üìÖ" label="Rendez-vous" active={location.pathname === '/patient/rdv'} color="emerald" />
          <NavLink href="/patient/dossier" icon="üìÑ" label="Dossier" active={location.pathname === '/patient/dossier'} color="purple" />
          <NavLink href="/patient/ordonnances" icon="üíä" label="Ordonnances" active={location.pathname === '/patient/ordonnances'} color="orange" />
        </nav>
      </aside>

      {/* Contenu principal */}
      <main className="lg:ml-72 p-6 lg:p-8 pb-24 transition-all duration-300 min-h-screen">
        <Routes key={location.pathname} fallbackElement={<div>Chargement...</div>}>
          <Route path="/" element={<DashboardHome profile={profile} prochainsRDV={prochainsRDV} />} />
          <Route path="/rdv" element={<RDVPage rdv={prochainsRDV} />} />
          <Route path="/dossier" element={<DossierPage />} />
          <Route path="/ordonnances" element={<OrdonnancesPage ordonnances={ordonnances} />} />
          <Route path="*" element={<NotFoundPage />} />
        </Routes>
      </main>
    </div>
  );
};

// ‚úÖ Composants stables (pas de conditional rendering cass√©)
const NavLink = ({ href, icon, label, active, color }) => (
  <Link 
    to={href}
    className={`flex items-center p-6 rounded-3xl w-full font-bold text-xl transition-all duration-300 group hover:shadow-xl hover:-translate-x-2 border-2 ${
      active
        ? `bg-gradient-to-r from-${color}-500 to-${color}-600 text-white shadow-2xl shadow-${color}-200 scale-105 border-${color}-300 ring-2 ring-${color}-200/50`
        : `text-gray-700 hover:bg-${color}-50 hover:border-${color}-200 hover:shadow-lg`
    }`}
  >
    <span className={`mr-6 text-3xl transition-transform group-hover:scale-110 ${active ? 'text-white' : ''}`}>{icon}</span>
    <span className="flex-1 min-w-0 truncate">{label}</span>
    {active && <div className="w-2 h-2 bg-white rounded-full animate-pulse ml-2"></div>}
  </Link>
);

const DashboardHome = ({ profile, prochainsRDV }) => (
  <div className="max-w-7xl mx-auto space-y-16">
    {/* Header */}
    <section className="bg-white/90 backdrop-blur-xl rounded-4xl shadow-2xl border-4 border-blue-200/50 p-12 lg:p-16 text-center">
      <h1 className="text-6xl lg:text-7xl font-black bg-gradient-to-r from-gray-900 to-blue-900 bg-clip-text text-transparent mb-8">
        Bienvenue {profile?.nom} üëã
      </h1>
      <div className="bg-blue-50/80 p-8 rounded-3xl backdrop-blur-sm border-2 border-blue-200/50 inline-block">
        <p className="text-2xl font-semibold text-blue-800">{profile?.email}</p>
        <p className="text-xl text-blue-600 mt-2">{profile?.telephone}</p>
      </div>
    </section>

    {/* Stats */}
    <section className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-8">
      <InfoCard icon="üì±" title={profile?.telephone || 'N/A'} label="Contact" color="blue" />
      <InfoCard icon="ü©∫" title={profile?.groupeSanguin || 'NR'} label="Groupe sanguin" color="emerald" />
      <InfoCard icon="‚ö†Ô∏è" title={profile?.allergies || 'Aucune'} label="Allergies" color="purple" />
      <VerifiedCard />
    </section>

    {/* RDV */}
    <section className="lg:grid lg:grid-cols-2 lg:gap-12 max-w-7xl mx-auto">
      <RDVList rdv={prochainsRDV.slice(0, 3)} />
      <HealthSummary rdvCount={prochainsRDV.length} />
    </section>

    {/* Actions */}
    <section className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8 max-w-7xl mx-auto">
      <ActionButton to="/patient/rdv/nouveau" icon="üìÖ" title="Nouveau RDV" color="emerald" />
      <ActionButton to="/patient/dossier" icon="üìÑ" title="Dossier m√©dical" color="purple" />
      <ActionButton to="/patient/ordonnances" icon="üíä" title="Ordonnances" color="orange" />
    </section>
  </div>
);

const RDVPage = ({ rdv }) => (
  <div className="max-w-6xl mx-auto">
    <div className="bg-white/90 backdrop-blur-xl rounded-4xl shadow-3xl border-4 border-emerald-200/50 p-16 mb-12">
      <div className="flex items-center justify-between mb-16">
        <h2 className="text-6xl font-black text-gray-900 flex items-center gap-6">
          üìÖ Rendez-vous ({rdv?.length || 0})
        </h2>
        <Link to="/patient/rdv/nouveau" className="px-12 py-8 bg-emerald-500 text-white font-bold text-2xl rounded-4xl hover:bg-emerald-600 shadow-2xl hover:shadow-3xl transition-all">
          + Nouveau
        </Link>
      </div>
    </div>
  </div>
);

const DossierPage = () => (
  <div className="max-w-6xl mx-auto">
    <div className="bg-white/90 backdrop-blur-xl rounded-4xl shadow-3xl border-4 border-purple-200/50 p-16">
      <h2 className="text-6xl font-black text-gray-900 mb-16 text-center">üìÑ Dossier m√©dical</h2>
      <p className="text-3xl text-gray-600 text-center">Contenu du dossier (lecture seule)</p>
    </div>
  </div>
);

const OrdonnancesPage = ({ ordonnances }) => (
  <div className="max-w-6xl mx-auto">
    <div className="bg-white/90 backdrop-blur-xl rounded-4xl shadow-3xl border-4 border-orange-200/50 p-16">
      <h2 className="text-6xl font-black text-gray-900 mb-16 text-center flex items-center justify-center gap-8">
        üíä Ordonnances
        <span className="text-4xl bg-orange-100 text-orange-800 px-8 py-4 rounded-3xl font-bold">
          {ordonnances.filter(o => o.statut === 'Active').length}
        </span>
      </h2>
    </div>
  </div>
);

const NotFoundPage = () => (
  <div className="max-w-4xl mx-auto p-32 text-center bg-white/70 backdrop-blur-xl rounded-4xl shadow-2xl border-4 border-gray-200/50">
    <h1 className="text-7xl font-black text-gray-400 mb-12">404 - Page introuvable</h1>
    <Link 
      to="/patient" 
