import React, { useState, useEffect } from "react";
import { axiosInstance } from "../api/endpoint";

const CreateDossier = () => {
  const [patients, setPatients] = useState([]);
  const [formData, setFormData] = useState({
    patientId: "",
    resumeMedical: "",
    groupeSanguin: "",
    antecedents: "",
    allergies: "",
    contactUrgence: { nom: "", telephone: "", lien: "" }
  });

  // -------------------------------
  // Récupérer tous les patients
  // -------------------------------
  useEffect(() => {
    const fetchPatients = async () => {
      try {
        const response = await axiosInstance.get("/patients");
        setPatients(response.data); // supposé renvoyer [{ _id, fullName, email }]
      } catch (err) {
        console.error("Erreur récupération patients:", err);
        alert("Impossible de charger la liste des patients.");
      }
    };

    fetchPatients();
  }, []);

  // -------------------------------
  // Gestion des inputs
  // -------------------------------
  const handleChange = (e) => {
    const { name, value } = e.target;
    if (name.startsWith("contact")) {
      const key = name.split(".")[1];
      setFormData({
        ...formData,
        contactUrgence: { ...formData.contactUrgence, [key]: value }
      });
    } else {
      setFormData({ ...formData, [name]: value });
    }
  };

  // -------------------------------
  // Soumission du formulaire
  // -------------------------------
  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!formData.patientId) {
      return alert("Veuillez sélectionner un patient !");
    }

    try {
      const payload = {
        ...formData,
        antecedents: formData.antecedents
          .split(",")
          .map((a) => ({ libelle: a.trim() }))
          .filter(Boolean),
        allergies: formData.allergies
          .split(",")
          .map((a) => ({ libelle: a.trim() }))
          .filter(Boolean)
      };

      await axiosInstance.post("/dossiers", payload);

      alert("✅ Dossier créé avec succès !");
      setFormData({
        patientId: "",
        resumeMedical: "",
        groupeSanguin: "",
        antecedents: "",
        allergies: "",
        contactUrgence: { nom: "", telephone: "", lien: "" }
      });
    } catch (err) {
      console.error(err);
      alert(err.response?.data?.message || "❌ Erreur serveur");
    }
  };

  return (
    <div style={{ padding: "20px" }}>
      <h2>Créer un Dossier Patient</h2>
      <form onSubmit={handleSubmit}>
        {/* Selection Patient */}
        <label>Patient :</label><br />
        <select
          name="patientId"
          value={formData.patientId}
          onChange={handleChange}
        >
          <option value="">-- Sélectionner un patient --</option>
          {patients.map((p) => (
            <option key={p._id} value={p._id}>
              {p.fullName} ({p.email})
            </option>
          ))}
        </select>
        <br /><br />

        {/* Autres champs */}
        <textarea
          name="resumeMedical"
          placeholder="Résumé Médical"
          value={formData.resumeMedical}
          onChange={handleChange}
        />
        <br /><br />

        <input
          name="groupeSanguin"
          placeholder="Groupe Sanguin (ex: A+)"
          value={formData.groupeSanguin}
          onChange={handleChange}
        />
        <br /><br />

        <input
          name="antecedents"
          placeholder="Antécédents (séparés par ,)"
          value={formData.antecedents}
          onChange={handleChange}
        />
        <br /><br />

        <input
          name="allergies"
          placeholder="Allergies (séparés par ,)"
          value={formData.allergies}
          onChange={handleChange}
        />
        <br /><br />

        <h4>Contact d'urgence</h4>
        <input
          name="contact.nom"
          placeholder="Nom"
          value={formData.contactUrgence.nom}
          onChange={handleChange}
        />
        <br /><br />
        <input
          name="contact.telephone"
          placeholder="Téléphone"
          value={formData.contactUrgence.telephone}
          onChange={handleChange}
        />
        <br /><br />
        <input
          name="contact.lien"
          placeholder="Lien avec le patient"
          value={formData.contactUrgence.lien}
          onChange={handleChange}
        />
        <br /><br />

        <button type="submit">Créer Dossier</button>
      </form>
    </div>
  );
};

export default CreateDossier;
