import React, { useState } from 'react';

const MedecinDashboard = () => {
  const [activeTab, setActiveTab] = useState('ordonnances');
  const [patients, setPatients] = useState([
    { id: 1, name: 'Diop Aminata', age: 32, lastVisit: '08/02/2026', status: 'actif' },
    { id: 2, name: 'Ndiaye Fatou', age: 45, lastVisit: '07/02/2026', status: 'actif' },
    { id: 3, name: 'Sow Mamadou', age: 28, lastVisit: '06/02/2026', status: 'en attente' }
  ]);
  
  const [ordonnance, setOrdonnance] = useState({
    patient: '',
    medicaments: [
      { nom: 'Parac√©tamol 1g', dose: '1cp', frequence: '3x/jour', duree: '5 jours' },
      { nom: 'Amoxicilline 500mg', dose: '1cp', frequence: '2x/jour', duree: '7 jours' }
    ]
  });

  const addMedicament = () => {
    setOrdonnance({
      ...ordonnance,
      medicaments: [...ordonnance.medicaments, { nom: '', dose: '', frequence: '', duree: '' }]
    });
  };

  const updateMedicament = (index, field, value) => {
    const newMeds = [...ordonnance.medicaments];
    newMeds[index][field] = value;
    setOrdonnance({ ...ordonnance, medicaments: newMeds });
  };

  const deleteMedicament = (index) => {
    const newMeds = ordonnance.medicaments.filter((_, i) => i !== index);
    setOrdonnance({ ...ordonnance, medicaments: newMeds });
  };

  const saveOrdonnance = () => {
    if (!ordonnance.patient) {
      alert('‚ùå Veuillez s√©lectionner un patient !');
      return;
    }
    alert('‚úÖ ORDONNANCE G√âN√âR√âE ET ENVOY√âE !\n\n' + 
          `Patient: ${ordonnance.patient}\n\n` +
          ordonnance.medicaments.map((m, i) => `${i+1}. ${m.nom || '---'}\n   ${m.dose || ''} - ${m.frequence || ''} - ${m.duree || ''}`).join('\n'));
  };

  const generatePDF = () => {
    alert('üìÑ G√©n√©ration du PDF en cours...\n‚úÖ Fichier t√©l√©charg√©');
  };

  const sendSMS = () => {
    if (!ordonnance.patient) {
      alert('‚ùå Veuillez s√©lectionner un patient !');
      return;
    }
    alert('üì± SMS envoy√© avec succ√®s √† : ' + ordonnance.patient);
  };

  const updatePatient = (value) => {
    setOrdonnance({ ...ordonnance, patient: value });
  };

  const stats = {
    patients: patients.length,
    ordonnances: 47,
    rdvsToday: 8,
    revenus: 1250000
  };

  return (
    <div style={{ 
      minHeight: '100vh', 
      background: 'linear-gradient(135deg, #10b981 0%, #059669 50%, #047857 100%)',
      padding: '20px',
      fontFamily: 'system-ui, -apple-system, sans-serif',
      color: 'white'
    }}>
      <div style={{ maxWidth: '1400px', margin: '0 auto' }}>
        
        {/* HEADER */}
        <div style={{
          background: 'rgba(255,255,255,0.15)',
          backdropFilter: 'blur(20px)',
          borderRadius: '24px',
          boxShadow: '0 25px 50px rgba(0,0,0,0.2)',
          border: '4px solid rgba(255,255,255,0.3)',
          padding: '30px',
          marginBottom: '30px'
        }}>
          <div style={{ display: 'flex', flexDirection: 'column', gap: '20px' }}>
            <h1 style={{
              fontSize: '48px',
              fontWeight: '900',
              background: 'linear-gradient(135deg, white 0%, #f0fdf4 100%)',
              WebkitBackgroundClip: 'text',
              WebkitTextFillColor: 'transparent',
              margin: 0
            }}>
              ü©∫ DOCTORAT - GOVAIMED
            </h1>
            <div style={{
              display: 'flex', flexDirection: 'column', gap: '12px',
              fontSize: '20px', background: 'rgba(255,255,255,0.2)',
              padding: '24px', borderRadius: '16px', width: '100%'
            }}>
              <div style={{ display: 'flex', gap: '24px', flexWrap: 'wrap' }}>
                <span>üë®‚Äç‚öïÔ∏è Dr Ndiaye Mamadou</span>
                <span>üè• Clinique Dakar</span>
              </div>
              <div style={{ display: 'flex', gap: '24px', flexWrap: 'wrap' }}>
                <span>üìû +221 77 123 4567</span>
                <span>ü©∫ M√©decine G√©n√©rale</span>
                <span style={{ padding: '8px 16px', background: 'rgba(255,255,255,0.3)', borderRadius: '12px' }}>‚úÖ En ligne</span>
              </div>
            </div>
            <div style={{ textAlign: 'right', width: '100%' }}>
              <div style={{ fontSize: '36px', fontWeight: '900', color: '#f0fdf4' }}>
                {stats.revenus.toLocaleString()} FCFA
              </div>
              <div style={{ fontSize: '18px' }}>Consultations ce mois</div>
            </div>
          </div>
        </div>

        {/* NAVIGATION */}
        <div style={{
          background: 'rgba(255,255,255,0.15)',
          backdropFilter: 'blur(20px)',
          borderRadius: '24px',
          boxShadow: '0 25px 50px rgba(0,0,0,0.2)',
          border: '4px solid rgba(255,255,255,0.3)',
          padding: '20px',
          marginBottom: '30px',
          display: 'flex',
          gap: '15px',
          flexWrap: 'wrap'
        }}>
          <TabButton label="üíä Ordonnances" active={activeTab === 'ordonnances'} onClick={() => setActiveTab('ordonnances')} />
          <TabButton label={`üë• Patients (${stats.patients})`} active={activeTab === 'patients'} onClick={() => setActiveTab('patients')} />
          <TabButton label="üìÖ RDV" active={activeTab === 'rdv'} onClick={() => setActiveTab('rdv')} />
          <TabButton label="üìä Stats" active={activeTab === 'stats'} onClick={() => setActiveTab('stats')} />
        </div>

        {/* STATS */}
        <div style={{
          display: 'grid',
          gridTemplateColumns: 'repeat(auto-fit, minmax(300px, 1fr))',
          gap: '24px',
          marginBottom: '40px'
        }}>
          <StatBox title="üë• Mes Patients" value={stats.patients.toString()} color="#f0fdf4" textColor="#166534" />
          <StatBox title="üíä Ordonnances" value={stats.ordonnances.toString()} color="#dbeafe" textColor="#1e40af" />
          <StatBox title="üìÖ RDV Aujourd'hui" value={stats.rdvsToday.toString()} color="#fef3c7" textColor="#92400e" />
          <StatBox title="üí∞ Revenus" value={stats.revenus.toLocaleString()} color="#d1fae5" textColor="#065f46" />
        </div>

        {activeTab === 'ordonnances' && (
          <OrdonnancesSection 
            ordonnance={ordonnance}
            addMedicament={addMedicament}
            updateMedicament={updateMedicament}
            deleteMedicament={deleteMedicament}
            saveOrdonnance={saveOrdonnance}
            generatePDF={generatePDF}
            sendSMS={sendSMS}
            updatePatient={updatePatient}
          />
        )}
        {activeTab === 'patients' && <PatientsSection patients={patients} />}
        {activeTab === 'rdv' && <RdvSection />}
        {activeTab === 'stats' && <StatsSection />}
      </div>
    </div>
  );
};

const OrdonnancesSection = ({ ordonnance, addMedicament, updateMedicament, deleteMedicament, saveOrdonnance, generatePDF, sendSMS, updatePatient }) => (
  <div style={{
    background: 'rgba(255,255,255,0.15)',
    backdropFilter: 'blur(20px)',
    borderRadius: '24px',
    boxShadow: '0 25px 50px rgba(0,0,0,0.2)',
    border: '4px solid rgba(255,255,255,0.3)',
    padding: '30px'
  }}>
    <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '30px', flexDirection: 'column', gap: '20px' }}>
      <div style={{ textAlign: 'center' }}>
        <h2 style={{ fontSize: '40px', fontWeight: '900', color: 'white', margin: 0 }}>
          üíä Nouvelle Ordonnance
        </h2>
        <p style={{ fontSize: '24px', margin: '5px 0 0 0' }}>Prescription s√©curis√©e et num√©rique</p>
      </div>
    </div>

    {/* S√âLECTION PATIENT */}
    <div style={{ marginBottom: '30px' }}>
      <label style={{ display: 'block', fontSize: '20px', marginBottom: '12px', color: 'white' }}>
        üë§ Patient :
      </label>
      <select 
        value={ordonnance.patient}
        onChange={(e) => updatePatient(e.target.value)}
        style={{
          width: '100%', padding: '16px 20px', fontSize: '18px',
          border: '2px solid rgba(255,255,255,0.3)',
          borderRadius: '16px', background: 'rgba(255,255,255,0.9)',
          color: '#1f2937'
        }}
      >
        <option value="">S√©lectionner un patient...</option>
        <option value="Aminata Diop - 32 ans">Aminata Diop - 32 ans</option>
        <option value="Mamadou Ndiaye - 45 ans">Mamadou Ndiaye - 45 ans</option>
        <option value="Fatou Sow - 28 ans">Fatou Sow - 28 ans</option>
      </select>
    </div>

    {/* LISTE M√âDICAMENTS */}
    <div style={{ marginBottom: '30px' }}>
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '20px' }}>
        <h3 style={{ fontSize: '28px', color: 'white', margin: 0 }}>üíä M√©dicaments</h3>
        <button onClick={addMedicament} style={{
          padding: '12px 24px', background: 'rgba(255,255,255,0.9)',
          color: '#1f2937', fontWeight: '700', borderRadius: '12px',
          border: 'none', cursor: 'pointer'
        }}>
          ‚ûï Ajouter
        </button>
      </div>
      
      {ordonnance.medicaments.map((med, index) => (
        <MedicamentRow 
          key={index}
          medicament={med}
          index={index}
          onUpdate={updateMedicament}
          onDelete={deleteMedicament}
        />
      ))}
    </div>

    {/* BOUTONS ACTION */}
    <div style={{ display: 'flex', gap: '20px', justifyContent: 'center', flexWrap: 'wrap' }}>
      <button style={primaryBtnStyle} onClick={saveOrdonnance}>
        ‚úÖ FINALISER ORDONNANCE
      </button>
      <button style={secondaryBtnStyle} onClick={generatePDF}>
        üìÑ PDF
      </button>
      <button style={secondaryBtnStyle} onClick={sendSMS}>
        üì± SMS Patient
      </button>
    </div>
  </div>
);

const MedicamentRow = ({ medicament, index, onUpdate, onDelete }) => (
  <div style={{
    display: 'grid', gridTemplateColumns: '2fr 1fr 1fr 1fr 60px',
    gap: '16px', padding: '20px', background: 'rgba(255,255,255,0.1)',
    borderRadius: '16px', marginBottom: '12px', alignItems: 'center'
  }}>
    <input 
      placeholder="Nom du m√©dicament"
      value={medicament.nom}
      onChange={(e) => onUpdate(index, 'nom', e.target.value)}
      style={inputStyle}
    />
    <input placeholder="Dose" value={medicament.dose} onChange={(e) => onUpdate(index, 'dose', e.target.value)} style={inputStyle} />
    <input placeholder="Fr√©q." value={medicament.frequence} onChange={(e) => onUpdate(index, 'frequence', e.target.value)} style={inputStyle} />
    <input placeholder="Dur√©e" value={medicament.duree} onChange={(e) => onUpdate(index, 'duree', e.target.value)} style={inputStyle} />
    <button onClick={() => onDelete(index)} style={{
      padding: '12px', background: '#ef4444', color: 'white',
      border: 'none', borderRadius: '12px', cursor: 'pointer', fontSize: '18px'
    }}>üóëÔ∏è</button>
  </div>
);

const TabButton = ({ label, active, onClick }) => (
  <div style={{
    padding: '20px 30px', fontSize: '20px', fontWeight: '900',
    borderRadius: '20px', cursor: 'pointer',
    background: active ? 'rgba(255,255,255,0.3)' : 'rgba(255,255,255,0.1)',
    color: 'white', transition: 'all 0.3s ease', whiteSpace: 'nowrap'
  }} onClick={onClick}>
    {label}
  </div>
);

const StatBox = ({ title, value, color, textColor }) => (
  <div style={{
    padding: '30px', background: color, borderRadius: '24px',
    border: `4px solid rgba(255,255,255,0.3)`, boxShadow: '0 10px 25px rgba(0,0,0,0.2)'
  }}>
    <h3 style={{ fontSize: '18px', fontWeight: '700', color: textColor, margin: '0 0 15px 0', display: 'flex', alignItems: 'center', gap: '10px' }}>
      {title}
    </h3>
    <p style={{ fontSize: '48px', fontWeight: '900', color: textColor, margin: 0 }}>{value}</p>
  </div>
);

const primaryBtnStyle = {
  padding: '20px 40px', background: 'rgba(255,255,255,0.9)',
  color: '#1f2937', fontSize: '20px', fontWeight: '900',
  borderRadius: '24px', border: 'none', cursor: 'pointer',
  boxShadow: '0 10px 25px rgba(255,255,255,0.3)'
};

const secondaryBtnStyle = {
  padding: '20px 30px', background: 'rgba(255,255,255,0.2)',
  color: 'white', fontSize: '18px', fontWeight: '700',
  borderRadius: '20px', border: 'none', cursor: 'pointer'
};

const inputStyle = {
  padding: '16px 20px', fontSize: '16px',
  border: '2px solid rgba(255,255,255,0.3)',
  borderRadius: '12px', background: 'rgba(255,255,255,0.9)',
  color: '#1f2937', outline: 'none', width: '100%'
};

const PatientsSection = ({ patients }) => (
  <div style={{ background: 'rgba(255,255,255,0.15)', borderRadius: '24px', padding: '40px' }}>
    <h2 style={{ fontSize: '40px', color: 'white', marginBottom: '20px' }}>üë• Mes Patients</h2>
    <div style={{ display: 'grid', gap: '15px' }}>
      {patients.map((patient) => (
        <div key={patient.id} style={{ background: 'rgba(255,255,255,0.1)', padding: '20px', borderRadius: '16px' }}>
          <p style={{ margin: '0 0 10px 0', fontSize: '18px', fontWeight: '700' }}>{patient.name}</p>
          <p style={{ margin: '5px 0', fontSize: '16px' }}>√Çge: {patient.age} ans</p>
          <p style={{ margin: '5px 0', fontSize: '16px' }}>Derni√®re visite: {patient.lastVisit}</p>
          <p style={{ margin: '5px 0', fontSize: '16px' }}>Statut: {patient.status}</p>
        </div>
      ))}
    </div>
  </div>
);

const RdvSection = () => (
  <div style={{ background: 'rgba(255,255,255,0.15)', borderRadius: '24px', padding: '40px' }}>
    <h2 style={{ fontSize: '40px', color: 'white' }}>üìÖ RDV</h2>
  </div>
);

const StatsSection = () => (
  <div style={{ background: 'rgba(255,255,255,0.15)', borderRadius: '24px', padding: '40px' }}>
    <h2 style={{ fontSize: '40px', color: 'white' }}>üìä Stats</h2>
  </div>
);

export default MedecinDashboard;
