import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { postData } from '../api/endpoint'; // ‚úÖ Import corrig√©
import { FiFileText, FiUser, FiAlertCircle, FiHeart, FiCheckCircle } from 'react-icons/fi';

const CreerDossier = () => {
  const navigate = useNavigate();
  const [patients, setPatients] = useState([]);
  const [loadingPatients, setLoadingPatients] = useState(true);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(false);
  
  const [formData, setFormData] = useState({
    patientId: '',
    resumeMedical: '',
    groupeSanguin: '',
    antecedents: [],
    allergies: [],
    contactUrgence: {
      nom: '',
      telephone: '',
      lien: ''
    },
    statut: 'ACTIF'
  });
  
  const [newAntecedent, setNewAntecedent] = useState('');
  const [newAllergie, setNewAllergie] = useState('');

  // ‚úÖ V√âRIFICATION AVANT envoi (√©vite l'erreur MongoDB)
  const checkDossierExists = async (patientId) => {
    try {
      // Cette v√©rification est faite c√¥t√© frontend via le filtrage des patients
      // Le backend a d√©j√† l'index unique qui prot√®ge
      return false;
    } catch {
      return true; // Consid√©rer comme existant en cas d'erreur
    }
  };

  useEffect(() => {
    loadPatients();
  }, []);

  const loadPatients = async () => {
    try {
      setLoadingPatients(true);
      // ‚úÖ Utiliser getData correctement (import√© par d√©faut)
      const patientsData = await getData('/patients');
      setPatients(patientsData);
    } catch (err) {
      console.error('Erreur chargement patients:', err);
      setError('Erreur lors du chargement des patients');
    } finally {
      setLoadingPatients(false);
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setError(null);
    
    if (name.startsWith('contactUrgence.')) {
      const field = name.split('.')[1];
      setFormData(prev => ({
        ...prev,
        contactUrgence: {
          ...prev.contactUrgence,
          [field]: value
        }
      }));
    } else {
      setFormData(prev => ({
        ...prev,
        [name]: value
      }));
    }
  };

  const addAntecedent = () => {
    if (newAntecedent.trim()) {
      setFormData(prev => ({
        ...prev,
        antecedents: [...prev.antecedents, { antecedent: newAntecedent.trim() }]
      }));
      setNewAntecedent('');
    }
  };

  const removeAntecedent = (index) => {
    setFormData(prev => ({
      ...prev,
      antecedents: prev.antecedents.filter((_, i) => i !== index)
    }));
  };

  const addAllergie = () => {
    if (newAllergie.trim()) {
      setFormData(prev => ({
        ...prev,
        allergies: [...prev.allergies, { allergie: newAllergie.trim() }]
      }));
      setNewAllergie('');
    }
  };

  const removeAllergie = (index) => {
    setFormData(prev => ({
      ...prev,
      allergies: prev.allergies.filter((_, i) => i !== index)
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError(null);
    setSuccess(false);

    // ‚úÖ Validation frontend
    if (!formData.patientId) {
      setError('Veuillez s√©lectionner un patient.');
      return;
    }

    try {
      setLoading(true);
      
      // ‚úÖ Pr√©paration des donn√©es nettoy√©es
      const dataToSend = {
        patientId: formData.patientId,
        resumeMedical: formData.resumeMedical || '',
        groupeSanguin: formData.groupeSanguin || '',
        antecedents: formData.antecedents || [],
        allergies: formData.allergies || [],
        contactUrgence: formData.contactUrgence.nom || formData.contactUrgence.telephone || formData.contactUrgence.lien 
          ? formData.contactUrgence 
          : undefined,
        statut: 'ACTIF',
        medecinId: decodeToken(localStorage.getItem('token'))?.userId // ‚úÖ Ajout m√©decin ID
      };

      // ‚úÖ Envoi avec gestion d'erreur sp√©cifique MongoDB
      const response = await postData('/api/new-dossier', dataToSend);
      
      setSuccess(true);
      setTimeout(() => {
        navigate('/medecin/dossiers');
      }, 1500);
      
    } catch (err) {
      console.error('Erreur cr√©ation dossier:', err);
      
      // ‚úÖ Messages d'erreur sp√©cifiques
      if (err.message.includes('dossier existe') || err.message.includes('duplicate')) {
        setError('‚ö†Ô∏è Un dossier existe d√©j√† pour ce patient. V√©rifiez vos dossiers existants.');
      } else if (err.message.includes('Session expir√©e')) {
        setError('Session expir√©e. Redirection vers la connexion...');
      } else {
        setError(err.message || 'Erreur lors de la cr√©ation du dossier m√©dical.');
      }
    } finally {
      setLoading(false);
    }
  };

  // Fonction utilitaire pour d√©coder le token (si pas import√©e)
  const decodeToken = (token) => {
    if (!token) return null;
    try {
      return JSON.parse(atob(token.split('.')[1]));
    } catch {
      return null;
    }
  };

  if (loadingPatients) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-500 mx-auto mb-4"></div>
          <p>Chargement des patients...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 to-indigo-100 py-8 px-4">
      <div className="max-w-4xl mx-auto">
        <div className="mb-8 text-center">
          <h2 className="text-4xl font-bold text-gray-900 mb-2 flex items-center justify-center gap-3">
            <FiFileText size={36} className="text-blue-600" />
            Cr√©er un Dossier M√©dical
          </h2>
          <p className="text-gray-600 text-lg">Nouveau dossier pour un patient sans dossier actif</p>
        </div>

        {error && (
          <div className="bg-red-50 border-l-4 border-red-500 p-4 rounded-lg mb-6 shadow-md animate-pulse">
            <div className="flex items-center">
              <FiAlertCircle className="mr-3 text-red-500" size={20} />
              <div>
                <p className="font-semibold text-red-800">{error}</p>
              </div>
            </div>
          </div>
        )}

        {success && (
          <div className="bg-green-50 border-l-4 border-green-500 p-4 rounded-lg mb-6 shadow-md animate-pulse">
            <div className="flex items-center">
              <FiCheckCircle className="mr-3 text-green-500" size={20} />
              <p className="font-semibold text-green-800">
                ‚úÖ Dossier cr√©√© avec succ√®s ! Redirection...
              </p>
            </div>
          </div>
        )}

        <form onSubmit={handleSubmit} className="bg-white rounded-2xl shadow-2xl p-8 border border-gray-100">
          {/* Patient Selection */}
          <div className="mb-8">
            <label className="block text-sm font-semibold text-gray-700 mb-3">
              <FiUser className="inline mr-2" size={20} />
              Patient * (sans dossier actif)
            </label>
            <select
              name="patientId"
              value={formData.patientId}
              onChange={handleChange}
              required
              className="w-full px-4 py-4 border-2 border-gray-300 rounded-xl focus:ring-4 focus:ring-blue-100 focus:border-blue-500 transition-all bg-gradient-to-r from-gray-50 to-white text-lg"
              disabled={loading}
            >
              <option value="">S√©lectionnez un patient</option>
              {patients.map(patient => (
                <option key={patient._id} value={patient._id}>
                  {patient.fullName || `${patient.nom} ${patient.prenom}`} - {patient.email}
                </option>
              ))}
            </select>
            {patients.length === 0 && (
              <div className="mt-3 p-4 bg-amber-50 border border-amber-200 rounded-lg">
                <p className="text-amber-800 font-medium">
                  üìã Tous les patients ont d√©j√† un dossier actif
                </p>
                <p className="text-sm text-amber-700 mt-1">
                  V√©rifiez vos dossiers existants ou contactez l'administrateur
                </p>
              </div>
            )}
          </div>

          {/* Autres champs - m√™me structure mais optimis√©e */}
          <div className="grid grid-cols-1 lg:grid-cols-2 gap-6 mb-8">
            {/* Groupe Sanguin */}
            <div>
              <label className="block text-sm font-semibold text-gray-700 mb-2">Groupe Sanguin</label>
              <select name="groupeSanguin" value={formData.groupeSanguin} onChange={handleChange} className="w-full px-4 py-3 border-2 border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 transition-all">
                <option value="">Non sp√©cifi√©</option>
                <option value="A+">A+</option><option value="A-">A-</option>
                <option value="B+">B+</option><option value="B-">B-</option>
                <option value="AB+">AB+</option><option value="AB-">AB-</option>
                <option value="O+">O+</option><option value="O-">O-</option>
              </select>
            </div>

            {/* R√©sum√© m√©dical */}
            <div>
              <label className="block text-sm font-semibold text-gray-700 mb-2">
                <FiFileText className="inline mr-2" size={16} />
                R√©sum√© m√©dical
              </label>
              <textarea
                name="resumeMedical"
                value={formData.resumeMedical}
                onChange={handleChange}
                rows={3}
                className="w-full px-4 py-3 border-2 border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 resize-vertical"
                placeholder="Ant√©c√©dents g√©n√©raux, √©tat actuel..."
              />
            </div>
          </div>

          {/* Ant√©c√©dents & Allergies */}
          <div className="grid grid-cols-1 lg:grid-cols-2 gap-6 mb-8">
            <div>
              <label className="block text-sm font-semibold text-gray-700 mb-3">
                <FiAlertCircle className="inline mr-2" size={18} />
                Ant√©c√©dents
              </label>
              <div className="space-y-2">
                <div className="flex gap-2">
                  <input
                    type="text"
                    value={newAntecedent}
                    onChange={(e) => setNewAntecedent(e.target.value)}
                    onKeyPress={(e) => e.key === 'Enter' && (e.preventDefault(), addAntecedent())}
                    placeholder="ex: Diab√®te type 2"
                    className="flex-1 px-3 py-2 border rounded-lg focus:ring-2 focus:ring-blue-500"
                  />
                  <button type="button" onClick={addAntecedent} className="px-4 py-2 bg-blue-500 text-white rounded-lg hover:bg-blue-600">
                    +
                  </button>
                </div>
                <div className="max-h-32 overflow-auto space-y-1">
                  {formData.antecedents.map((a, i) => (
                    <div key={i} className="flex justify-between items-center bg-blue-50 p-2 rounded border">
                      <span>{a.antecedent}</span>
                      <button type="button" onClick={() => removeAntecedent(i)} className="text-red-500 hover:text-red-700">√ó</button>
                    </div>
                  ))}
                </div>
              </div>
            </div>

            <div>
              <label className="block text-sm font-semibold text-gray-700 mb-3">
                <FiHeart className="inline mr-2" size={18} />
                Allergies
              </label>
              <div className="space-y-2">
                <div className="flex gap-2">
                  <input
                    type="text"
                    value={newAllergie}
                    onChange={(e) => setNewAllergie(e.target.value)}
                    onKeyPress={(e) => e.key === 'Enter' && (e.preventDefault(), addAllergie())}
                    placeholder="ex: P√©nicilline"
                    className="flex-1 px-3 py-2 border rounded-lg focus:ring-2 focus:ring-blue-500"
                  />
                  <button type="button" onClick={addAllergie} className="px-4 py-2 bg-red-500 text-white rounded-lg hover:bg-red-600">
                    +
                  </button>
                </div>
                <div className="max-h-32 overflow-auto space-y-1">
                  {formData.allergies.map((a, i) => (
                    <div key={i} className="flex justify-between items-center bg-red-50 p-2 rounded border">
                      <span>{a.allergie}</span>
                      <button type="button" onClick={() => removeAllergie(i)} className="text-red-500 hover:text-red-700">√ó</button>
                    </div>
                  ))}
                </div>
              </div>
            </div>
          </div>

          {/* Contact Urgence */}
          <div className="mb-8 p-6 bg-gradient-to-r from-gray-50 to-blue-50 rounded-xl border-2 border-dashed border-gray-200">
            <h3 className="text-xl font-bold text-gray-900 mb-4">üìû Contact d'Urgence</h3>
            <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
              <input
                type="text" name="contactUrgence.nom"
                value={formData.contactUrgence.nom} onChange={handleChange}
                placeholder="Nom" className="p-3 border rounded-lg focus:ring-2 focus:ring-blue-500"
              />
              <input
                type="tel" name="contactUrgence.telephone"
                value={formData.contactUrgence.telephone} onChange={handleChange}
                placeholder="01 23 45 67 89" className="p-3 border rounded-lg focus:ring-2 focus:ring-blue-500"
              />
              <input
                type="text" name="contactUrgence.lien"
                value={formData.contactUrgence.lien} onChange={handleChange}
                placeholder="√âpoux/Parent/..." className="p-3 border rounded-lg focus:ring-2 focus:ring-blue-500"
              />
            </div>
          </div>

          {/* Boutons */}
          <div className="flex flex-col sm:flex-row gap-4 pt-6 border-t">
            <button
              type="submit"
              disabled={loading || !formData.patientId}
              className="flex-1 bg-gradient-to-r from-blue-600 to-indigo-600 text-white py-4 rounded-xl hover:from-blue-700 hover:to-indigo-700 transition-all font-semibold shadow-xl hover:shadow-2xl disabled:opacity-50 disabled:cursor-not-allowed text-lg"
            >
              {loading ? (
                <>
                  <svg className="animate-spin -ml-1 mr-3 h-5 w-5 inline" fill="none" viewBox="0 0 24 24">
                    <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
                    <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
                  </svg>
                  Cr√©ation en cours...
                </>
              ) : (
                '‚úÖ Cr√©er le Dossier M√©dical'
              )}
            </button>
            <button
              type="button"
              onClick={() => navigate('/medecin/dossiers')}
              className="px-8 py-4 border-2 border-gray-300 text-gray-700 rounded-xl hover:bg-gray-50 hover:border-gray-400 transition-all font-semibold"
              disabled={loading}
            >
              Annuler
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default CreerDossier;
