import React, { useState, useEffect } from 'react';
import { Link, useNavigate, useLocation } from 'react-router-dom';
import { 
  getAuthToken, 
  decodeToken, 
  removeAuthToken,
  getData
} from '../api/endpoint';
import {
  FiPackage, 
  FiFileText, 
  FiBarChart, 
  FiBell, 
  FiSettings, 
  FiLogOut, 
  FiMenu, 
  FiX,
  FiCheckCircle,
  FiClock
} from 'react-icons/fi';

// Composant Dashboard principal (page d'accueil pharmacien)
const DashboardHome = ({ user }) => {
  const [stats, setStats] = useState({
    ordonnances: 0,
    ordonnancesEnAttente: 0,
    ordonnancesTraitees: 0
  });
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    loadStats();
  }, []);

  const loadStats = async () => {
    try {
      setLoading(true);
      const ordonnancesData = await getData('/ordonnances').catch(() => []);
      
      const enAttente = ordonnancesData.filter(ord => ord.statut === 'EN_ATTENTE' || !ord.statut);
      const traitees = ordonnancesData.filter(ord => ord.statut === 'TRAITEE');

      setStats({
        ordonnances: ordonnancesData.length,
        ordonnancesEnAttente: enAttente.length,
        ordonnancesTraitees: traitees.length
      });
    } catch (err) {
      console.error('Erreur lors du chargement des statistiques:', err);
    } finally {
      setLoading(false);
    }
  };

  return (
    <>
      <div className="mb-8">
        <h2 className="text-3xl font-bold text-gray-900">Tableau de Bord Pharmacien</h2>
        <p className="text-gray-600 mt-2">Bienvenue, {user?.fullName || user?.email || 'Pharmacien'}</p>
      </div>

      {/* Stats Cards */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-10">
        <div className="bg-white p-8 rounded-2xl shadow-lg hover:shadow-xl transition-all border border-gray-100 group transform hover:scale-105">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm font-medium text-gray-600">Total Ordonnances</p>
              <p className="text-3xl font-bold text-gray-900 mt-2">
                {loading ? '...' : stats.ordonnances}
              </p>
              <p className="text-sm text-blue-600 mt-1 font-medium">Toutes</p>
            </div>
            <FiFileText size={32} className="text-blue-500 group-hover:scale-110 transition-transform" />
          </div>
        </div>

