import React, { useEffect, useState } from 'react';
import { getData, decodeToken } from '../api/endpoint';

const PatientDashboard = () => {
  const [consultations, setConsultations] = useState([]);
  const [rendezVous, setRendezVous] = useState([]);
  const [loading, setLoading] = useState(true);
  const token = localStorage.getItem('token');

  useEffect(() => {
    const fetchData = async () => {
      try {
        const consults = await getData('/patient/mes-consultations');
        setConsultations(consults);

        // On suppose que decodeToken(token)._id existe
        const userId = decodeToken(token)._id;
        const rdvs = await getData(`/rendezvous?patientId=${userId}`);
        setRendezVous(rdvs);

      } catch (error) {
        console.error("Erreur chargement patient :", error.message);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, [token]);

  if (loading) return <p>Chargement...</p>;

  return (
    <div>
      <h2>Mes Consultations</h2>
      <ul>
        {consultations.map(c => (
          <li key={c._id}>{c.date} - {c.doctorName}</li>
        ))}
      </ul>

      <h2>Mes Rendez-Vous</h2>
      <ul>
        {rendezVous.map(r => (
          <li key={r._id}>{r.date} - {r.service}</li>
        ))}
      </ul>
    </div>
  );
};

export default PatientDashboard;
