import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";

const PatientDashboard = () => {
  const [user, setUser] = useState(null);
  const [dossiers, setDossiers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const navigate = useNavigate();

  // Fonction pour récupérer le token
  const getToken = () => localStorage.getItem("token");

  // Vérifie si l'utilisateur est bien patient
  const fetchUser = async () => {
    try {
      const token = getToken();
      if (!token) throw new Error("Non connecté");

      const res = await axios.get("http://localhost:5000/api/users/me", {
        headers: { Authorization: `Bearer ${token}` },
      });

      if (res.data.role !== "Patient") {
        navigate("/login");
        return;
      }

      setUser(res.data);
    } catch (err) {
      console.error("Erreur récupération infos patient :", err);
      setError("Impossible de récupérer vos informations. Veuillez vous reconnecter.");
      navigate("/login");
    }
  };

  // Récupère les dossiers du patient
  const fetchDossiers = async () => {
    try {
      const token = getToken();
      if (!token) throw new Error("Non connecté");

      const res = await axios.get("http://localhost:5000/api/patient/dossiers", {
        headers: { Authorization: `Bearer ${token}` },
      });

      setDossiers(res.data);
    } catch (err) {
      console.error("Erreur récupération dossiers :", err);
      setError("Impossible de récupérer vos dossiers pour le moment.");
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    const init = async () => {
      await fetchUser();
      await fetchDossiers();
    };
    init();
  }, []);

  if (loading) return <div style={{ padding: "40px", textAlign: "center" }}>Chargement...</div>;

  return (
    <div style={{ padding: "20px" }}>
      <h1 style={{ marginBottom: "20px" }}>Bienvenue, {user?.fullName}</h1>

      {error && (
        <div style={{
          backgroundColor: "#fee",
          color: "#c33",
          padding: "12px",
          borderRadius: "8px",
          marginBottom: "20px",
          border: "1px solid #fcc",
        }}>
          {error}
        </div>
      )}

      <h2 style={{ marginBottom: "10px" }}>Mes dossiers médicaux :</h2>

      {dossiers.length === 0 ? (
        <p>Aucun dossier trouvé.</p>
      ) : (
        <ul style={{ listStyle: "none", padding: 0 }}>
          {dossiers.map((dossier) => (
            <li key={dossier._id} style={{
              marginBottom: "15px",
              padding: "15px",
              border: "1px solid #ddd",
              borderRadius: "8px",
              backgroundColor: "#fafafa"
            }}>
              <strong>Résumé :</strong> {dossier.resumeMedical}<br />
              <strong>Médecin :</strong> {dossier.medecin?.fullName || "N/A"}<br />
              <strong>Date :</strong> {new Date(dossier.createdAt).toLocaleDateString()}
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default PatientDashboard;
