import React, { useState, useEffect } from 'react';
import { Link, useNavigate, Routes, Route, useLocation } from 'react-router-dom';
import { 
  getAuthToken, 
  decodeToken, 
  removeAuthToken,
  getData
} from '../api/endpoint';
import {
  FiUsers, 
  FiFileText, 
  FiCalendar, 
  FiActivity,
  FiBarChart, 
  FiBell, 
  FiSettings, 
  FiLogOut, 
  FiMenu, 
  FiX
} from 'react-icons/fi';

// Import des pages medecin (non utilis√© ici, mais disponible pour r√©f√©rence)

// Composant Dashboard principal (page d'accueil medecin)
const DashboardHome = ({ user }) => {
  const [stats, setStats] = useState({
    patients: 0,
    consultations: 0,
    rendezVous: 0,
    dossiers: 0
  });
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    loadStats();
  }, []);

  const loadStats = async () => {
    try {
      setLoading(true);
      const [dossiersData, consultationsData, rdvData] = await Promise.all([
        getData('/mes-dossiers').catch(() => []),
        getData('/consultations').catch(() => []),
        getData('/rendezvous').catch(() => [])
      ]);

      const token = localStorage.getItem('token');
      const decoded = token ? JSON.parse(atob(token.split('.')[1])) : null;
      const medecinId = decoded?.userId;

      const mesConsultations = consultationsData.filter(cons => 
        cons.medecinId?._id === medecinId || cons.medecinId === medecinId
      );
      const mesRdv = rdvData.filter(rdv => 
        rdv.medecinId?._id === medecinId || rdv.medecinId === medecinId
      );

      // Extraire les patients uniques depuis les dossiers
      const patientIds = [...new Set(dossiersData.map(d => 
        d.patientId?._id || d.patientId
      ))];

      setStats({
        patients: patientIds.length,
        consultations: mesConsultations.length,
        rendezVous: mesRdv.length,
        dossiers: dossiersData.length
      });
    } catch (err) {
      console.error('Erreur lors du chargement des statistiques:', err);
    } finally {
      setLoading(false);
    }
  };

  return (
    <>
      <div className="mb-8">
        <h2 className="text-3xl font-bold text-gray-900">Tableau de Bord M√©decin</h2>
        <p className="text-gray-600 mt-2">Bienvenue, Dr. {user?.fullName || user?.email || 'M√©decin'}</p>
      </div>

      {/* Stats Cards */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-10">
        <Link to="/medecin/patients" className="bg-white p-8 rounded-2xl shadow-lg hover:shadow-xl transition-all border border-gray-100 group transform hover:scale-105">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm font-medium text-gray-600">Mes Patients</p>
              <p className="text-3xl font-bold text-gray-900 mt-2">
                {loading ? '...' : stats.patients}
              </p>
              <p className="text-sm text-blue-600 mt-1 font-medium">Voir tous</p>
            </div>
            <FiUsers size={32} className="text-blue-500 group-hover:scale-110 transition-transform" />
          </div>
        </Link>

        <Link to="/medecin/consultations" className="bg-white p-8 rounded-2xl shadow-lg hover:shadow-xl transition-all border border-gray-100 group transform hover:scale-105">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm font-medium text-gray-600">Consultations</p>
              <p className="text-3xl font-bold text-gray-900 mt-2">
                {loading ? '...' : stats.consultations}
              </p>
              <p className="text-sm text-green-600 mt-1 font-medium">Total</p>
            </div>
            <FiActivity size={32} className="text-green-500 group-hover:scale-110 transition-transform" />
          </div>
        </Link>

        <Link to="/medecin/rendez-vous" className="bg-white p-8 rounded-2xl shadow-lg hover:shadow-xl transition-all border border-gray-100 group transform hover:scale-105">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm font-medium text-gray-600">Rendez-vous</p>
              <p className="text-3xl font-bold text-gray-900 mt-2">
                {loading ? '...' : stats.rendezVous}
              </p>
              <p className="text-sm text-purple-600 mt-1 font-medium">Programm√©s</p>
            </div>
            <FiCalendar size={32} className="text-purple-500 group-hover:scale-110 transition-transform" />
          </div>
        </Link>

        <Link to="/medecin/dossiers" className="bg-white p-8 rounded-2xl shadow-lg hover:shadow-xl transition-all border border-gray-100 group transform hover:scale-105">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm font-medium text-gray-600">Dossiers</p>
              <p className="text-3xl font-bold text-gray-900 mt-2">
                {loading ? '...' : stats.dossiers}
              </p>
              <p className="text-sm text-red-600 mt-1 font-medium">G√©rer</p>
            </div>
            <FiFileText size={32} className="text-red-500 group-hover:scale-110 transition-transform" />
          </div>
        </Link>
      </div>

      {/* Quick Actions */}
      <div className="bg-white rounded-2xl shadow-lg p-8 border border-gray-100 mb-10">
        <h3 className="text-2xl font-bold text-gray-900 mb-6">Actions rapides</h3>
        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
          <Link to="/medecin/dossiers/creer" className="block w-full bg-gradient-to-r from-blue-500 to-blue-600 text-white p-4 rounded-xl hover:shadow-lg hover:scale-105 transition-all text-center font-medium">
            üìã Cr√©er un dossier patient
          </Link>
          <Link to="/medecin/consultations" className="block w-full border-2 border-blue-100 bg-blue-50 text-blue-700 p-4 rounded-xl hover:bg-blue-100 hover:border-blue-200 hover:shadow-md transition-all text-center font-medium">
            ü©∫ Nouvelle consultation
          </Link>
        </div>
      </div>

      {/* Informations importantes */}
      <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
        <div className="bg-gradient-to-r from-blue-50 to-indigo-50 rounded-2xl shadow-lg p-6 border border-blue-100">
          <h3 className="text-xl font-bold text-gray-900 mb-3 flex items-center">
            <FiUsers className="mr-2 text-blue-500" />
            Gestion des Patients
          </h3>
          <p className="text-gray-700 mb-4">
            Acc√©dez √† la liste de vos patients, consultez leurs dossiers m√©dicaux et suivez leur historique.
          </p>
          <Link to="/medecin/patients" className="text-blue-600 font-medium hover:underline">
            Voir mes patients ‚Üí
          </Link>
        </div>
        <div className="bg-gradient-to-r from-green-50 to-emerald-50 rounded-2xl shadow-lg p-6 border border-green-100">
          <h3 className="text-xl font-bold text-gray-900 mb-3 flex items-center">
            <FiFileText className="mr-2 text-green-500" />
            Dossiers M√©dicaux
          </h3>
          <p className="text-gray-700 mb-4">
            Cr√©ez et g√©rez les dossiers m√©dicaux de vos patients avec toutes les informations n√©cessaires.
          </p>
          <Link to="/medecin/dossiers" className="text-green-600 font-medium hover:underline">
            G√©rer les dossiers ‚Üí
          </Link>
        </div>
      </div>
    </>
  );
};

// Composant Layout Medecin qui accepte des children
export const MedecinLayoutWrapper = ({ children }) => {
  const navigate = useNavigate();
  const location = useLocation();
  const [sidebarOpen, setSidebarOpen] = useState(false);
  const [user, setUser] = useState(null);

  useEffect(() => {
    const token = getAuthToken();
    if (!token) {
      navigate('/login');
      return;
    }

    const decoded = decodeToken(token);
    if (decoded?.role !== 'Medecin') {
      navigate('/');
      return;
    }
    
    setUser(decoded);
  }, [navigate]);

  const logout = () => {
    removeAuthToken();
    navigate('/login');
  };

  const navigation = [
    { name: 'Dashboard', icon: FiBarChart, href: '/medecin', exact: true },
    { name: 'Mes Patients', icon: FiUsers, href: '/medecin/patients' },
    { name: 'Consultations', icon: FiActivity, href: '/medecin/consultations' },
    { name: 'Rendez-vous', icon: FiCalendar, href: '/medecin/rendez-vous' },
    { name: 'Dossiers', icon: FiFileText, href: '/medecin/dossiers' },
    { name: 'Param√®tres', icon: FiSettings, href: '/medecin/settings' }
  ];

  return (
    <div className="min-h-screen bg-gray-100">
      {/* Mobile Sidebar Overlay */}
      {sidebarOpen && (
        <div 
          className="fixed inset-0 z-40 lg:hidden" 
          onClick={() => setSidebarOpen(false)}
        >
          <div className="absolute inset-0 bg-black bg-opacity-25" />
        </div>
      )}
      
      {/* Sidebar */}
      <div className={`fixed inset-y-0 left-0 z-50 w-64 bg-white shadow-lg transform ${
        sidebarOpen ? 'translate-x-0' : '-translate-x-full'
      } lg:translate-x-0 lg:static lg:inset-0 transition-transform duration-200 ease-in-out`}>
        <div className="flex items-center justify-between h-16 px-6 border-b border-gray-200">
          <h1 className="text-xl font-bold text-gray-900">Espace M√©decin</h1>
          <button 
            onClick={() => setSidebarOpen(false)} 
            className="lg:hidden p-2 rounded-md text-gray-400 hover:text-gray-500"
          >
            <FiX size={20} />
          </button>
        </div>
        
        <nav className="mt-8 px-4 space-y-2">
          {navigation.map((item) => {
            const isActive = item.exact 
              ? location.pathname === item.href 
              : location.pathname.startsWith(item.href);
            return (
              <Link
                key={item.name}
                to={item.href}
                className={`flex items-center px-4 py-3 rounded-lg transition-all group ${
                  isActive 
                    ? 'bg-blue-100 text-blue-700 font-semibold' 
                    : 'text-gray-700 hover:bg-gray-100 hover:text-blue-600'
                }`}
              >
                <item.icon size={20} className="mr-3 flex-shrink-0" />
                <span>{item.name}</span>
              </Link>
            );
          })}
        </nav>

        <div className="absolute bottom-6 left-6 w-56">
          <button
            onClick={logout}
            className="flex items-center w-full px-4 py-3 text-red-600 hover:bg-red-50 rounded-lg transition-all group"
          >
            <FiLogOut size={20} className="mr-3 group-hover:-translate-x-1 transition-transform" />
            D√©connexion
          </button>
        </div>
      </div>

      {/* Main Content */}
      <div className="lg:ml-64">
        {/* Topbar */}
        <header className="bg-white shadow-sm border-b border-gray-200">
          <div className="flex items-center justify-between h-16 px-6">
            <button
              className="lg:hidden p-2 rounded-md text-gray-400 hover:text-gray-500"
              onClick={() => setSidebarOpen(true)}
            >
              <FiMenu size={20} />
            </button>
            
            <div className="flex items-center space-x-4">
              <button className="p-2 text-gray-400 hover:text-gray-500 rounded-lg relative">
                <FiBell size={20} />
                <span className="absolute -top-1 -right-1 w-3 h-3 bg-red-500 rounded-full"></span>
              </button>
              
              <div className="flex items-center space-x-3">
                <div className="w-8 h-8 bg-gradient-to-r from-green-500 to-green-600 rounded-full flex items-center justify-center">
                  <span className="text-white font-semibold text-sm">
                    {user?.fullName?.[0]?.toUpperCase() || user?.email?.[0]?.toUpperCase() || 'M'}
                  </span>
                </div>
                <span className="font-medium text-gray-700 hidden md:block">
                  Dr. {user?.fullName || user?.email || 'M√©decin'}
                </span>
              </div>
            </div>
          </div>
        </header>

        {/* Content avec children */}
        <main style={{ padding: '20px', minHeight: 'calc(100vh - 64px)' }}>
          {children}
        </main>
      </div>
    </div>
  );
};

// Export du layout pour utilisation dans App.js
export { DashboardHome };
export default MedecinLayoutWrapper;
