// src/pages/Composants/MyDossiers.jsx
import React, { useEffect, useState } from "react";
import { getData, getAuthToken, decodeToken, handleApiError } from "../../api/endpoint";

const MyDossiers = () => {
  const [dossiers, setDossiers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  useEffect(() => {
    const fetchDossiers = async () => {
      try {
        // Récupérer le token et décoder le patient
        const token = getAuthToken();
        const patient = decodeToken(token);

        if (!patient) throw new Error("Utilisateur non authentifié");

        // Appel API pour récupérer les dossiers du patient
        const data = await getData("/patient/dossiers", { patientId: patient.id });
        setDossiers(data);
      } catch (err) {
        console.error(err);
        setError(handleApiError(err).message);
      } finally {
        setLoading(false);
      }
    };

    fetchDossiers();
  }, []);

  if (loading) return <div className="p-6">Chargement des dossiers...</div>;
  if (error) return <div className="p-6 text-red-600">Erreur : {error}</div>;

  return (
    <div className="p-6">
      <h2 className="text-2xl font-bold mb-4">Mes dossiers médicaux</h2>

      {dossiers.length === 0 ? (
        <p>Aucun dossier trouvé.</p>
      ) : (
        <div className="space-y-4">
          {dossiers.map((dossier) => (
            <div key={dossier._id} className="p-4 border rounded shadow">
              <p><strong>Résumé médical :</strong> {dossier.resumeMedical}</p>
              <p><strong>Groupe sanguin :</strong> {dossier.groupeSanguin}</p>
              <p><strong>Antécédents :</strong> {dossier.antecedents.map(a => a.libelle).join(", ")}</p>
              <p><strong>Allergies :</strong> {dossier.allergies.map(a => a.libelle).join(", ")}</p>
              <p>
                <strong>Contact d'urgence :</strong> {dossier.contactUrgence.nom} - {dossier.contactUrgence.telephone} ({dossier.contactUrgence.lien})
              </p>
              <p><strong>Statut :</strong> {dossier.statut}</p>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default MyDossiers;
