import React, { useEffect, useState } from 'react';
import api from '../endpoint'; // Assure-toi que le chemin est correct

const PatientDashboard = () => {
  const [patient, setPatient] = useState(null);
  const [medecin, setMedecin] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchPatientData = async () => {
      try {
        // Récupère le dossier du patient connecté
        const dossiers = await api.getData('/mes-dossiers');
        
        if (dossiers.length > 0) {
          const dossier = dossiers[0]; // Si plusieurs dossiers, prendre le premier actif
          setPatient(dossier.patientId);
          setMedecin(dossier.medecinId);
        } else {
          setError('Aucun dossier trouvé pour ce patient.');
        }
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    fetchPatientData();
  }, []);

  if (loading) return <p>Chargement...</p>;
  if (error) return <p>Erreur: {error}</p>;

  return (
    <div>
      <h1>Bienvenue {patient.fullName}</h1>
      <h2>Détails du patient</h2>
      <p>Email: {patient.email}</p>
      <p>Sexe: {patient.sexe}</p>
      <p>Date de naissance: {new Date(patient.date_of_birth).toLocaleDateString()}</p>
      
      <h2>Médecin référent</h2>
      <p>Nom: {medecin.fullName}</p>
      <p>Email: {medecin.email}</p>
    </div>
  );
};

export default PatientDashboard;
