import React, { useEffect, useState } from 'react';
import axios from 'axios';

const CreateDossier = () => {
  const [patients, setPatients] = useState([]);
  const [patientId, setPatientId] = useState('');

  const [resumeMedical, setResumeMedical] = useState('');
  const [groupeSanguin, setGroupeSanguin] = useState('');
  const [antecedents, setAntecedents] = useState([]);
  const [allergies, setAllergies] = useState([]);
  const [contactUrgence, setContactUrgence] = useState({
    nom: '',
    telephone: '',
    lien: ''
  });

  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  /* =========================
     Charger les patients
  ========================= */
  useEffect(() => {
    const fetchPatients = async () => {
      try {
        const token = localStorage.getItem('token');

        const res = await axios.get(
          'http://localhost:5000/api/patients',
          {
            headers: {
              Authorization: `Bearer ${token}`
            }
          }
        );

        setPatients(res.data);
      } catch (err) {
        console.error(err);
        setError("‚ùå Impossible de charger la liste des patients");
      }
    };

    fetchPatients();
  }, []);

  /* =========================
     Soumission du formulaire
  ========================= */
  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');

    try {
      const token = localStorage.getItem('token');

      await axios.post(
        'http://localhost:5000/api/new-dossier',
        {
          patientId,
          resumeMedical,
          groupeSanguin,
          antecedents,
          allergies,
          contactUrgence
        },
        {
          headers: {
            Authorization: `Bearer ${token}`
          }
        }
      );

      alert('‚úÖ Dossier m√©dical cr√©√© avec succ√®s');

      // reset
      setPatientId('');
      setResumeMedical('');
      setGroupeSanguin('');
      setAntecedents([]);
      setAllergies([]);
      setContactUrgence({ nom: '', telephone: '', lien: '' });

    } catch (err) {
      console.error(err);

      if (err.response?.status === 401) {
        setError('üîê Vous devez √™tre connect√©');
      } else if (err.response?.status === 403) {
        setError('‚õî Acc√®s refus√©');
      } else {
        setError('‚ùå Erreur serveur lors de la cr√©ation du dossier');
      }
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={{ padding: 20 }}>
      <h2>ü©∫ Cr√©er un dossier m√©dical</h2>

      {error && <p style={{ color: 'red' }}>{error}</p>}

      <form onSubmit={handleSubmit}>
        {/* ======================
            S√©lection du patient
        ====================== */}
        <label>Patient</label>
        <select
          value={patientId}
          onChange={(e) => setPatientId(e.target.value)}
          required
        >
          <option value="">-- S√©lectionner un patient --</option>
          {patients.map((patient) => (
            <option key={patient._id} value={patient._id}>
              {patient.fullName} ‚Äî {patient.email}
            </option>
          ))}
        </select>

        <textarea
          placeholder="R√©sum√© m√©dical"
          value={resumeMedical}
          onChange={(e) => setResumeMedical(e.target.value)}
        />

        <input
          type="text"
          placeholder="Groupe sanguin"
          value={groupeSanguin}
          onChange={(e) => setGroupeSanguin(e.target.value)}
        />

        <input
          type="text"
          placeholder="Ant√©c√©dents (s√©par√©s par virgule)"
          onChange={(e) => setAntecedents(e.target.value.split(','))}
        />

        <input
          type="text"
          placeholder="Allergies (s√©par√©es par virgule)"
          onChange={(e) => setAllergies(e.target.value.split(','))}
        />

        <input
          type="text"
          placeholder="Contact urgence - Nom"
          value={contactUrgence.nom}
          onChange={(e) =>
            setContactUrgence({ ...contactUrgence, nom: e.target.value })
          }
        />

        <input
          type="text"
          placeholder="T√©l√©phone urgence"
          value={contactUrgence.telephone}
          onChange={(e) =>
            setContactUrgence({ ...contactUrgence, telephone: e.target.value })
          }
        />

        <input
          type="text"
          placeholder="Lien (parent, ami...)"
          value={contactUrgence.lien}
          onChange={(e) =>
            setContactUrgence({ ...contactUrgence, lien: e.target.value })
          }
        />

        <button type="submit" disabled={loading}>
          {loading ? 'Cr√©ation...' : 'Cr√©er le dossier'}
        </button>
      </form>
    </div>
  );
};

export default CreateDossier;
