import React, { useEffect, useState } from "react";
import * as api from "../api/endpoint";
import "./DoctorDashboard.css";

const DoctorDashboard = () => {
  const [dossiers, setDossiers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  useEffect(() => {
    loadDossiers();
  }, []);

  // Charger tous les dossiers du médecin connecté
  const loadDossiers = async () => {
    setLoading(true);
    try {
      const data = await api.getData("/dossier/mes-dossiers"); // endpoint backend
      setDossiers(data);
      setLoading(false);
    } catch (err) {
      console.error("Erreur lors du chargement des dossiers:", err);
      setError(err.message || "Erreur lors du chargement des dossiers");
      setLoading(false);
    }
  };

  // Afficher le dossier complet dans un alert (ou modal plus tard)
  const viewDossier = (dossier) => {
    const patient = dossier.patientId; // patient peuplé
    alert(
      `Dossier de ${patient.fullName}:\n
Date de naissance: ${patient.date_of_birth || "Non renseignée"}\n
Sexe: ${patient.sexe || "Non renseigné"}\n
Résumé médical: ${dossier.resumeMedical || "Aucun"}\n
Allergies: ${(dossier.allergies || []).join(", ") || "Aucune"}\n
Antécédents: ${(dossier.antecedents || []).join(", ") || "Aucun"}\n
Groupe sanguin: ${dossier.groupeSanguin || "Non renseigné"}\n
Contact urgence: ${dossier.contactUrgence ? `${dossier.contactUrgence.nom} - ${dossier.contactUrgence.telephone} (${dossier.contactUrgence.lien || "Lien non renseigné"})` : "Aucun"}`
    );
  };

  return (
    <div className="doctor-interface">
      <header className="doctor-header">
        <h1>Dashboard Médecin</h1>
      </header>

      {loading && <p>Chargement des dossiers...</p>}
      {error && <p style={{ color: "red" }}>{error}</p>}

      {!loading && !error && (
        <div className="dossiers-list">
          {dossiers.length === 0 ? (
            <p>Aucun dossier enregistré pour vos patients.</p>
          ) : (
            <table>
              <thead>
                <tr>
                  <th>Nom</th>
                  <th>Email</th>
                  <th>Date de naissance</th>
                  <th>Sexe</th>
                  <th>Résumé médical</th>
                  <th>Actions</th>
                </tr>
              </thead>
              <tbody>
                {dossiers.map((dossier) => {
                  const patient = dossier.patientId;
                  return (
                    <tr key={dossier._id}>
                      <td>{patient.fullName}</td>
                      <td>{patient.email}</td>
                      <td>{patient.date_of_birth ? new Date(patient.date_of_birth).toLocaleDateString() : "Non renseignée"}</td>
                      <td>{patient.sexe || "Non renseigné"}</td>
                      <td>{dossier.resumeMedical || "Aucun"}</td>
                      <td>
                        <button onClick={() => viewDossier(dossier)}>Voir Dossier</button>
                      </td>
                    </tr>
                  );
                })}
              </tbody>
            </table>
          )}
        </div>
      )}
    </div>
  );
};

export default DoctorDashboard;
