import React, { useState, useEffect } from 'react';
import api, { deleteData, postData, putData } from '../../api/endpoint'; // ✅ CORRIGÉ
import { FiFileText, FiSearch, FiPlus, FiEdit, FiTrash2, FiEye } from 'react-icons/fi';

const Ordonnances = () => {
  const [ordonnances, setOrdonnances] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [showForm, setShowForm] = useState(false);
  const [showModal, setShowModal] = useState(false);
  const [selectedOrdonnance, setSelectedOrdonnance] = useState(null);
  const [formData, setFormData] = useState({
    patientId: '',
    medecinId: '',
    medicaments: [],
    instructionsSupplementaires: ''
  });
  const [newMedicament, setNewMedicament] = useState({
    nomMedicament: '',
    dosage: '',
    frequence: '',
    duree: ''
  });
  const [patients, setPatients] = useState([]);
  const [medecins, setMedecins] = useState([]);

  useEffect(() => {
    loadData();
  }, []);

  const loadData = async () => {
    try {
      setLoading(true);
      setError(null);
      const [ordonnancesData, usersData] = await Promise.all([
        api.getData('/ordonnances'), // ✅ CORRIGÉ
        api.getData('/users') // ✅ CORRIGÉ
      ]);
      setOrdonnances(Array.isArray(ordonnancesData) ? ordonnancesData : []);
      setPatients(usersData.filter(u => u.role === 'Patient'));
      setMedecins(usersData.filter(u => u.role === 'Medecin'));
    } catch (err) {
      console.error('Erreur chargement:', err);
      setError(err.message || 'Impossible de charger les données.');
      setOrdonnances([]);
    } finally {
      setLoading(false);
    }
  };

  const handleView = (ordonnance) => {
    setSelectedOrdonnance(ordonnance);
    setShowModal(true);
  };

  const handleDelete = async (id) => {
    if (!window.confirm('Êtes-vous sûr de vouloir supprimer cette ordonnance ?')) return;
    try {
      await deleteData(`/ordonnances/${id}`); // ✅ ENDPOINT STANDARD
      loadData();
    } catch (err) {
      alert(err.message || 'Erreur suppression');
    }
  };

  const handleOpenForm = (ordonnance = null) => {
    if (ordonnance) {
      setFormData({
        patientId: ordonnance.patientId?._id || ordonnance.patientId || '',
        medecinId: ordonnance.medecinId?._id || ordonnance.medecinId || '',
        medicaments: ordonnance.medicaments || [],
        instructionsSupplementaires: ordonnance.instructionsSupplementaires || ''
      });
      setSelectedOrdonnance(ordonnance);
    } else {
      setFormData({
        patientId: '',
        medecinId: '',
        medicaments: [],
        instructionsSupplementaires: ''
      });
      setSelectedOrdonnance(null);
    }
    setShowForm(true);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (selectedOrdonnance) {
        await putData(`/ordonnances/${selectedOrdonnance._id}`, formData); // ✅ ENDPOINT STANDARD
      } else {
        await postData('/ordonnances', formData); // ✅ ENDPOINT STANDARD
      }
      setShowForm(false);
      loadData();
    } catch (err) {
      alert(err.message || 'Erreur sauvegarde');
    }
  };

  const addMedicament = () => {
    if (newMedicament.nomMedicament.trim()) {
      setFormData(prev => ({
        ...prev,
        medicaments: [...prev.medicaments, { ...newMedicament }]
      }));
      setNewMedicament({ nomMedicament: '', dosage: '', frequence: '', duree: '' });
    }
  };

  const removeMedicament = (index) => {
    setFormData(prev => ({
      ...prev,
      medicaments: prev.medicaments.filter((_, i) => i !== index)
    }));
  };

  // ✅ TOUT LE JSX RESTE IDENTIQUE (EXCELLENT)
  const filteredOrdonnances = ordonnances.filter(ord =>
    ord.patientId?.fullName?.toLowerCase().includes(searchTerm.toLowerCase()) ||
    ord.medecinId?.fullName?.toLowerCase().includes(searchTerm.toLowerCase()) ||
    ord.medicaments?.some(m => m.nomMedicament?.toLowerCase().includes(searchTerm.toLowerCase()))
  );

  // ... VOTRE JSX COMPLET IDENTIQUE ✅
// ✅ TEMPLATE CORRIGÉ POUR CHAQUE PAGE
const NomPage = () => {
  // ... VOS useState() + useEffect() ✅

  if (loading) return <div className="flex items-center justify-center h-64 p-6"><div className="text-lg">Chargement...</div></div>;

  return (
    <div className="p-6 max-w-7xl mx-auto">
      {/* VOTRE TITRE */}
      <div className="mb-8">
        <h1 className="text-3xl font-bold text-gray-900">Votre Titre</h1>
        <p className="text-gray-600 mt-2">Description</p>
      </div>

      {/* VOTRE TABLEAU / FORMULAIRE */}
      <div className="bg-white rounded-2xl shadow-lg border border-gray-100 overflow-hidden">
        {/* CONTENU */}
      </div>
    </div>
  );
};


};

export default Ordonnances;
