import React, { useState, useEffect } from 'react';
import { getData, postData } from '../../api/endpoint';
import { FiCalendar, FiClock, FiUser, FiCheck, FiX } from 'react-icons/fi';

const RendezVous = () => {
  const [rdvs, setRdvs] = useState([]);
  const [formData, setFormData] = useState({
    patientId: '',
    medecinId: '',
    date: '',
    heure: '',
    motif: '',
    statut: 'PLANIFIE'
  });

  useEffect(() => {
    loadRdvs();
  }, []);

  const loadRdvs = async () => {
    const data = await getData('/rendezvous');
    setRdvs(data);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    await postData('/new-rdv', formData);
    setFormData({ patientId: '', medecinId: '', date: '', heure: '', motif: '', statut: 'PLANIFIE' });
    loadRdvs();
  };

  const updateStatut = async (id, statut) => {
    await postData(`/rdv/${id}/statut`, { statut });
    loadRdvs();
  };

  return (
    <div className="p-8">
      <h1 className="text-3xl font-bold mb-8 flex items-center gap-3">
        <FiCalendar size={36} className="text-blue-600" /> Rendez-vous
      </h1>

      {/* Formulaire rapide */}
      <form onSubmit={handleSubmit} className="bg-white p-6 rounded-xl shadow-lg mb-8 grid md:grid-cols-3 gap-4">
        <input placeholder="Patient ID" value={formData.patientId} onChange={e => setFormData(p => ({...p, patientId: e.target.value}))} className="p-3 border rounded-lg" />
        <input type="date" value={formData.date} onChange={e => setFormData(p => ({...p, date: e.target.value}))} className="p-3 border rounded-lg" />
        <input type="time" value={formData.heure} onChange={e => setFormData(p => ({...p, heure: e.target.value}))} className="p-3 border rounded-lg" />
        <input placeholder="Motif" value={formData.motif} onChange={e => setFormData(p => ({...p, motif: e.target.value}))} className="p-3 border rounded-lg md:col-span-2" />
        <button type="submit" className="bg-green-500 text-white px-6 py-3 rounded-lg font-bold col-span-full md:col-span-1">
          <FiCalendar className="inline mr-2" /> Planifier
        </button>
      </form>

      {/* Calendrier */}
      <div className="grid gap-4">
        {rdvs.map(rdv => (
          <div key={rdv._id} className={`p-6 rounded-xl shadow-md border-l-4 ${
            rdv.statut === 'CONFIRME' ? 'border-green-500 bg-green-50' :
            rdv.statut === 'ANNULE' ? 'border-red-500 bg-red-50' :
            'border-blue-500 bg-blue-50'
          }`}>
            <div className="flex justify-between items-start">
              <div className="flex items-center gap-4 flex-1">
                <div className="text-2xl">
                  <FiUser className={`${
                    rdv.statut === 'CONFIRME' ? 'text-green-500' :
                    rdv.statut === 'ANNULE' ? 'text-red-500' : 'text-blue-500'
                  }`} />
                </div>
                <div>
                  <h3 className="font-bold text-lg">{rdv.patientId?.fullName}</h3>
                  <p className="flex items-center gap-2 text-gray-600">
                    <FiCalendar className="inline" />
                    {new Date(rdv.date).toLocaleDateString()} Ã  {rdv.heure}
                  </p>
                  <p className="text-sm text-gray-500">{rdv.motif}</p>
                </div>
              </div>
              <div className="flex gap-2">
                <button onClick={() => updateStatut(rdv._id, 'CONFIRME')} className="p-2 text-green-500 hover:bg-green-100 rounded-full">
                  <FiCheck />
                </button>
                <button onClick={() => updateStatut(rdv._id, 'ANNULE')} className="p-2 text-red-500 hover:bg-red-100 rounded-full">
                  <FiX />
                </button>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default RendezVous;
