// src/pages/Composants/MyDossiers.jsx
import React, { useEffect, useState } from "react";
import { getData, getAuthToken, decodeToken } from "../../api/endpoint";

const MyDossiers = () => {
  const [dossiers, setDossiers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  useEffect(() => {
    const fetchDossiers = async () => {
      setLoading(true);
      setError("");

      try {
        // Récupérer l'ID du patient depuis le token
        const token = getAuthToken();
        const payload = decodeToken(token);
        const patientId = payload.id;

        // Appel API pour récupérer les dossiers du patient
        const data = await getData(`/patient/dossiers?patientId=${patientId}`);
        setDossiers(data);
      } catch (err) {
        setError(err.message || "Impossible de charger les dossiers");
      } finally {
        setLoading(false);
      }
    };

    fetchDossiers();
  }, []);

  if (loading) return <div>Chargement des dossiers...</div>;
  if (error) return <div className="text-red-500">{error}</div>;
  if (dossiers.length === 0) return <div>Aucun dossier trouvé.</div>;

  return (
    <div>
      <h2 className="text-2xl font-bold mb-4">Mes dossiers</h2>
      <div className="flex flex-col gap-4">
        {dossiers.map((dossier) => (
          <div key={dossier._id} className="p-4 border rounded shadow">
            <p><strong>Résumé médical:</strong> {dossier.resumeMedical}</p>
            <p><strong>Groupe sanguin:</strong> {dossier.groupeSanguin}</p>
            <p><strong>Antécédents:</strong> {dossier.antecedents.map(a => a.libelle).join(", ")}</p>
            <p><strong>Allergies:</strong> {dossier.allergies.map(a => a.libelle).join(", ")}</p>
            <p><strong>Contact urgence:</strong> {dossier.contactUrgence.nom} - {dossier.contactUrgence.telephone} ({dossier.contactUrgence.lien})</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default MyDossiers;
