import React, { useState, useEffect } from 'react';
import { Routes, Route, useLocation, useParams } from 'react-router-dom';

// Composants manquants dÃ©finis localement
const LoadingScreen = () => (
  <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-slate-50 to-blue-50">
    <div className="text-center">
      <div className="animate-spin rounded-full h-32 w-32 border-b-2 border-blue-600 mx-auto mb-8"></div>
      <h2 className="text-2xl font-bold text-gray-900 mb-2">Chargement du tableau de bord...</h2>
      <p className="text-gray-600">Veuillez patienter quelques instants</p>
    </div>
  </div>
);

const NotFoundPage = () => (
  <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-slate-50 to-blue-50">
    <div className="text-center p-12 max-w-md mx-auto">
      <div className="text-6xl mb-8">ðŸ”</div>
      <h1 className="text-4xl font-bold text-gray-900 mb-4">Page non trouvÃ©e</h1>
      <p className="text-xl text-gray-600 mb-8">La page que vous cherchez n'existe pas.</p>
      <a href="/" className="bg-blue-600 text-white px-8 py-4 rounded-xl font-bold hover:bg-blue-700 transition-all">
        Retour au tableau de bord
      </a>
    </div>
  </div>
);

// Composants stubs pour les pages manquantes
const ProfileMedecin = ({ profile }) => (
  <div className="p-6 border-b border-gray-200">
    <h2 className="text-xl font-bold text-gray-900 mb-4">ðŸ‘¨â€âš•ï¸ Dr {profile?.prenom} {profile?.nom}</h2>
    <div className="space-y-2 text-sm text-gray-600">
      <div>ðŸ“§ {profile?.email}</div>
      <div>ðŸ“± {profile?.telephone}</div>
      <div>ðŸ¥ {profile?.specialite} - {profile?.etablissement}</div>
    </div>
  </div>
);

const NavigationMedecin = ({ activePath, patientCount, rdvCount }) => (
  <nav className="p-6 space-y-2">
    <a href="/" className={`flex items-center p-3 rounded-xl font-semibold transition-all ${activePath === '/' ? 'bg-blue-100 text-blue-800 shadow-lg' : 'hover:bg-gray-100'}`}>
      ðŸ“Š Tableau de bord
    </a>
    <a href="/patients" className={`flex items-center p-3 rounded-xl font-semibold transition-all ${activePath === '/patients' ? 'bg-green-100 text-green-800 shadow-lg' : 'hover:bg-gray-100'}`}>
      ðŸ‘¥ Patients ({patientCount})
    </a>
    <a href="/ordonnances" className={`flex items-center p-3 rounded-xl font-semibold transition-all ${activePath === '/ordonnances' ? 'bg-purple-100 text-purple-800 shadow-lg' : 'hover:bg-gray-100'}`}>
      ðŸ’Š Ordonnances
    </a>
    <a href="/rdv" className={`flex items-center p-3 rounded-xl font-semibold transition-all ${activePath === '/rdv' ? 'bg-orange-100 text-orange-800 shadow-lg' : 'hover:bg-gray-100'}`}>
      ðŸ“… RDV ({rdvCount})
    </a>
    <a href="/examens" className={`flex items-center p-3 rounded-xl font-semibold transition-all ${activePath === '/examens' ? 'bg-indigo-100 text-indigo-800 shadow-lg' : 'hover:bg-gray-100'}`}>
      ðŸ”¬ Examens
    </a>
  </nav>
);

const DashboardMedecin = ({ profile, patients, rdv, ordonnances }) => (
  <div className="space-y-8">
    <div className="grid lg:grid-cols-3 gap-6">
      <div className="bg-gradient-to-r from-blue-500 to-blue-600 text-white p-8 rounded-2xl shadow-xl">
        <h3 className="text-2xl font-bold mb-4">ðŸ‘¥ Patients actifs</h3>
        <div className="text-4xl font-bold">{patients?.length || 0}</div>
      </div>
      <div className="bg-gradient-to-r from-green-500 to-green-600 text-white p-8 rounded-2xl shadow-xl">
        <h3 className="text-2xl font-bold mb-4">ðŸ“… RDV aujourd'hui</h3>
        <div className="text-4xl font-bold">{rdv?.length || 0}</div>
      </div>
      <div className="bg-gradient-to-r from-purple-500 to-purple-600 text-white p-8 rounded-2xl shadow-xl">
        <h3 className="text-2xl font-bold mb-4">ðŸ’Š Ordonnances</h3>
        <div className="text-4xl font-bold">{ordonnances || 0}</div>
      </div>
    </div>
  </div>
);

