import React from 'react';
import { NavLink, useLocation } from 'react-router-dom';
import '../Styles/Navbar.css';

// Barre de navigation principale
const Navbar = () => {
  const location = useLocation();

  // Ne pas afficher la navbar sur les pages login/register/admin
  if (location.pathname === '/login' || location.pathname === '/register' || location.pathname.startsWith('/admin')) {
    return null;
  }

  return (
    <nav className="navbar">
      <div className="navbar-logo">
        <NavLink to="/" className="logo">
          Govaimed Sanitaire
        </NavLink>
      </div>

      <ul className="navbar-links">
        <li><NavLink to="/" end>Accueil</NavLink></li>
        <li><NavLink to="#services">Fonctionnalités</NavLink></li>
        <li><NavLink to="#about">Sécurité</NavLink></li>
        <li><NavLink to="#contact">Contact</NavLink></li>
        <li><NavLink to="/login">Connexion</NavLink></li>
        <li><NavLink to="/register">S'inscrire</NavLink></li>
      </ul>
    </nav>
  );
};

export default Navbar;
