import React, { useState, useEffect } from 'react';
import CreateDossierModal from './CreateDossierModal';
import PatientsList from './PatientsList';
import DossiersList from './DossiersList';
import { getData } from '../api/endpoint';

const DoctorDashboard = () => {
  const [activeSection, setActiveSection] = useState('dossiers');
  const [showCreateModal, setShowCreateModal] = useState(false);
  const [patientsCount, setPatientsCount] = useState(0);
  const [dossiersCount, setDossiersCount] = useState(0);

  useEffect(() => {
    // Compteurs en temps r√©el
    const updateCounts = async () => {
      try {
        const [patients, dossiers] = await Promise.all([
          getData('/patients'),
          getData('/dossier/mes-dossiers')
        ]);
        setPatientsCount(Array.isArray(patients) ? patients.length : 0);
        setDossiersCount(Array.isArray(dossiers) ? dossiers.length : 0);
      } catch (err) {
        console.log('Comptage:', err);
      }
    };

    updateCounts();
    const interval = setInterval(updateCounts, 30000); // Toutes les 30s
    return () => clearInterval(interval);
  }, []);

  return (
    <div style={{ padding: '2rem', maxWidth: '1400px', margin: '0 auto' }}>
      <header style={{ textAlign: 'center', marginBottom: '3rem' }}>
        <h1 style={{ color: '#1976d2', fontSize: '2.5rem' }}>üè• Espace M√©decin</h1>
        <p style={{ color: '#666', fontSize: '1.2rem' }}>Gestion patients & dossiers en temps r√©el</p>
      </header>

      {/* BOUTONS ACTIONS PRINCIPALES */}
      <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(280px, 1fr))', gap: '1.5rem', marginBottom: '3rem' }}>
        <ActionButton 
          icon="‚ûï" title="Nouveau Dossier" 
          onClick={() => setShowCreateModal(true)}
          color="#4caf50"
        />
        <ActionButton 
          icon="üë•" title={`Patients (${patientsCount})`} 
          onClick={() => setActiveSection('patients')}
          color="#2196f3"
          badge={patientsCount > 0}
        />
        <ActionButton 
          icon="üìÅ" title={`Dossiers (${dossiersCount})`} 
          onClick={() => setActiveSection('dossiers')}
          color="#ff9800"
          badge={dossiersCount > 0}
        />
        <ActionButton 
          icon="üìä" title="Statistiques" 
          onClick={() => setActiveSection('stats')}
          color="#9c27b0"
        />
      </div>

      {/* CONTENU */}
      <div style={{ background: 'white', borderRadius: '16px', boxShadow: '0 10px 40px rgba(0,0,0,0.1)', overflow: 'hidden' }}>
        {activeSection === 'patients' && <PatientsList />}
        {activeSection === 'dossiers' && <DossiersList />}
        {activeSection === 'stats' && <StatsSection />}
      </div>

      {showCreateModal && (
        <CreateDossierModal 
          onClose={() => setShowCreateModal(false)}
          onSuccess={() => {
            setShowCreateModal(false);
            // Refresh counts
            window.location.reload(); 
          }}
        />
      )}
    </div>
  );
};

// Composant bouton r√©utilisable
const ActionButton = ({ icon, title, onClick, color, badge = false }) => (
  <button onClick={onClick} style={{
    padding: '2rem 1.5rem', textAlign: 'center',
    background: `linear-gradient(135deg, ${color}, ${hexToRgb(color, 0.1)})`,
    color: 'white', border: 'none', borderRadius: '16px',
    fontSize: '1.3rem', fontWeight: 'bold', cursor: 'pointer',
    boxShadow: `0 8px 25px ${hexToRgb(color, 0.3)}`,
    transition: 'all 0.3s ease',
    position: 'relative',
    overflow: 'hidden'
  }}
  onMouseOver={(e) => {
    e.target.style.transform = 'translateY(-5px)';
    e.target.style.boxShadow = `0 15px 35px ${hexToRgb(color, 0.4)}`;
  }}
  onMouseOut={(e) => {
    e.target.style.transform = 'translateY(0)';
    e.target.style.boxShadow = `0 8px 25px ${hexToRgb(color, 0.3)}`;
  }}
  >
    <div style={{ fontSize: '2.5rem', marginBottom: '0.5rem' }}>{icon}</div>
    <div>{title}</div>
    {badge && (
      <div style={{
        position: 'absolute', top: '1rem', right: '1rem',
        background: '#f44336', color: 'white', borderRadius: '50%',
        width: '24px', height: '24px', fontSize: '0.8rem', fontWeight: 'bold',
        display: 'flex', alignItems: 'center', justifyContent: 'center'
      }}>
        !
      </div>
    )}
  </button>
);

// Utilitaire couleur
const hexToRgb = (hex, alpha = 1) => {
  const r = parseInt(hex.slice(1, 3), 16);
  const g = parseInt(hex.slice(3, 5), 16);
  const b = parseInt(hex.slice(5, 7), 16);
  return `rgba(${r}, ${g}, ${b}, ${alpha})`;
};

const StatsSection = () => (
  <div style={{ padding: '3rem', textAlign: 'center' }}>
    <h2>üìä Statistiques</h2>
    <p>En construction...</p>
  </div>
);

export default DoctorDashboard;
