import React, { useState, useEffect } from 'react';
import { Link, useNavigate, Routes, Route, useLocation } from 'react-router-dom';
import { 
  getAuthToken, 
  decodeToken, 
  removeAuthToken 
} from '../api/endpoint';
import {
  FiUsers, FiActivity, FiCalendar, FiDollarSign, FiBarChart2, 
  FiMenu, FiChevronDown, FiPlus, FiUser, FiSettings, FiFileText
} from 'react-icons/fi';

// Import des pages admin
import Patients from './admin/Patients';
import Medecins from './admin/Medecins';
import Services from './admin/Services';
import Rdv from './admin/Rdv';
import Finances from './admin/Finances';
import Moderateurs from './admin/Moderateurs';

// Import de recharts pour les graphiques
import {
  LineChart, Line, AreaChart, Area, XAxis, YAxis, 
  CartesianGrid, Tooltip, Legend, ResponsiveContainer
} from 'recharts';

// Composant DashboardHome avec style my virtuActive
const DashboardHome = ({ stats, recentActivity }) => {
  const tendanceData = [
    { mois: 'Août', patients: 1100, medecins: 85, rdv: 420 },
    { mois: 'Sept', patients: 1150, medecins: 86, rdv: 430 },
    { mois: 'Oct', patients: 1180, medecins: 87, rdv: 440 },
    { mois: 'Nov', patients: 1200, medecins: 88, rdv: 450 },
    { mois: 'Déc', patients: 1220, medecins: 88, rdv: 455 },
    { mois: 'Jan', patients: 1247, medecins: 89, rdv: 456 },
  ];

  const revenusData = [
    { mois: 'Août', revenus: 2100000 },
    { mois: 'Sept', revenus: 2200000 },
    { mois: 'Oct', revenus: 2300000 },
    { mois: 'Nov', revenus: 2350000 },
    { mois: 'Déc', revenus: 2400000 },
    { mois: 'Jan', revenus: 2450000 },
  ];

  return (
    <>
      <div className="mb-8">
        <h2 className="text-3xl font-bold bg-gradient-to-r from-blue-900 to-blue-600 bg-clip-text text-transparent">
          Dashboard Admin
        </h2>
        <p className="text-gray-600 mt-2">Bienvenue dans le panneau de gestion my virtuActive</p>
      </div>

      {/* Stats Cards - Style my virtuActive */}
      <div className="grid grid-cols-1 md:grid-cols-2 xl:grid-cols-4 gap-6 mb-10">
        <div className="bg-white/70 backdrop-blur-md p-8 rounded-3xl shadow-xl border border-blue-100/50 hover:shadow-2xl transition-all duration-300 hover:-translate-y-1">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm font-semibold text-gray-600 uppercase tracking-wide">Total Patients</p>
              <p className="text-4xl font-bold text-gray-900 mt-2">{stats.totalPatients}</p>
              <p className="text-green-600 text-sm font-semibold mt-1">+12% ce mois</p>
            </div>
            <div className="w-16 h-16 bg-gradient-to-br from-blue-500 to-blue-600 rounded-2xl flex items-center justify-center shadow-lg">
              <FiUsers size={28} className="text-white" />
            </div>
          </div>
        </div>

        <div className="bg-white/70 backdrop-blur-md p-8 rounded-3xl shadow-xl border border-green-100/50 hover:shadow-2xl transition-all duration-300 hover:-translate-y-1">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm font-semibold text-gray-600 uppercase tracking-wide">Médecins</p>
              <p className="text-4xl font-bold text-gray-900 mt-2">{stats.totalMedecins}</p>
              <p className="text-green-600 text-sm font-semibold mt-1">+3 nouveaux</p>
            </div>
            <div className="w-16 h-16 bg-gradient-to-br from-emerald-500 to-teal-600 rounded-2xl flex items-center justify-center shadow-lg">
              <FiUser size={28} className="text-white" />
            </div>
          </div>
        </div>

        <div className="bg-white/70 backdrop-blur-md p-8 rounded-3xl shadow-xl border border-purple-100/50 hover:shadow-2xl transition-all duration-300 hover:-translate-y-1">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm font-semibold text-gray-600 uppercase tracking-wide">Rendez-vous</p>
              <p className="text-4xl font-bold text-gray-900 mt-2">{stats.totalRdv}</p>
              <p className="text-blue-600 text-sm font-semibold mt-1">+28 aujourd'hui</p>
            </div>
            <div className="w-16 h-16 bg-gradient-to-br from-purple-500 to-indigo-600 rounded-2xl flex items-center justify-center shadow-lg">
              <FiCalendar size={28} className="text-white" />
            </div>
          </div>
        </div>

        <div className="bg-white/70 backdrop-blur-md p-8 rounded-3xl shadow-xl border border-orange-100/50 hover:shadow-2xl transition-all duration-300 hover:-translate-y-1">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm font-semibold text-gray-600 uppercase tracking-wide">Revenus</p>
              <p className="text-4xl font-bold text-gray-900 mt-2">{stats.revenue.toLocaleString()} CFA</p>
              <p className="text-orange-600 text-sm font-semibold mt-1">+15% ce mois</p>
            </div>
            <div className="w-16 h-16 bg-gradient-to-br from-orange-500 to-red-500 rounded-2xl flex items-center justify-center shadow-lg">
              <FiDollarSign size={28} className="text-white" />
            </div>
          </div>
        </div>
      </div>

      {/* Graphiques */}
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-8 mb-10">
        <div className="bg-white/70 backdrop-blur-md rounded-3xl shadow-xl border border-blue-100/50 p-8">
          <div className="flex items-center justify-between mb-8">
            <h3 className="text-2xl font-bold text-gray-900">Évolution (6 mois)</h3>
          </div>
          <ResponsiveContainer width="100%" height={300}>
            <AreaChart data={tendanceData}>
              <defs>
                <linearGradient id="colorPatients" x1="0" y1="0" x2="0" y2="1">
                  <stop offset="5%" stopColor="#3b82f6" stopOpacity={0.8}/>
                  <stop offset="95%" stopColor="#3b82f6" stopOpacity={0}/>
                </linearGradient>
                <linearGradient id="colorMedecins" x1="0" y1="0" x2="0" y2="1">
                  <stop offset="5%" stopColor="#10b981" stopOpacity={0.8}/>
                  <stop offset="95%" stopColor="#10b981" stopOpacity={0}/>
                </linearGradient>
                <linearGradient id="colorRdv" x1="0" y1="0" x2="0" y2="1">
                  <stop offset="5%" stopColor="#8b5cf6" stopOpacity={0.8}/>
                  <stop offset="95%" stopColor="#8b5cf6" stopOpacity={0}/>
                </linearGradient>
              </defs>
              <CartesianGrid strokeDasharray="3 3" stroke="#e5e7eb" />
              <XAxis dataKey="mois" stroke="#6b7280" />
              <YAxis stroke="#6b7280" />
              <Tooltip contentStyle={{ backgroundColor: '#fff', border: '1px solid #e5e7eb', borderRadius: '8px' }} />
              <Legend />
              <Area type="monotone" dataKey="patients" stroke="#3b82f6" fillOpacity={1} fill="url(#colorPatients)" name="Patients" />
              <Area type="monotone" dataKey="medecins" stroke="#10b981" fillOpacity={1} fill="url(#colorMedecins)" name="Médecins" />
              <Area type="monotone" dataKey="rdv" stroke="#8b5cf6" fillOpacity={1} fill="url(#colorRdv)" name="Rendez-vous" />
            </AreaChart>
          </ResponsiveContainer>
        </div>

        <div className="bg-white/70 backdrop-blur-md rounded-3xl shadow-xl border border-blue-100/50 p-8">
          <div className="flex items-center justify-between mb-8">
            <h3 className="text-2xl font-bold text-gray-900">Évolution Revenus</h3>
          </div>
          <ResponsiveContainer width="100%" height={300}>
            <LineChart data={revenusData}>
              <CartesianGrid strokeDasharray="3 3" stroke="#e5e7eb" />
              <XAxis dataKey="mois" stroke="#6b7280" />
              <YAxis stroke="#6b7280" tickFormatter={(value) => `${(value / 1000000).toFixed(1)}M`} />
              <Tooltip formatter={(value) => [`${value.toLocaleString()} CFA`]} contentStyle={{ backgroundColor: '#fff', border: '1px solid #e5e7eb', borderRadius: '8px' }} />
              <Legend />
              <Line type="monotone" dataKey="revenus" stroke="#f59e0b" strokeWidth={3} dot={{ fill: '#f59e0b', r: 6 }} activeDot={{ r: 8 }} name="Revenus (CFA)" />
            </LineChart>
          </ResponsiveContainer>
        </div>
      </div>

      {/* Recent Activity + Quick Actions */}
      <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
        <div className="lg:col-span-2 bg-white/70 backdrop-blur-md rounded-3xl shadow-xl border border-blue-100/50 p-8">
          <h3 className="text-2xl font-bold text-gray-900 mb-8 flex items-center gap-3">
            Activité récente
            <span className="px-3 py-1 bg-blue-100 text-blue-800 text-xs rounded-2xl font-semibold">Live</span>
          </h3>
          <div className="space-y-4">
            {recentActivity.map((activity) => (
              <div key={activity.id} className="flex items-start p-6 bg-gradient-to-r from-blue-50 to-indigo-50 rounded-2xl hover:shadow-md transition-all group border border-blue-100/50">
                <div className="w-12 h-12 bg-gradient-to-r from-blue-500 to-blue-600 rounded-2xl flex items-center justify-center mr-4 flex-shrink-0 shadow-lg">
                  <span className="text-white font-bold">✓</span>
                </div>
                <div className="flex-1 min-w-0">
                  <p className="font-semibold text-gray-900">{activity.action}</p>
                  <p className="text-sm text-gray-600 mt-1">{activity.user}</p>
                </div>
                <span className="text-sm font-semibold text-blue-600 ml-4 whitespace-nowrap">{activity.time}</span>
              </div>
            ))}
          </div>
        </div>

        <div className="bg-white/70 backdrop-blur-md rounded-3xl shadow-xl border border-green-100/50 p-8">
          <h3 className="text-xl font-bold text-gray-900 mb-8 flex items-center gap-3">
            Actions rapides
          </h3>
          <div className="space-y-4">
            <Link to="/admin/utilisateurs" className="w-full block bg-gradient-to-r from-emerald-500 to-teal-600 text-white p-6 rounded-2xl font-semibold shadow-xl hover:shadow-2xl hover:-translate-y-1 transition-all duration-200 flex items-center justify-center gap-3">
              <FiPlus size={24} />
              Nouvel utilisateur
            </Link>
            <Link to="/admin/dossiers/creer" className="w-full block bg-gradient-to-r from-blue-500 to-indigo-600 text-white p-6 rounded-2xl font-semibold shadow-xl hover:shadow-2xl hover:-translate-y-1 transition-all duration-200 flex items-center justify-center gap-3">
              <FiPlus size={24} />
              Créer dossier
            </Link>
            <Link to="/admin/services" className="w-full block bg-gradient-to-r from-purple-500 to-pink-600 text-white p-6 rounded-2xl font-semibold shadow-xl hover:shadow-2xl hover:-translate-y-1 transition-all duration-200 flex items-center justify-center gap-3">
              <FiPlus size={24} />
              Gérer services
            </Link>
          </div>
        </div>
      </div>
    </>
  );
};

// ✅ Layout principal avec style my virtuActive
const AdminDashboard = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const [sidebarOpen, setSidebarOpen] = useState(false);
  const [user, setUser] = useState(null);
  const [stats] = useState({
    totalPatients: 1247,
    totalMedecins: 89,
    totalRdv: 456,
    revenue: 2450000
  });
  const [recentActivity, setRecentActivity] = useState([]);

  useEffect(() => {
    const token = getAuthToken();
    if (!token) {
      navigate('/login');
      return;
    }

    const decoded = decodeToken(token);
    if (decoded?.role !== 'Admin') {
      navigate('/dashboard');
      return;
    }
    
    setUser(decoded);
    loadAdminData();
  }, [navigate]);

  const loadAdminData = async () => {
    setRecentActivity([
      { id: 1, action: 'Nouveau patient inscrit', user: 'Mamadou Diop', time: '2min' },
      { id: 2, action: 'Médecin ajouté', user: 'Dr. Awa Ndiaye', time: '15min' },
      { id: 3, action: 'RDV confirmé', user: 'Service Pédiatrie', time: '1h' }
    ]);
  };

  const logout = () => {
    removeAuthToken();
    navigate('/login');
  };

  const navigation = [
    { name: 'Dashboard', icon: FiBarChart2, href: '/admin', exact: true },
    { name: 'Patients', icon: FiUsers, href: '/admin/patients' },
    { name: 'Médecins', icon: FiUser, href: '/admin/medecins' },
    { name: 'Consultations', icon: FiActivity, href: '/admin/consultations' },
    { name: 'Ordonnances', icon: FiFileText, href: '/admin/ordonnances' },
    { name: 'Rendez-vous', icon: FiCalendar, href: '/admin/rdv' },
    { name: 'Services', icon: FiActivity, href: '/admin/services' },
    { name: 'Journal', icon: FiFileText, href: '/admin/journal' },
    { name: 'Finances', icon: FiDollarSign, href: '/admin/finances' },
    { name: 'Modérateurs', icon: FiUsers, href: '/admin/moderateurs' },
    { name: 'Paramètres', icon: FiSettings, href: '/admin/settings' }
  ];

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-50 to-blue-50 font-sans">
      {/* Header my virtuActive */}
      <header className="bg-white/80 backdrop-blur-md shadow-sm border-b border-blue-100/50 fixed w-full z-50">
        <div className="max-w-7xl mx-auto px-6 py-4 flex items-center justify-between">
          {/* Logo */}
          <div className="flex items-center gap-3">
            <div className="w-12 h-12 bg-gradient-to-r from-blue-600 to-blue-700 rounded-2xl flex items-center justify-center shadow-lg">
              <span className="text-white font-bold text-xl">mV</span>
            </div>
            <div>
              <h1 className="text-2xl font-bold bg-gradient-to-r from-blue-900 to-blue-600 bg-clip-text text-transparent">
                my virtuActive
              </h1>
              <span className="text-xs text-blue-600 font-medium">Administration</span>
            </div>
          </div>

          {/* Menu principal */}
          <nav className="hidden md:flex gap-2">
            <button className={`px-4 py-2 rounded-xl text-sm font-semibold transition-all duration-200 flex items-center gap-2 ${
              location.pathname === '/admin' 
                ? 'bg-blue-600 text-white shadow-lg shadow-blue-200' 
                : 'text-gray-700 hover:bg-blue-50 hover:text-blue-700'
            }`}>
              <FiActivity size={16} />
              <span>Dashboard</span>
            </button>
            <button className={`px-4 py-2 rounded-xl text-sm font-semibold transition-all duration-200 flex items-center gap-2 ${
              location.pathname.startsWith('/admin/patients') 
                ? 'bg-blue-600 text-white shadow-lg shadow-blue-200' 
                : 'text-gray-700 hover:bg-blue-50 hover:text-blue-700'
            }`}>
              <FiUsers size={16} />
              <span>Patients</span>
            </button>
          </nav>

          {/* Profil */}
          <div className="flex items-center gap-4">
            <div className="flex items-center gap-2 bg-blue-50 px-4 py-2 rounded-xl">
              <div className="w-8 h-8 bg-gradient-to-r from-blue-500 to-blue-600 rounded-full flex items-center justify-center">
                <span className="text-white text-xs font-bold">AD</span>
              </div>
              <span className="text-sm font-semibold text-gray-900 hidden sm:inline">
                {user?.email || 'Admin Principal'}
              </span>
            </div>
            <div className="flex items-center gap-2 p-2 bg-gray-100 rounded-xl hover:bg-gray-200 cursor-pointer group" onClick={logout}>
              <FiChevronDown size={18} className="text-gray-600 group-hover:text-gray-900 transition-colors" />
              <div className="w-8 h-8 bg-gradient-to-br from-purple-500 to-pink-500 rounded-full flex items-center justify-center shadow-md">
                <span className="text-white text-xs font-bold">JD</span>
              </div>
            </div>
          </div>
        </div>
      </header>

      <div className="pt-20 flex">
        {/* Sidebar my virtuActive */}
        <aside className={`bg-white/70 backdrop-blur-md shadow-xl border-r border-blue-100/50 fixed h-full w-72 z-40 transform transition-transform duration-300 ease-in-out lg:translate-x-0 ${
          sidebarOpen ? 'translate-x-0' : '-translate-x-full lg:translate-x-0'
        }`}>
          <div className="p-6 border-b border-gray-100">
            <h2 className="text-xl font-bold text-gray-900">Navigation</h2>
          </div>
          <nav className="p-4 space-y-2">
            {navigation.map((item) => {
              const isActive = item.exact 
                ? location.pathname === item.href 
                : location.pathname.startsWith(item.href);
              return (
                <Link
                  key={item.name}
                  to={item.href}
                  className={`w-full flex items-center gap-4 p-4 rounded-2xl text-left transition-all duration-200 group hover:bg-blue-50 hover:shadow-md ${
                    isActive
                      ? 'bg-gradient-to-r from-blue-600 to-blue-700 text-white shadow-lg shadow-blue-200'
                      : 'text-gray-700 hover:text-blue-900'
                  }`}
                >
                  <item.icon 
                    size={22} 
                    className={`transition-transform group-hover:scale-110 ${
                      isActive ? 'text-white' : ''
                    }`} 
                  />
                  <span className="font-semibold">{item.name}</span>
                </Link>
              );
            })}
          </nav>
        </aside>

        {/* Overlay mobile */}
        {sidebarOpen && (
          <div 
            className="fixed inset-0 bg-black/50 z-30 lg:hidden"
            onClick={() => setSidebarOpen(false)}
          />
        )}

        {/* Contenu principal */}
        <main className="flex-1 ml-0 lg:ml-72 p-8 max-w-7xl mx-auto w-full">
          <button
            className="lg:hidden fixed top-20 left-4 z-50 bg-white/90 backdrop-blur-md p-3 rounded-2xl shadow-xl hover:shadow-2xl transition-all"
            onClick={() => setSidebarOpen(true)}
          >
            <FiMenu size={24} />
          </button>

          <Routes>
            <Route path="/" element={<DashboardHome stats={stats} recentActivity={recentActivity} />} />
            <Route path="/patients" element={<Patients />} />
            <Route path="/medecins" element={<Medecins />} />
            <Route path="/rdv" element={<Rdv />} />
            <Route path="/services" element={<Services />} />
            <Route path="/finances" element={<Finances />} />
            <Route path="/moderateurs" element={<Moderateurs />} />
            <Route path="*" element={<DashboardHome stats={stats} recentActivity={recentActivity} />} />
          </Routes>
        </main>
      </div>
    </div>
  );
};

// ✅ EXPORTS COMPLETS pour App.js
export const AdminLayout = AdminDashboard;
export const AdminLayoutWrapper = AdminDashboard;  // ✅ AJOUTÉ pour App.js
export { DashboardHome };
export default AdminDashboard;
