import React, { useEffect, useState } from 'react';
import { getData } from '../api/endpoint';

const MedecinDossiers = () => {
  const [dossiers, setDossiers] = useState([]);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchDossiers = async () => {
      try {
        setLoading(true);
        setError(null);
        // Utilisez '/api/dossiers' pour correspondre √† votre endpoint backend
        const data = await getData('/api/dossiers');
        setDossiers(data);
      } catch (error) {
        console.error('Erreur lors du chargement des dossiers:', error);
        
        // Gestion sp√©cifique de l'erreur 403
        if (error.response?.status === 403) {
          setError('Acc√®s r√©serv√© aux administrateurs');
        } else {
          setError('Erreur lors du chargement des dossiers');
        }
      } finally {
        setLoading(false);
      }
    };

    fetchDossiers();
  }, []);

  if (loading) {
    return (
      <div style={{ padding: '20px' }}>
        <p>Chargement des dossiers...</p>
      </div>
    );
  }

  return (
    <div style={{ padding: '20px' }}>
      <h2>ü©∫ Dossiers des patients</h2>

      {error ? (
        <div style={{
          backgroundColor: '#fee',
          color: '#c33',
          padding: '15px',
          borderRadius: '5px',
          borderLeft: '4px solid #c33',
          marginBottom: '20px'
        }}>
          ‚ö†Ô∏è {error}
        </div>
      ) : dossiers.length === 0 ? (
        <p>Aucun dossier trouv√©.</p>
      ) : (
        dossiers.map((dossier) => (
          <div 
            key={dossier.id || dossier._id} 
            style={{
              border: '1px solid #ddd',
              borderRadius: '8px',
              padding: '16px',
              marginBottom: '12px',
              backgroundColor: '#fafafa'
            }}
          >
            <p><strong>üë§ Patient :</strong> {dossier.patientName}</p>
            <p><strong>üìã Motif :</strong> {dossier.motif}</p>
            <p><strong>üî¨ Diagnostic :</strong> {dossier.diagnostic || '‚Äî'}</p>
          </div>
        ))
      )}
    </div>
  );
};

export default MedecinDossiers;
