import React, { useState, useEffect } from 'react';
import axios from 'axios';

const CreateDossier = () => {
  const [patients, setPatients] = useState([]);
  const [selectedPatientId, setSelectedPatientId] = useState('');
  const [resumeMedical, setResumeMedical] = useState('');
  const [groupeSanguin, setGroupeSanguin] = useState('');
  const [antecedents, setAntecedents] = useState([]);
  const [allergies, setAllergies] = useState([]);
  const [contactUrgence, setContactUrgence] = useState({
    nom: '',
    telephone: '',
    lien: ''
  });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const token = localStorage.getItem('token');

  const bloodGroups = ['A+', 'A-', 'B+', 'B-', 'AB+', 'AB-', 'O+', 'O-'];

  // üîπ R√©cup√©rer la liste des patients
  useEffect(() => {
    const fetchPatients = async () => {
      try {
        const res = await axios.get('http://localhost:5000/api/patients', {
          headers: { Authorization: `Bearer ${token}` }
        });
        setPatients(res.data);
      } catch (err) {
        console.error(err);
        setError('Erreur lors du chargement des patients');
      }
    };
    fetchPatients();
  }, [token]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');

    try {
      const res = await axios.post(
        'http://localhost:5000/api/dossiers',
        {
          patientId: selectedPatientId,
          resumeMedical,
          groupeSanguin,
          antecedents,
          allergies,
          contactUrgence
        },
        {
          headers: { Authorization: `Bearer ${token}` }
        }
      );

      alert('‚úÖ Dossier cr√©√© avec succ√®s');

      // reset formulaire
      setSelectedPatientId('');
      setResumeMedical('');
      setGroupeSanguin('');
      setAntecedents([]);
      setAllergies([]);
      setContactUrgence({ nom: '', telephone: '', lien: '' });

    } catch (err) {
      console.error('Erreur serveur:', err.response || err.message);

      if (err.response?.status === 401) setError('üîê Vous devez √™tre connect√©');
      else if (err.response?.status === 403) setError('‚õî Acc√®s refus√©');
      else setError('‚ùå Erreur serveur lors de la cr√©ation du dossier');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={{ padding: 20 }}>
      <h2>Cr√©er un dossier m√©dical</h2>
      {error && <p style={{ color: 'red' }}>{error}</p>}

      <form onSubmit={handleSubmit}>
        <label>Patient :</label>
        <select
          value={selectedPatientId}
          onChange={(e) => setSelectedPatientId(e.target.value)}
          required
        >
          <option value="">-- S√©lectionnez un patient --</option>
          {patients.map((patient) => (
            <option key={patient._id} value={patient._id}>
              {patient.fullName} ({patient.email})
            </option>
          ))}
        </select>

        <textarea
          placeholder="R√©sum√© m√©dical"
          value={resumeMedical}
          onChange={(e) => setResumeMedical(e.target.value)}
        />

        <label>Groupe sanguin :</label>
        <select
          value={groupeSanguin}
          onChange={(e) => setGroupeSanguin(e.target.value)}
          required
        >
          <option value="">-- S√©lectionnez le groupe --</option>
          {bloodGroups.map((g) => (
            <option key={g} value={g}>{g}</option>
          ))}
        </select>

        <input
          type="text"
          placeholder="Ant√©c√©dents (s√©par√©s par virgule)"
          onChange={(e) => setAntecedents(e.target.value.split(',').map(a => a.trim()))}
        />

        <input
          type="text"
          placeholder="Allergies (s√©par√©es par virgule)"
          onChange={(e) => setAllergies(e.target.value.split(',').map(a => a.trim()))}
        />

        <input
          type="text"
          placeholder="Contact urgence - Nom"
          value={contactUrgence.nom}
          onChange={(e) =>
            setContactUrgence({ ...contactUrgence, nom: e.target.value })
          }
        />

        <input
          type="text"
          placeholder="T√©l√©phone urgence"
          value={contactUrgence.telephone}
          onChange={(e) =>
            setContactUrgence({ ...contactUrgence, telephone: e.target.value })
          }
        />

        <input
          type="text"
          placeholder="Lien (parent, ami...)"
          value={contactUrgence.lien}
          onChange={(e) =>
            setContactUrgence({ ...contactUrgence, lien: e.target.value })
          }
        />

        <button type="submit" disabled={loading}>
          {loading ? 'Cr√©ation...' : 'Cr√©er le dossier'}
        </button>
      </form>
    </div>
  );
};

export default CreateDossier;
