import React, { useState, useEffect } from 'react';
import getData, { deleteData } from '../../api/endpoint';
import { FiFileText, FiSearch, FiTrash2, FiEye } from 'react-icons/fi';

const Journal = () => {
  const [journalEntries, setJournalEntries] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [showModal, setShowModal] = useState(false);
  const [selectedEntry, setSelectedEntry] = useState(null);
  const [filterType, setFilterType] = useState('ALL');
  const [filterRole, setFilterRole] = useState('ALL');

  useEffect(() => {
    loadJournal();
  }, []);

  const loadJournal = async () => {
    try {
      setLoading(true);
      const data = await getData('/journal');
      setJournalEntries(data);
    } catch (err) {
      console.error('Erreur lors du chargement:', err);
      setError('Impossible de charger le journal.');
    } finally {
      setLoading(false);
    }
  };

  const handleView = (entry) => {
    setSelectedEntry(entry);
    setShowModal(true);
  };

  const handleDelete = async (id) => {
    if (!window.confirm('Êtes-vous sûr de vouloir supprimer cette entrée ?')) return;
    try {
      await deleteData(`/journal/id?id=${id}`);
      alert('Entrée supprimée avec succès');
      loadJournal();
    } catch (err) {
      alert('Erreur lors de la suppression');
    }
  };

  const getResourceTypeColor = (type) => {
    switch (type) {
      case 'DOSSIER_MEDICAL': return 'bg-blue-100 text-blue-800';
      case 'CONSULTATION': return 'bg-green-100 text-green-800';
      case 'ORDONNANCE': return 'bg-purple-100 text-purple-800';
      case 'RENDEZ_VOUS': return 'bg-yellow-100 text-yellow-800';
      case 'UTILISATEUR': return 'bg-red-100 text-red-800';
      default: return 'bg-gray-100 text-gray-800';
    }
  };

  const getRoleColor = (role) => {
    switch (role) {
      case 'Admin': return 'bg-red-100 text-red-800';
      case 'Medecin': return 'bg-green-100 text-green-800';
      case 'Patient': return 'bg-blue-100 text-blue-800';
      case 'Pharmacien': return 'bg-orange-100 text-orange-800';
      case 'Assistant': return 'bg-purple-100 text-purple-800';
      default: return 'bg-gray-100 text-gray-800';
    }
  };

  const filteredEntries = journalEntries.filter(entry => {
    const matchesSearch = 
      entry.action?.toLowerCase().includes(searchTerm.toLowerCase()) ||
      entry.userId?.fullName?.toLowerCase().includes(searchTerm.toLowerCase()) ||
      entry.userId?.email?.toLowerCase().includes(searchTerm.toLowerCase());
    
    const matchesType = filterType === 'ALL' || entry.resourceType === filterType;
    const matchesRole = filterRole === 'ALL' || entry.role === filterRole;
    
    return matchesSearch && matchesType && matchesRole;
  });

  if (loading) {
    return <div className="flex items-center justify-center h-64"><div>Chargement...</div></div>;
  }

  return (
    <div>
      <div className="mb-8">
        <h2 className="text-3xl font-bold text-gray-900">Journal d'Activités</h2>
        <p className="text-gray-600 mt-2">Historique de toutes les actions effectuées dans le système</p>
      </div>

      {/* Filtres */}
      <div className="mb-6 grid grid-cols-1 md:grid-cols-4 gap-4">
        <div className="relative">
          <FiSearch className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400" size={20} />
          <input
            type="text"
            placeholder="Rechercher..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            className="w-full pl-10 pr-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500"
          />
        </div>
        <select
          value={filterType}
          onChange={(e) => setFilterType(e.target.value)}
          className="px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500"
        >
          <option value="ALL">Tous les types</option>
          <option value="DOSSIER_MEDICAL">Dossier médical</option>
          <option value="CONSULTATION">Consultation</option>
          <option value="ORDONNANCE">Ordonnance</option>
          <option value="RENDEZ_VOUS">Rendez-vous</option>
          <option value="UTILISATEUR">Utilisateur</option>
        </select>
        <select
          value={filterRole}
          onChange={(e) => setFilterRole(e.target.value)}
          className="px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500"
        >
          <option value="ALL">Tous les rôles</option>
          <option value="Admin">Admin</option>
          <option value="Medecin">Médecin</option>
          <option value="Patient">Patient</option>
          <option value="Pharmacien">Pharmacien</option>
          <option value="Assistant">Assistant</option>
        </select>
        <div className="text-sm text-gray-600 flex items-center">
          Total: {filteredEntries.length} entrées
        </div>
      </div>

      {/* Tableau */}
      <div className="bg-white rounded-2xl shadow-lg border border-gray-100 overflow-hidden">
        <table className="w-full">
          <thead className="bg-gray-50">
            <tr>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">Date/Heure</th>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">Utilisateur</th>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">Rôle</th>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">Action</th>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">Type</th>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">Actions</th>
            </tr>
          </thead>
          <tbody className="divide-y divide-gray-200">
            {filteredEntries.map((entry) => (
              <tr key={entry._id} className="hover:bg-gray-50">
                <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                  {new Date(entry.createdAt).toLocaleString('fr-FR')}
                </td>
                <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                  {entry.userId?.fullName || entry.userId?.email || 'N/A'}
                </td>
                <td className="px-6 py-4 whitespace-nowrap">
                  <span className={`px-2 py-1 rounded-full text-xs font-medium ${getRoleColor(entry.role)}`}>
                    {entry.role}
                  </span>
                </td>
                <td className="px-6 py-4 text-sm text-gray-900">{entry.action || 'N/A'}</td>
                <td className="px-6 py-4 whitespace-nowrap">
                  <span className={`px-2 py-1 rounded-full text-xs font-medium ${getResourceTypeColor(entry.resourceType)}`}>
                    {entry.resourceType}
                  </span>
                </td>
                <td className="px-6 py-4 whitespace-nowrap text-sm font-medium">
                  <div className="flex gap-2">
                    <button onClick={() => handleView(entry)} className="text-blue-600 hover:text-blue-900">
                      <FiEye size={18} />
                    </button>
                    <button onClick={() => handleDelete(entry._id)} className="text-red-600 hover:text-red-900">
                      <FiTrash2 size={18} />
                    </button>
                  </div>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      {/* Modal de détails */}
      {showModal && selectedEntry && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white rounded-2xl p-8 max-w-2xl w-full mx-4 max-h-[90vh] overflow-y-auto">
            <h3 className="text-2xl font-bold mb-4">Détails de l'Entrée</h3>
            <div className="space-y-4">
              <div><strong>Date/Heure:</strong> {new Date(selectedEntry.createdAt).toLocaleString('fr-FR')}</div>
              <div><strong>Utilisateur:</strong> {selectedEntry.userId?.fullName || selectedEntry.userId?.email}</div>
              <div><strong>Rôle:</strong> {selectedEntry.role}</div>
              <div><strong>Action:</strong> {selectedEntry.action}</div>
              <div><strong>Type de ressource:</strong> {selectedEntry.resourceType}</div>
              {selectedEntry.resourceId && (
                <div><strong>ID Ressource:</strong> {selectedEntry.resourceId}</div>
              )}
              {selectedEntry.ipAddress && (
                <div><strong>Adresse IP:</strong> {selectedEntry.ipAddress}</div>
              )}
              {selectedEntry.details && Object.keys(selectedEntry.details).length > 0 && (
                <div>
                  <strong>Détails:</strong>
                  <pre className="bg-gray-50 p-4 rounded-lg mt-2 text-sm overflow-auto">
                    {JSON.stringify(selectedEntry.details, null, 2)}
                  </pre>
                </div>
              )}
            </div>
            <button onClick={() => setShowModal(false)} className="mt-6 bg-gray-500 text-white px-6 py-2 rounded-lg">Fermer</button>
          </div>
        </div>
      )}
    </div>
  );
};

export default Journal;
