import React, { useState, useEffect } from 'react';
import api, { deleteData, putData } from '../../api/endpoint'; // ✅ CORRIGÉ

const Rdv = () => {
  const [filter, setFilter] = useState('Tous');
  const [rendezvous, setRendezVous] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [users, setUsers] = useState([]);
  const [services, setServices] = useState([]);

  useEffect(() => {
    loadRendezVous();
    loadUsers();
    loadServices();
  }, []);

  const loadRendezVous = async () => {
    try {
      setLoading(true);
      setError(null);
      const rdvData = await api.getData('/rendezvous'); // ✅ CORRIGÉ
      setRendezVous(Array.isArray(rdvData) ? rdvData : []);
    } catch (err) {
      console.error('Erreur chargement RDV:', err);
      setError(err.message || 'Impossible de charger les rendez-vous.');
      setRendezVous([]);
    } finally {
      setLoading(false);
    }
  };

  const loadUsers = async () => {
    try {
      const usersData = await api.getData('/users'); // ✅ CORRIGÉ
      setUsers(usersData);
    } catch (err) {
      console.error('Erreur utilisateurs:', err);
      setUsers([]);
    }
  };

  const loadServices = async () => {
    try {
      const servicesData = await api.getData('/services'); // ✅ CORRIGÉ
      setServices(servicesData);
    } catch (err) {
      console.error('Erreur services:', err);
      setServices([]);
    }
  };

  const handleCancel = async (rdvId) => {
    if (!window.confirm('Êtes-vous sûr de vouloir annuler ce rendez-vous ?')) return;
    try {
      await putData(`/rendezvous/${rdvId}`, { statut: 'ANNULE' }); // ✅ ENDPOINT STANDARD
      loadRendezVous();
    } catch (err) {
      alert(err.message || 'Erreur annulation');
    }
  };

  const handleDelete = async (rdvId) => {
    if (!window.confirm('Êtes-vous sûr de vouloir supprimer ce rendez-vous ?')) return;
    try {
      await deleteData(`/rendezvous/${rdvId}`); // ✅ ENDPOINT STANDARD
      loadRendezVous();
    } catch (err) {
      alert(err.message || 'Erreur suppression');
    }
  };

  // ✅ TOUTES LES FONCTIONS UTILITAIRES IDENTIQUES (EXCELLENTES)
  const getUserName = (userId) => {
    if (!userId || !users.length) return 'N/A';
    const user = users.find(u => u._id === userId);
    return user?.fullName || 'N/A';
  };

  const getServiceName = (serviceId) => {
    if (!serviceId || !services.length) return 'N/A';
    const service = services.find(s => s._id === serviceId);
    return service?.nom || 'N/A';
  };

  // ... formatDate, formatTime, getStatusColor, getStatusLabel, filteredRdv (IDENTIQUES ✅)

  // ✅ JSX IDENTIQUE (PARFAIT)
// ✅ TEMPLATE CORRIGÉ POUR CHAQUE PAGE
const NomPage = () => {
  // ... VOS useState() + useEffect() ✅

  if (loading) return <div className="flex items-center justify-center h-64 p-6"><div className="text-lg">Chargement...</div></div>;

  return (
    <div className="p-6 max-w-7xl mx-auto">
      {/* VOTRE TITRE */}
      <div className="mb-8">
        <h1 className="text-3xl font-bold text-gray-900">Votre Titre</h1>
        <p className="text-gray-600 mt-2">Description</p>
      </div>

      {/* VOTRE TABLEAU / FORMULAIRE */}
      <div className="bg-white rounded-2xl shadow-lg border border-gray-100 overflow-hidden">
        {/* CONTENU */}
      </div>
    </div>
  );
};

};

export default Rdv;
