import React, { useEffect, useState } from "react";
import { getData } from "../../api/endpoint";

const MyDossiers = () => {
  const [dossiers, setDossiers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  useEffect(() => {
    const fetchDossiers = async () => {
      try {
        const data = await getData("/patient/mes-dossiers");
        setDossiers(data);
      } catch (err) {
        setError(err.message || "Impossible de récupérer vos dossiers.");
      } finally {
        setLoading(false);
      }
    };

    fetchDossiers();
  }, []);

  if (loading) return <div>Chargement des dossiers...</div>;
  if (error) return <div className="text-red-500">{error}</div>;
  if (!dossiers.length) return <div>Aucun dossier trouvé.</div>;

  return (
    <div className="overflow-x-auto">
      <table className="min-w-full bg-white border border-gray-300 rounded">
        <thead className="bg-blue-600 text-white">
          <tr>
            <th className="px-4 py-2 text-left">Résumé Médical</th>
            <th className="px-4 py-2 text-left">Groupe Sanguin</th>
            <th className="px-4 py-2 text-left">Antécédents</th>
            <th className="px-4 py-2 text-left">Allergies</th>
            <th className="px-4 py-2 text-left">Contact d'Urgence</th>
            <th className="px-4 py-2 text-left">Statut</th>
            <th className="px-4 py-2 text-left">Créé le</th>
          </tr>
        </thead>
        <tbody>
          {dossiers.map((dossier) => (
            <tr key={dossier._id} className="border-b hover:bg-gray-50">
              <td className="px-4 py-2">{dossier.resumeMedical}</td>
              <td className="px-4 py-2">{dossier.groupeSanguin}</td>
              <td className="px-4 py-2">
                {dossier.antecedents.map(a => a.libelle).join(", ")}
              </td>
              <td className="px-4 py-2">
                {dossier.allergies.map(a => a.libelle).join(", ")}
              </td>
              <td className="px-4 py-2">
                {dossier.contactUrgence?.nom} ({dossier.contactUrgence?.telephone}) – {dossier.contactUrgence?.lien}
              </td>
              <td className="px-4 py-2">{dossier.statut}</td>
              <td className="px-4 py-2">{new Date(dossier.createdAt).toLocaleDateString()}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default MyDossiers;
