import React, { useState, useEffect } from 'react';
import { Link, Routes, Route, useLocation } from 'react-router-dom';

const MedecinDashboard = () => {
  const location = useLocation();
  const [profile, setProfile] = useState(null);
  const [patients, setPatients] = useState([]);
  const [rendezVous, setRendezVous] = useState([]);
  const [ordonnances, setOrdonnances] = useState([]);
  const [notifications, setNotifications] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    setTimeout(() => {
      setProfile({
        nom: 'NDIAYE', prenom: 'Moustapha', email: 'moustapha.ndiaye@govsanitaire.sn',
        telephone: '+221 78 987 6543', specialite: 'Cardiologue',
        numeroOrdre: 'SN-MED-2024-001', etablissement: 'CHU de Fann',
        statut: 'ValidÃ©', photo: 'ğŸ‘¨â€âš•ï¸'
      });
      
      setPatients([
        { id: '1', nom: 'DIOP', prenom: 'Aminata', age: 35, sexe: 'F', derniereConsult: '08/02/2026' },
        { id: '2', nom: 'SOW', prenom: 'Fatou', age: 42, sexe: 'F', derniereConsult: '07/02/2026' },
        { id: '3', nom: 'BA', prenom: 'Ibrahima', age: 28, sexe: 'M', derniereConsult: '06/02/2026' }
      ]);
      
      setRendezVous([
        { id: '1', patient: 'Aminata DIOP', date: '24/02/2026 10:00', statut: 'ConfirmÃ©' },
        { id: '2', patient: 'Fatou SOW', date: '24/02/2026 14:00', statut: 'ConfirmÃ©' },
        { id: '3', patient: 'Nouveau', date: '24/02/2026 16:00', statut: 'En attente' }
      ]);
      
      setOrdonnances([
        { id: '1', patient: 'Aminata DIOP', medicament: 'ParacÃ©tamol 500mg', statut: 'Active' }
      ]);
      
      setNotifications([
        { id: '1', message: 'Nouveau patient enregistrÃ©', type: 'info', date: '08/02/2026' }
      ]);
      
      setLoading(false);
    }, 1000);
  }, []);

  if (loading) return <LoadingScreen />;

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-50 to-blue-50">
      {/* Notifications */}
      <NotificationsMedecin notifications={notifications} />
      
      {/* Sidebar MÃ©decin */}
      <aside className="hidden lg:block lg:w-64 lg:h-screen lg:fixed lg:inset-y-0 lg:z-40 bg-white shadow-lg border-r border-gray-200">
        <ProfileMedecin profile={profile} />
        <NavigationMedecin activePath={location.pathname} />
      </aside>

      {/* Contenu principal */}
      <main className="lg:ml-64 p-6 lg:p-8 pb-20">
        <Routes>
          <Route path="/" element={<DashboardMedecin profile={profile} patients={patients} rdv={rendezVous} ordonnances={ordonnances} />} />
          <Route path="/patients" element={<PatientsPage patients={patients} />} />
          <Route path="/patients/:id" element={<PatientDetail patients={patients} />} />
          <Route path="/dossier/:patientId" element={<DossierPage patients={patients} />} />
          <Route path="/ordonnances" element={<OrdonnancesPage ordonnances={ordonnances} />} />
          <Route path="*" element={<NotFoundPage />} />
        </Routes>
      </main>
    </div>
  );
};

// ğŸ”¥ PROFIL MÃ‰DECIN OFFICIEL
const ProfileMedecin = ({ profile }) => (
  <div className="p-6 border-b border-gray-200">
    <div className="w-20 h-20 bg-blue-600 text-white rounded-full flex items-center justify-center mx-auto mb-4 font-bold text-xl shadow-md">
      {profile?.photo}
    </div>
    <h2 className="text-xl font-bold text-gray-900 text-center mb-2">
      Dr. {profile?.prenom} {profile?.nom}
    </h2>
    <div className="text-center text-sm space-y-1 bg-blue-50 p-3 rounded-lg border border-blue-200">
      <p className="font-semibold text-blue-800">{profile?.specialite}</p>
      <p className="text-xs text-gray-600">{profile?.etablissement}</p>
      <p className="text-xs font-mono bg-white px-2 py-1 rounded text-blue-700">
        #{profile?.numeroOrdre}
      </p>
      <span className={`px-3 py-1 rounded-full text-xs font-bold ${
        profile?.statut === 'ValidÃ©' ? 'bg-green-100 text-green-800' : 'bg-yellow-100 text-yellow-800'
      }`}>
        {profile?.statut}
      </span>
    </div>
  </div>
);

// ğŸ”¥ NAVIGATION MÃ‰DECIN
const NavigationMedecin = ({ activePath }) => (
  <nav className="p-4 space-y-2 mt-4">
    <NavLink href="/medecin" icon="ğŸ“Š" label="Dashboard" active={activePath === '/medecin'} color="blue" />
    <NavLink href="/medecin/patients" icon="ğŸ‘¥" label="Patients" active={activePath === '/medecin/patients'} color="green" />
    <NavLink href="/medecin/ordonnances" icon="ğŸ’Š" label="Ordonnances" active={activePath === '/medecin/ordonnances'} color="orange" />
  </nav>
);

// ğŸ”¥ DASHBOARD MÃ‰DECIN PRINCIPAL
const DashboardMedecin = ({ profile, patients, rdv, ordonnances }) => {
  const rdvJour = rdv.filter(r => r.date.startsWith('24/02/2026'));
  const rdvAvenir = rdv.filter(r => !r.date.startsWith('24/02/2026'));

  return (
    <div className="max-w-7xl mx-auto space-y-8">
      {/* Header */}
      <div className="bg-white p-8 rounded-2xl shadow-lg border border-gray-200">
        <h1 className="text-4xl font-bold text-gray-900 mb-2">
          Bonjour Dr. {profile?.prenom} {profile?.nom}
        </h1>
        <p className="text-xl text-blue-800">{profile?.specialite} - {profile?.etablissement}</p>
      </div>

      {/* Stats */}
      <div className="grid md:grid-cols-2 lg:grid-cols-4 gap-6">
        <StatCard icon="ğŸ“…" title={rdv.length} label="RDV Ã  venir" color="blue" />
        <StatCard icon="ğŸ”¥" title={rdvJour.length} label="Aujourd'hui" color="red" />
        <StatCard icon="ğŸ‘¥" title={patients.length} label="Patients suivis" color="green" />
        <StatCard icon="ğŸ’Š" title={ordonnances.length} label="Ordonnances" color="orange" />
      </div>

      {/* RDV + Patients */}
      <div className="grid lg:grid-cols-2 gap-8">
        <RDVAvenir rdv={rdvAvenir.slice(0, 3)} />
        <PatientsRecents patients={patients.slice(0, 4)} />
      </div>
    </div>
  );
};

// ğŸ”¥ COMPOSANTS MÃ‰DECIN
const StatCard = ({ icon, title, label, color }) => (
  <div className={`bg-white p-6 rounded-2xl shadow-md border-l-4 border-${color}-500 hover:shadow-lg transition-all group`}>
    <div className="text-3xl mb-4 group-hover:scale-110 transition-transform">{icon}</div>
    <p className="text-3xl font-bold text-gray-900 mb-2">{title}</p>
    <p className="text-lg font-semibold text-gray-600">{label}</p>
  </div>
);

const RDVAvenir = ({ rdv }) => (
  <div className="bg-white p-8 rounded-2xl shadow-lg border border-gray-200">
    <h3 className="text-2xl font-bold text-gray-900 mb-6">ğŸ“… Rendez-vous Ã  venir</h3>
    <div className="space-y-4">
      {rdv.map(item => (
        <div key={item.id} className="p-4 bg-blue-50 rounded-xl border-l-4 border-blue-400 hover:shadow-md transition-all">
          <div className="flex justify-between items-start">
            <div>
              <h4 className="font-bold text-lg">{item.patient}</h4>
              <p className="text-sm text-gray-600">{item.date}</p>
            </div>
            <span className={`px-3 py-1 rounded-lg text-sm font-bold ${
              item.statut === 'ConfirmÃ©' ? 'bg-green-500 text-white' : 'bg-yellow-500 text-white'
            }`}>
              {item.statut}
            </span>
          </div>
        </div>
      ))}
    </div>
  </div>
);

const PatientsRecents = ({ patients }) => (
  <div className="bg-white p-8 rounded-2xl shadow-lg border border-gray-200">
    <h3 className="text-2xl font-bold text-gray-900 mb-6">ğŸ‘¥ Patients rÃ©cents</h3>
    <div className="space-y-3">
      {patients.map(patient => (
        <Link key={patient.id} to={`/medecin/patients/${patient.id}`} className="flex items-center p-4 bg-gray-50 rounded-xl hover:bg-gray-100 hover:shadow-md transition-all group">
          <div className="w-12 h-12 bg-blue-500 text-white rounded-full flex items-center justify-center font-bold mr-4 flex-shrink-0">
            {patient.sexe === 'M' ? 'ğŸ‘¨' : 'ğŸ‘©'}
          </div>
          <div className="flex-1 min-w-0">
            <h4 className="font-bold text-lg truncate">{patient.prenom} {patient.nom}</h4>
            <p className="text-sm text-gray-600">{patient.age} ans â€¢ DerniÃ¨re consult: {patient.derniereConsult}</p>
          </div>
        </Link>
      ))}
    </div>
  </div>
