import React, { useState, useEffect } from 'react';
import { getData } from '../api/endpoint';

const DossiersList = () => {
  const [dossiers, setDossiers] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchDossiers = async () => {
      try {
        setLoading(true);
        const data = await getData('/dossier/mes-dossiers');
        setDossiers(Array.isArray(data) ? data : []);
      } catch (err) {
        console.error('Dossiers error:', err);
        setDossiers([]);
      } finally {
        setLoading(false);
      }
    };

    fetchDossiers();
    const interval = setInterval(fetchDossiers, 30000);
    return () => clearInterval(interval);
  }, []);

  if (loading) return <div style={{ padding: '4rem', textAlign: 'center' }}>‚è≥ Chargement dossiers...</div>;

  return (
    <div>
      <div style={{ 
        padding: '2rem 3rem 1.5rem', 
        background: 'linear-gradient(135deg, #f093fb 0%, #f5576c 100%)',
        color: 'white'
      }}>
        <h2 style={{ margin: 0, fontSize: '2rem' }}>üìÅ Dossiers M√©dicaux</h2>
        <p style={{ margin: '0.5rem 0 0', opacity: 0.9 }}>
          {dossiers.length} dossier(s) ‚Ä¢ Temps r√©el
        </p>
      </div>
      
      {dossiers.length === 0 ? (
        <div style={{ padding: '4rem', textAlign: 'center' }}>
          <div style={{ fontSize: '4rem', marginBottom: '1rem' }}>üìã</div>
          <h3>Aucun dossier</h3>
        </div>
      ) : (
        <table style={{ width: '100%' }}>
          {/* Table identique √† votre version pr√©c√©dente */}
        </table>
      )}
    </div>
  );
};

export default DossiersList;
