// src/api/endpoint.js - LIGNE 1 UNIQUEMENT
const BASE_URL = process.env.REACT_APP_API_BASE_URL || 'http://localhost:5000/api';

// =================== TOKEN MANAGEMENT ===================
export const TOKEN_KEY = 'token';

export const getAuthToken = () => localStorage.getItem(TOKEN_KEY);
export const setAuthToken = (token) => localStorage.setItem(TOKEN_KEY, token);
export const removeAuthToken = () => localStorage.removeItem(TOKEN_KEY);

export const decodeToken = (token) => {
  if (!token) return null;
  try {
    const base64 = token.split('.')[1];
    const json = decodeURIComponent(
      atob(base64)
        .split('')
        .map(c => '%' + ('00' + c.charCodeAt(0).toString(16)).slice(-2))
        .join('')
    );
    return JSON.parse(json);
  } catch {
    return null;
  }
};

export const isTokenValid = (token) => {
  if (!token) return false;
  try {
    const decoded = decodeToken(token);
    if (!decoded || !decoded.exp) return false;
    return decoded.exp * 1000 > Date.now();
  } catch {
    return false;
  }
};

// =================== URL BUILDER CORRIG√â ===================
const buildUrl = (endpoint) => {
  // Nettoie l'endpoint s'il contient d√©j√† une URL compl√®te
  let cleanEndpoint = endpoint;
  
  // Supprime tout pr√©fixe http://localhost:5000/ ou similaire
  if (endpoint.match(/^https?:\/\/localhost:\d+/)) {
    cleanEndpoint = endpoint.replace(/^https?:\/\/[^\/]+/, '');
  }
  
  // Supprime /api/ en double
  cleanEndpoint = cleanEndpoint.replace(/^\/api/, '');
  
  // Construction finale de l'URL
  const separator = cleanEndpoint.startsWith('/') ? '' : '/';
  return `${BASE_URL}${separator}${cleanEndpoint}`;
};

// =================== HEADERS DYNAMIQUES ===================
const getHeaders = (extraHeaders = {}) => {
  const token = getAuthToken();
  const headers = { 'Content-Type': 'application/json', ...extraHeaders };
  
  if (token && isTokenValid(token)) {
    headers.Authorization = `Bearer ${token}`;
  }
  
  return headers;
};

// =================== ERROR HANDLER ROBUSTE ===================
const handleApiError = async (response) => {
  // 401 : Redirection login
  if (response.status === 401) {
    removeAuthToken();
    if (typeof window !== 'undefined' && window.location.pathname !== '/login') {
      window.location.href = '/login';
    }
    throw new Error('Session expir√©e. Veuillez vous reconnecter.');
  }

  let errorData;
  try {
    errorData = await response.json();
  } catch {
    errorData = { message: `Erreur HTTP ${response.status}` };
  }

  console.error('üö® D√©tails de l\'erreur serveur:', errorData);

  let errorMessage = errorData.message || `Erreur HTTP ${response.status}`;

  // MongoDB duplicate key
  if (errorData.errorName === 'MongoServerError' && errorData.code === 11000) {
    errorMessage = 'Donn√©es en doublon d√©tect√©es. V√©rifiez les informations saisies.';
  }

  // Validation errors
  if (errorData.validationErrors) {
    const validationMessages = Object.values(errorData.validationErrors).flat().join(', ');
    errorMessage += ` - Validation: ${validationMessages}`;
  }

  throw new Error(errorMessage);
};

// =================== API METHODS OPTIMIS√âS ===================
export const getData = async (endpoint) => {
  try {
    const url = buildUrl(endpoint);
    console.log('üì° GET ‚Üí', url); // Debug
    
    const response = await fetch(url, {
      method: 'GET',
      headers: getHeaders(),
    });

    if (!response.ok) {
      throw await handleApiError(response);
    }

    return await response.json();
  } catch (error) {
    console.error('‚ùå Error fetching data:', error);
    throw error;
  }
};

export const postData = async (endpoint, payload) => {
  try {
    const url = buildUrl(endpoint);
    console.log('üì§ POST ‚Üí', url);
    
    const response = await fetch(url, {
      method: 'POST',
      headers: getHeaders(),
      body: JSON.stringify(payload),
    });

    if (!response.ok) {
      throw await handleApiError(response);
    }

    return await response.json();
  } catch (error) {
    console.error('‚ùå Error posting data:', error);
    throw error;
  }
};

export const putData = async (endpoint, payload) => {
  try {
    const url = buildUrl(endpoint);
    console.log('üîÑ PUT ‚Üí', url);
    
    const response = await fetch(url, {
      method: 'PUT',
      headers: getHeaders(),
      body: JSON.stringify(payload),
    });

    if (!response.ok) {
      throw await handleApiError(response);
    }

    return await response.json();
  } catch (error) {
    console.error('‚ùå Error updating data:', error);
    throw error;
  }
};

export const deleteData = async (endpoint) => {
  try {
    const url = buildUrl(endpoint);
    console.log('üóëÔ∏è DELETE ‚Üí', url);
    
    const response = await fetch(url, {
      method: 'DELETE',
      headers: getHeaders(),
    });

    if (!response.ok) {
      throw await handleApiError(response);
    }

    return await response.json();
  } catch (error) {
    console.error('‚ùå Error deleting data:', error);
    throw error;
  }
};

// =================== EXPORTS ===================
export default {
  getData,
  postData,
  putData,
  deleteData,
  getAuthToken,
  setAuthToken,
  removeAuthToken,
  isTokenValid,
  decodeToken,
};
