import React, { useState, useEffect } from 'react';
import api, { deleteData } from '../../api/endpoint'; // ✅ CORRIGÉ

const Services = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const [services, setServices] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [medecins, setMedecins] = useState([]);
  const [rendezvous, setRendezVous] = useState([]);

  useEffect(() => {
    loadServices();
    loadMedecins();
    loadRendezVous();
  }, []);

  const loadServices = async () => {
    try {
      setLoading(true);
      setError(null);
      const servicesData = await api.getData('/services'); // ✅ CORRIGÉ
      setServices(Array.isArray(servicesData) ? servicesData : []);
    } catch (err) {
      console.error('Erreur chargement services:', err);
      setError(err.message || 'Impossible de charger les services.');
      setServices([]);
    } finally {
      setLoading(false);
    }
  };

  const loadMedecins = async () => {
    try {
      const users = await api.getData('/users'); // ✅ CORRIGÉ
      const medecinsList = users.filter(user => user.role === 'Medecin');
      setMedecins(medecinsList);
    } catch (err) {
      console.error('Erreur médecins:', err);
      setMedecins([]);
    }
  };

  const loadRendezVous = async () => {
    try {
      const rdvData = await api.getData('/rendezvous'); // ✅ CORRIGÉ
      setRendezVous(rdvData);
    } catch (err) {
      console.error('Erreur RDV:', err);
      setRendezVous([]);
    }
  };

  const handleDelete = async (serviceId) => {
    if (!window.confirm('Êtes-vous sûr de vouloir supprimer ce service ?')) return;
    try {
      await deleteData(`/services/${serviceId}`); // ✅ ENDPOINT STANDARD
      loadServices();
    } catch (err) {
      alert(err.message || 'Erreur suppression');
    }
  };

  // ✅ FONCTIONS getMedecinsCount, getRdvCount IDENTIQUES (EXCELLENTES)

  // ✅ JSX IDENTIQUE (PARFAIT)
// ✅ TEMPLATE CORRIGÉ POUR CHAQUE PAGE
const NomPage = () => {
  // ... VOS useState() + useEffect() ✅

  if (loading) return <div className="flex items-center justify-center h-64 p-6"><div className="text-lg">Chargement...</div></div>;

  return (
    <div className="p-6 max-w-7xl mx-auto">
      {/* VOTRE TITRE */}
      <div className="mb-8">
        <h1 className="text-3xl font-bold text-gray-900">Votre Titre</h1>
        <p className="text-gray-600 mt-2">Description</p>
      </div>

      {/* VOTRE TABLEAU / FORMULAIRE */}
      <div className="bg-white rounded-2xl shadow-lg border border-gray-100 overflow-hidden">
        {/* CONTENU */}
      </div>
    </div>
  );
};

};

export default Services;
