import React, { useEffect, useState } from "react";
import * as api from "../api/endpoint";
import "./PatientDashboard.css";

const PatientDashboard = () => {
  const [patient, setPatient] = useState(null);
  const [rendezvous, setRendezvous] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  useEffect(() => {
    loadPatientData();
    loadRendezVous();
  }, []);

  const loadPatientData = async () => {
    try {
      // Récupérer l'utilisateur connecté
      const token = api.getAuthToken();
      const decoded = api.decodeToken(token);
      const data = await api.getData(`/user/${decoded.userId}`);
      setPatient(data);
    } catch (err) {
      console.error(err);
      setError(err.message || "Erreur lors du chargement des données");
    }
  };

  const loadRendezVous = async () => {
    try {
      const token = api.getAuthToken();
      const decoded = api.decodeToken(token);
      const data = await api.getData(`/rendezvous?patientId=${decoded.userId}`);
      setRendezvous(data);
      setLoading(false);
    } catch (err) {
      console.error(err);
      setError(err.message || "Erreur lors du chargement des rendez-vous");
      setLoading(false);
    }
  };

  const handleCancel = async (id) => {
    if (!window.confirm("Voulez-vous vraiment annuler ce rendez-vous ?")) return;
    try {
      await api.deleteData(`/rendezvous/${id}`);
      setRendezvous(rendezvous.filter(r => r._id !== id));
      alert("Rendez-vous annulé !");
    } catch (err) {
      console.error(err);
      alert("Erreur lors de l'annulation : " + err.message);
    }
  };

  if (loading) return <p>Chargement...</p>;
  if (error) return <p style={{ color: "red" }}>{error}</p>;

  return (
    <div className="patient-dashboard">
      <h2>Bienvenue, {patient?.fullName}</h2>

      <section className="patient-info">
        <h3>Mes informations</h3>
        <p><strong>Email:</strong> {patient?.email}</p>
        <p><strong>Date de naissance:</strong> {patient?.date_of_birth}</p>
        <p><strong>Sexe:</strong> {patient?.sexe}</p>
        <p><strong>Statut:</strong> {patient?.statut}</p>
      </section>

      <section className="patient-rendezvous">
        <h3>Mes rendez-vous</h3>
        {rendezvous.length === 0 ? (
          <p>Aucun rendez-vous prévu.</p>
        ) : (
          <table>
            <thead>
              <tr>
                <th>Date</th>
                <th>Heure</th>
                <th>Service</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {rendezvous.map(rv => (
                <tr key={rv._id}>
                  <td>{new Date(rv.date).toLocaleDateString()}</td>
                  <td>{rv.heure}</td>
                  <td>{rv.service?.name || rv.service}</td>
                  <td>
                    <button onClick={() => handleCancel(rv._id)}>Annuler</button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </section>
    </div>
  );
};

export default PatientDashboard;
