import React, { useState, useEffect } from 'react';
import { postData, getData } from '../api/endpoint';

const CreateDossierModal = ({ onClose, onSuccess }) => {
  const [formData, setFormData] = useState({
    patientId: '', // ‚úÖ AJOUT√â - Backend exige CETTE cl√©
    patientFullName: '',
    patientEmail: '',
    dateOfBirth: '',
    sexe: 'M',
    groupeSanguin: '',
    resumeMedical: '',
    allergies: ''
  });
  const [patients, setPatients] = useState([]);
  const [loading, setLoading] = useState(false);
  const [showPatientsList, setShowPatientsList] = useState(false);

  // ‚úÖ CHARGEMENT PATIENTS EXISTANTS
  useEffect(() => {
    loadPatients();
  }, []);

  const loadPatients = async () => {
    try {
      const data = await getData('/patients');
      setPatients(Array.isArray(data) ? data : []);
    } catch (err) {
      console.error('Patients:', err);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    // ‚úÖ VALIDATION patientId OBLIGATOIRE
    if (!formData.patientId) {
      alert('‚ùå S√©lectionnez un patient existant ou cr√©ez-le d\'abord');
      return;
    }

    setLoading(true);
    try {
      console.log('üì§ Envoi:', formData);
      await postData('/dossier', formData);
      alert('‚úÖ Dossier cr√©√© avec succ√®s !');
      onSuccess();
    } catch (err) {
      alert('‚ùå Erreur: ' + err.message);
    } finally {
      setLoading(false);
    }
  };

  // ‚úÖ S√âLECTION PATIENT EXISTANT
  const selectPatient = (patient) => {
    setFormData({
      patientId: patient._id, // ‚úÖ ID MongoDB requis
      patientFullName: patient.fullName || patient.nom || '',
      patientEmail: patient.email || '',
      dateOfBirth: patient.date_of_birth || '',
      sexe: patient.sexe || 'M',
      groupeSanguin: '',
      resumeMedical: '',
      allergies: ''
    });
    setShowPatientsList(false);
  };

  const handleInputChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  return (
    <div style={{
      position: 'fixed', top: 0, left: 0, right: 0, bottom: 0,
      background: 'rgba(0,0,0,0.6)', display: 'flex', alignItems: 'center', justifyContent: 'center',
      zIndex: 10000
    }}>
      <div style={{
        background: 'white', padding: '2.5rem', borderRadius: '16px', maxWidth: '700px',
        width: '90%', maxHeight: '90vh', overflowY: 'auto', boxShadow: '0 20px 60px rgba(0,0,0,0.3)'
      }}>
        <div style={{ display: 'flex', justifyContent: 'space-between', marginBottom: '2rem' }}>
          <h2 style={{ margin: 0, color: '#1976d2', fontSize: '1.8rem' }}>‚ûï Nouveau Dossier</h2>
          <button onClick={onClose} style={{
            background: 'none', border: 'none', fontSize: '2rem', cursor: 'pointer', color: '#666'
          }}>√ó</button>
        </div>

        <form onSubmit={handleSubmit}>
          {/* ‚úÖ BOUTON S√âLECTION PATIENT (OBLIGATOIRE) */}
          <div style={{ marginBottom: '1.5rem' }}>
            <button 
              type="button"
              onClick={() => setShowPatientsList(!showPatientsList)}
              style={{
                width: '100%', padding: '1rem', background: '#2196f3', color: 'white',
                border: 'none', borderRadius: '8px', fontSize: '1.1rem', cursor: 'pointer',
                display: 'flex', alignItems: 'center', gap: '1rem'
              }}
            >
              üë§ {formData.patientId ? 'Patient s√©lectionn√©' : 'S√©lectionner un patient existant *'}
            </button>
          </div>

          {/* ‚úÖ LISTE PATIENTS */}
          {showPatientsList && (
            <div style={{ 
              maxHeight: '200px', overflowY: 'auto', 
              border: '2px solid #e0e0e0', borderRadius: '8px', marginBottom: '1.5rem',
              background: '#f8f9fa'
            }}>
              {patients.length === 0 ? (
                <div style={{ padding: '1rem', textAlign: 'center', color: '#666' }}>
                  Aucun patient. Cr√©ez-en un d'abord.
                </div>
              ) : (
                patients.map(patient => (
                  <div 
                    key={patient._id}
                    onClick={() => selectPatient(patient)}
                    style={{
                      padding: '1rem', cursor: 'pointer', borderBottom: '1px solid #eee',
                      display: 'flex', alignItems: 'center', gap: '1rem',
                      background: formData.patientId === patient._id ? '#e3f2fd' : 'transparent'
                    }}
                  >
                    <div style={{ fontWeight: 'bold', fontSize: '1.1rem' }}>
                      {patient.fullName || patient.nom}
                    </div>
                    <div style={{ color: '#666', fontSize: '0.9rem' }}>
                      {patient.email}
                    </div>
                  </div>
                ))
              )}
            </div>
          )}

          {/* ‚úÖ AFFICHAGE PATIENT S√âLECTIONN√â */}
          {formData.patientId && (
            <div style={{ 
              padding: '1rem', background: '#e8f5e8', borderRadius: '8px', marginBottom: '1.5rem',
              borderLeft: '4px solid #4caf50'
            }}>
              ‚úÖ Patient: <strong>{formData.patientFullName}</strong> 
              ({formData.patientEmail})
            </div>
          )}

          {/* ‚úÖ CHAMPS M√âDICAUX (optionnels) */}
          <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '1rem', marginBottom: '1.5rem' }}>
            <div>
              <label style={{ display: 'block', marginBottom: '0.5rem', fontWeight: '500' }}>
                Groupe sanguin
              </label>
              <select 
                name="groupeSanguin" 
                value={formData.groupeSanguin} 
                onChange={handleInputChange}
                style={inputStyle}
              >
                <option value="">S√©lectionner...</option>
                <option value="A+">A+</option><option value="A-">A-</option>
                <option value="B+">B+</option><option value="B-">B-</option>
                <option value="AB+">AB+</option><option value="AB-">AB-</option>
                <option value="O+">O+</option><option value="O-">O-</option>
              </select>
            </div>
            <div>
              <label style={{ display: 'block', marginBottom: '0.5rem', fontWeight: '500' }}>
                Sexe patient
              </label>
              <input 
                name="sexe" 
                value={formData.sexe} 
                readOnly 
                style={{ ...inputStyle, background: '#f5f5f5' }}
              />
            </div>
          </div>

          <div style={{ marginBottom: '1.5rem' }}>
            <label style={{ display: 'block', marginBottom: '0.5rem', fontWeight: '500' }}>
              R√©sum√© m√©dical
            </label>
            <textarea
              name="resumeMedical"
              placeholder="Ant√©c√©dents, diagnostics actuels..."
              value={formData.resumeMedical}
              onChange={handleInputChange}
              rows={4}
              style={{ ...inputStyle, width: '100%', resize: 'vertical' }}
            />
          </div>

          <div style={{ display: 'flex', gap: '1rem' }}>
            <button 
              type="submit" 
              disabled={!formData.patientId || loading}
              style={{
                flex: 1, padding: '1.2rem', 
                background: formData.patientId ? 'linear-gradient(45deg, #4caf50, #45a049)' : '#ccc',
                color: 'white', border: 'none', borderRadius: '8px', 
                fontSize: '1.1rem', fontWeight: 'bold', cursor: formData.patientId ? 'pointer' : 'not-allowed'
              }}
            >
              {loading ? '‚è≥ Cr√©ation...' : 'üíæ Cr√©er Dossier'}
            </button>
            <button 
              type="button"
              onClick={onClose}
              style={{
                flex: 1, padding: '1.2rem', background: '#f44336', color: 'white',
                border: 'none', borderRadius: '8px', fontSize: '1.1rem', cursor: 'pointer'
              }}
            >
