import React, { useEffect, useState } from 'react';
import { getData } from '../endpoint'; // ton utilitaire fetch

const DoctorDashboard = () => {
  const [dossiers, setDossiers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Fonction pour charger les dossiers
  const loadDossiers = async () => {
    try {
      setLoading(true);
      setError(null);
      const data = await getData('http://localhost:5000/api/dossier/mes-dossiers');
      setDossiers(data);
    } catch (err) {
      console.error('Erreur lors du chargement des dossiers:', err);
      setError(err.message || 'Erreur inconnue');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    loadDossiers();
  }, []);

  if (loading) {
    return <div>Chargement des dossiers...</div>;
  }

  if (error) {
    return <div style={{ color: 'red' }}>Erreur: {error}</div>;
  }

  if (dossiers.length === 0) {
    return <div>Aucun dossier médical trouvé.</div>;
  }

  return (
    <div>
      <h1>Mes dossiers médicaux</h1>
      <table border="1" cellPadding="5">
        <thead>
          <tr>
            <th>Patient</th>
            <th>Email</th>
            <th>Date de naissance</th>
            <th>Sexe</th>
            <th>Résumé</th>
            <th>Groupe sanguin</th>
          </tr>
        </thead>
        <tbody>
          {dossiers.map((dossier) => (
            <tr key={dossier._id}>
              <td>{dossier.patientId?.fullName || 'N/A'}</td>
              <td>{dossier.patientId?.email || 'N/A'}</td>
              <td>{dossier.patientId?.date_of_birth ? new Date(dossier.patientId.date_of_birth).toLocaleDateString() : 'N/A'}</td>
              <td>{dossier.patientId?.sexe || 'N/A'}</td>
              <td>{dossier.resumeMedical || 'N/A'}</td>
              <td>{dossier.groupeSanguin || 'N/A'}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default DoctorDashboard;
