import React, { useEffect, useState, useCallback, useMemo } from "react";
import { getData, deleteData, putData } from "../api/endpoint";
import DossierForm from "./DossierForm";
const ITEMS_PER_PAGE = 10;

const DoctorDashboard = () => {
  const [patients, setPatients] = useState([]);
  const [dossiers, setDossiers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [searchTerm, setSearchTerm] = useState("");
  const [currentPage, setCurrentPage] = useState(1);
  const [currentTab, setCurrentTab] = useState("patients");
  const [modalData, setModalData] = useState(null);

  const fetchData = useCallback(async () => {
    try {
      setLoading(true);
      setError(null);
      const patientsData = await getData("/patients");
      const dossiersData = await getData("/mes-dossiers");
      setPatients(patientsData || []);
      setDossiers(dossiersData || []);
    } catch (err) {
      console.error("Fetch error:", err);
      setError(err.message || "Erreur lors du chargement des donn√©es");
    } finally {
      setLoading(false);
    }
  }, []);

  useEffect(() => {
    fetchData();
  }, [fetchData]);

  const handleDeletePatient = useCallback(async (id) => {
    if (!window.confirm("Voulez-vous vraiment supprimer ce patient ?")) return;
    
    const prevPatients = patients;
    setPatients(prev => prev.filter(p => p._id !== id));
    
    try {
      await deleteData(`/user/${id}`);
    } catch (err) {
      setPatients(prevPatients);
      alert("Erreur lors de la suppression : " + err.message);
    }
  }, [patients]);

  const handleDeleteDossier = useCallback(async (id) => {
    if (!window.confirm("Voulez-vous vraiment supprimer ce dossier ?")) return;
    
    try {
      setDossiers(prev => prev.filter(d => d._id !== id));
      await deleteData(`/dossier/${id}`);
    } catch (err) {
      alert("Erreur lors de la suppression : " + err.message);
    }
  }, []);

  const openModal = useCallback((item, type) => {
    setModalData({ ...item, type });
  }, []);

  const closeModal = useCallback(() => {
    setModalData(null);
  }, []);

  const saveChanges = useCallback(async () => {
    if (!modalData) return;
    
    try {
      if (modalData.type === "patient") {
        await putData(`/user/${modalData._id}`, modalData);
        setPatients(prev => prev.map(p => p._id === modalData._id ? modalData : p));
      } else {
        await putData(`/dossier/${modalData._id}`, modalData);
        setDossiers(prev => prev.map(d => d._id === modalData._id ? modalData : d));
      }
      closeModal();
    } catch (err) {
      alert("Erreur lors de la mise √† jour : " + err.message);
    }
  }, [modalData, closeModal]);

  const getPaginatedData = useCallback((data) => {
    const filtered = data.filter(item =>
      item.username 
        ? item.username.toLowerCase().includes(searchTerm.toLowerCase())
        : item.patientName && item.patientName.toLowerCase().includes(searchTerm.toLowerCase())
    );
    
    const totalPages = Math.ceil(filtered.length / ITEMS_PER_PAGE);
    const startIndex = (currentPage - 1) * ITEMS_PER_PAGE;
    
    return {
      paginated: filtered.slice(startIndex, startIndex + ITEMS_PER_PAGE),
      totalPages,
      filteredCount: filtered.length
    };
  }, [searchTerm, currentPage]);

  const patientsData = useMemo(() => getPaginatedData(patients), [getPaginatedData, patients]);
  const dossiersData = useMemo(() => getPaginatedData(dossiers), [getPaginatedData, dossiers]);

  const currentData = currentTab === 'patients' ? patientsData : dossiersData;

  if (loading) {
    return (
      <div className="flex justify-center items-center min-h-screen">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600"></div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="flex justify-center items-center min-h-screen text-red-600">
        <div className="text-center">
          <h2 className="text-2xl font-bold mb-4">Erreur</h2>
          <p>{error}</p>
          <button 
            onClick={fetchData}
            className="mt-4 bg-blue-600 text-white px-6 py-2 rounded-lg hover:bg-blue-700"
          >
            R√©essayer
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 to-indigo-100 p-8">
      <div className="max-w-7xl mx-auto">
        <div className="mb-12 text-center">
          <h1 className="text-5xl font-bold bg-gradient-to-r from-blue-600 to-purple-600 bg-clip-text text-transparent mb-4">
            Dashboard M√©decin
          </h1>
          <p className="text-xl text-gray-600">Gestion patients et dossiers m√©dicaux</p>
        </div>

        <div className="mb-12 space-y-6">
          <input
            type="text"
            placeholder="Rechercher un patient ou dossier..."
            value={searchTerm}
            onChange={(e) => {
              setSearchTerm(e.target.value);
              setCurrentPage(1);
            }}
            className="w-full max-w-2xl mx-auto block p-4 text-lg border-2 border-gray-200 rounded-2xl shadow-lg focus:ring-4 focus:ring-blue-200 focus:border-blue-500 transition-all duration-300"
          />
          
          <div className="flex justify-center">
            <div className="bg-white rounded-2xl shadow-xl p-1">
              <button
                onClick={() => {
                  setCurrentTab('patients');
                  setCurrentPage(1);
                }}
                className={`px-8 py-3 rounded-xl font-medium transition-all shadow-md ${
                  currentTab === 'patients'
                    ? 'bg-blue-600 text-white shadow-lg'
                    : 'text-gray-700 hover:bg-gray-100'
                }`}
              >
                üë• Patients ({patients.length})
              </button>
              <button
                onClick={() => {
                  setCurrentTab('dossiers');
                  setCurrentPage(1);
                }}
                className={`px-8 py-3 rounded-xl font-medium transition-all shadow-md ml-1 ${
                  currentTab === 'dossiers'
                    ? 'bg-purple-600 text-white shadow-lg'
                    : 'text-gray-700 hover:bg-gray-100'
                }`}
              >
                üìÅ Dossiers ({dossiers.length})
              </button>
            </div>
          </div>
        </div>

        <section className="bg-white rounded-3xl shadow-2xl p-10 mb-12">
          <div className="flex justify-between items-center mb-8">
            <h3 className="text-3xl font-bold text-gray-800 flex items-center">
              {currentTab === 'patients' ? 'üë• Patients' : 'üìÅ Dossiers M√©dicaux'}
              <span className="ml-2 text-xl text-gray-500">({currentData.filteredCount})</span>
            </h3>
            <div className="text-sm text-gray-500">
              Page {currentPage} sur {currentData.totalPages}
            </div>
          </div>

          <div className="overflow-x-auto">
            <table className="w-full table-auto">
              <thead>
                <tr className={currentTab === 'patients' 
                  ? "bg-gradient-to-r from-blue-500 to-blue-600 text-white"
                  : "bg-gradient-to-r from-purple-500 to-purple-600 text-white"
                }>
                  {currentTab === 'patients' ? (
                    <>
                      <th className="px-6 py-4 text-left rounded-tl-2xl">Nom</th>
                      <th className="px-6 py-4 text-left">Email</th>
                      <th className="px-6 py-4 text-left">T√©l√©phone</th>
                      <th className="px-6 py-4 text-left rounded-tr-2xl">Actions</th>
                    </>
                  ) : (
                    <>
                      <th className="px-6 py-4 text-left rounded-tl-2xl">Patient</th>
                      <th className="px-6 py-4 text-left">R√©sum√©</th>
                      <th className="px-6 py-4 text-left">Date</th>
                      <th className="px-6 py-4 text-left rounded-tr-2xl">Actions</th>
                    </>
                  )}
                </tr>
              </thead>
              <tbody className="divide-y divide-gray-200">
                {currentData.paginated.map((item) => (
                  <tr key={item._id} className="hover:bg-gray-50 transition-colors">
                    {currentTab === 'patients' ? (
                      <>
                        <td className="px-6 py-4 font-medium text-gray-900">{item.username}</td>
                        <td className="px-6 py-4 text-gray-600">{item.email}</td>
                        <td className="px-6 py-4 text-gray-600">{item.phone || "N/A"}</td>
                      </>
                    ) : (
                      <>
                        <td className="px-6 py-4 font-medium text-gray-900">{item.patientName || "N/A"}</td>
                        <td className="px-6 py-4 max-w-md truncate text-gray-600">{item.summary}</td>
                        <td className="px-6 py-4 text-gray-600">{new Date(item.createdAt).toLocaleDateString('fr-FR')}</td>
                      </>
                    )}
                    <td className="px-6 py-4">
                      <div className="flex space-x-2">
                        <button
                          onClick={() => openModal(item, currentTab)}
                          className={`px-4 py-2 text-white text-sm font-medium rounded-xl transition-all shadow-md ${
                            currentTab === 'patients' 
                              ? 'bg-blue-500 hover:bg-blue-600' 
                              : 'bg-purple-500 hover:bg-purple-600'
                          }`}
                        >
                          ‚úèÔ∏è Modifier
                        </button>
                        <button
                          onClick={() => currentTab === 'patients' 
                            ? handleDeletePatient(item._id)
                            : handleDeleteDossier(item._id)
                          }
                          className="px-4 py-2 bg-red-500 text-white text-sm font-medium rounded-xl hover:bg-red-600 transition-all shadow-md"
                        >
                          üóëÔ∏è Supprimer
                        </button>
                      </div>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>

          {currentData.totalPages > 1 && (
            <div className="mt-8 flex justify-center space-x-2">
              <button
                onClick={() => setCurrentPage(prev => Math.max(prev - 1, 1))}
                disabled={currentPage === 1}
                className="px-4 py-2 bg-gray-200 text-gray-700 rounded-xl font-medium hover:bg-gray-300 disabled:opacity-50 transition-all shadow-md"
              >
                ‚Üê Pr√©c√©dent
              </button>
              {Array.from({ length: currentData.totalPages }, (_, i) => (
                <button
                  key={i}
                  onClick={() => setCurrentPage(i + 1)}
                  className={`px-4 py-2 rounded-xl font-medium transition-all shadow-md ${
                    currentPage === i + 1
                      ? "bg-blue-600 text-white shadow-lg"
                      : "bg-white text-gray-700 hover:bg-gray-100"
                  }`}
                >
                  {i + 1}
                </button>
              ))}
              <button
                onClick={() => setCurrentPage(prev => Math.min(prev + 1, currentData.totalPages))}
                disabled={currentPage === currentData.totalPages}
                className="px-4 py-2 bg-gray-200 text-gray-700 rounded-xl font-medium hover:bg-gray-300 disabled:opacity-50 transition-all shadow-md"
              >
                Suivant ‚Üí
              </button>
            </div>
          )}
        </section>

        {modalData && (
          <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
            <div className="bg-white rounded-3xl shadow-2xl max-w-2xl w-full max-h-[90vh] overflow-y-auto">
              <div className="p-8 border-b border-gray-200">
                <h3 className="text-2xl font-bold text-gray-800">
                  √âditer {modalData.type === 'patient' ? 'Patient' : 'Dossier'}
                </h3>
              </div>
              
              <div className="p-8 space-y-6">
                {modalData.type === "patient" ? (
                  <>
                    <div>
                      <label className="block text-sm font-medium text-gray-700 mb-2">Nom</label>
                      <input
                        type="text"
                        value={modalData.username || ''}
                        onChange={(e) => setModalData({ ...modalData, username: e.target.value })}
                        className="w-full p-4 border border-gray-300 rounded-xl focus:ring-2 focus:ring-blue-500 focus:border-blue-500 transition-all"
                      />
                    </div>
                    <div>
                      <label className="block text-sm font-medium text-gray-700 mb-2">Email</label>
                      <input
                        type="email"
                        value={modalData.email || ''}
                        onChange={(e) => setModalData({ ...modalData, email: e.target.value })}
                        className="w-full p-4 border border-gray-300 rounded-xl focus:ring-2 focus:ring-blue-500 focus:border-blue-500 transition-all"
                      />
                    </div>
                    <div>
                      <label className="block text-sm font-medium text-gray-700 mb-2">T√©l√©phone</label>
                      <input
                        type="tel"
                        value={modalData.phone || ''}
                        onChange={(e) => setModalData({ ...modalData, phone: e.target.value })}
                        className="w-full p-4 border border-gray-300 rounded-xl focus:ring-2 focus:ring-blue-500 focus:border-blue-500 transition-all"
                      />
                    </div>
                  </>
                ) : (
                  <>
                    <div>
                      <label className="block text-sm font-medium text-gray-700 mb-2">Nom Patient</label>
                      <input
                        type="text"
                        value={modalData.patientName || ''}
                        onChange={(e) => setModalData({ ...modalData, patientName: e.target.value })}
                        className="w-full p-4 border border-gray-300 rounded-xl focus:ring-2 focus:ring-purple-500 focus:border-purple-500 transition-all"
                      />
                    </div>
                    <div>
                      <label className="block text-sm font-medium text-gray-700 mb-2">R√©sum√©</label>
                      <textarea
                        rows={5}
                        value={modalData.summary || ''}
                        onChange={(e) => setModalData({ ...modalData, summary: e.target.value })}
                        className="w-full p-4 border border-gray-300 rounded-xl focus:ring-2 focus:ring-purple-500 focus:border-purple-500 transition-all resize-vertical"
                      />
                    </div>
                  </>
                )}
              </div>

              <div className="p-8 border-t border-gray-200 bg-gray-50 rounded-b-3xl flex justify-end space-x-3">
                <button
                  onClick={closeModal}
                  className="px-8 py-3 bg-gray-200 text-gray-800 font-medium rounded-2xl hover:bg-gray-300 transition-all shadow-md"
                >
                  Annuler
                </button>
                <button
                  onClick={saveChanges}
                  className="px-8 py-3 bg-gradient-to-r from-blue-600 to-purple-600 text-white font-medium rounded-2xl hover:shadow-xl hover:from-blue-700 hover:to-purple-700 transition-all shadow-lg"
                >
                  üíæ Sauvegarder
                </button>
              </div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default DoctorDashboard;
