import React, { useState, useEffect } from 'react';
import api, { deleteData, postData } from '../../api/endpoint';
import { 
  FiFileText, 
  FiSearch, 
  FiPlus, 
  FiEdit, 
  FiTrash2, 
  FiEye, 
  FiUserPlus,
  FiUsers  // ✅ AJOUTÉ ICI
} from 'react-icons/fi';


const Patients = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const [patients, setPatients] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const [selectedPatient, setSelectedPatient] = useState(null);
  const [showForm, setShowForm] = useState(false);
  const [formData, setFormData] = useState({
    fullName: '',
    email: '',
    password: '',
    date_of_birth: '',
    sexe: '',
    statut: 'ACTIF'
  });
  const [formError, setFormError] = useState('');
  const [submitting, setSubmitting] = useState(false);
  const [successMessage, setSuccessMessage] = useState('');

  useEffect(() => {
    loadPatients();
  }, []);

  // ✅ FONCTION MANQUANTE AJOUTÉE
  const handleCloseForm = () => {
    setShowForm(false);
    setFormData({
      fullName: '',
      email: '',
      password: '',
      date_of_birth: '',
      sexe: '',
      statut: 'ACTIF'
    });
    setFormError('');
    setSelectedPatient(null);
  };

  const loadPatients = async () => {
    try {
      setLoading(true);
      setError(null);
      const users = await api.getData('/users');
      const patientsList = users.filter(user => user.role === 'Patient');
      setPatients(patientsList);
    } catch (err) {
      console.error('Erreur chargement patients:', err);
      setError(err.message || 'Impossible de charger les patients.');
      setPatients([]);
    } finally {
      setLoading(false);
    }
  };

  const handleDelete = async (patientId) => {
    if (!window.confirm('Êtes-vous sûr de vouloir supprimer ce patient ?')) return;
    try {
      await deleteData(`/users/${patientId}`);
      loadPatients();
      setSuccessMessage('Patient supprimé avec succès !');
      setTimeout(() => setSuccessMessage(''), 3000);
    } catch (err) {
      alert(err.message || 'Erreur suppression');
    }
  };

  const handleView = (patient) => {
    setSelectedPatient(patient);
    setShowModal(true);
  };

  const handleOpenForm = () => {
    setFormData({
      fullName: '',
      email: '',
      password: '',
      date_of_birth: '',
      sexe: '',
      statut: 'ACTIF'
    });
    setFormError('');
    setShowForm(true);
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setFormError('');
    setSubmitting(true);

    if (!formData.fullName || !formData.email || !formData.password) {
      setFormError('Veuillez remplir tous les champs obligatoires');
      setSubmitting(false);
      return;
    }

    if (formData.password.length < 6) {
      setFormError('Le mot de passe doit contenir au moins 6 caractères');
      setSubmitting(false);
      return;
    }

    try {
      const patientData = {
        fullName: formData.fullName,
        email: formData.email,
        motDePasse: formData.password,
        role: 'Patient',
        dateNaissance: formData.date_of_birth || undefined,
        sexe: formData.sexe || undefined,
        statut: formData.statut
      };

      await postData('/auth/register', patientData);
      handleCloseForm(); // ✅ FONCTION MAINTENANT DÉFINIE
      loadPatients();
      setSuccessMessage('Patient créé avec succès !');
      setTimeout(() => setSuccessMessage(''), 3000);
    } catch (err) {
      setFormError(err.message || 'Erreur création patient');
    } finally {
      setSubmitting(false);
    }
  };

  const calculateAge = (dateOfBirth) => {
    if (!dateOfBirth) return 'N/A';
    const birthDate = new Date(dateOfBirth);
    const today = new Date();
    let age = today.getFullYear() - birthDate.getFullYear();
    const monthDiff = today.getMonth() - birthDate.getMonth();
    if (monthDiff < 0 || (monthDiff === 0 && today.getDate() < birthDate.getDate())) {
      age--;
    }
    return age;
  };

  const filteredPatients = patients.filter(patient =>
    patient.fullName?.toLowerCase().includes(searchTerm.toLowerCase()) ||
    patient.email?.toLowerCase().includes(searchTerm.toLowerCase())
  );

  if (loading) {
    return (
      <div className="flex items-center justify-center min-h-[400px] p-6">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600 mx-auto mb-4"></div>
          <p className="text-lg text-gray-600">Chargement des patients...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="p-6 max-w-7xl mx-auto">
      {/* HEADER */}
      <div className="flex flex-col lg:flex-row justify-between items-start lg:items-center mb-12 gap-6">
        <div>
          <h1 className="text-4xl font-bold text-gray-900 flex items-center gap-4 mb-2">
            <FiUsers size={48} className="text-blue-600" />
            Gestion des Patients
          </h1>
          <p className="text-xl text-gray-600">({filteredPatients.length} patients)</p>
        </div>
        
        <div className="flex gap-4">
          <div className="relative">
            <FiSearch className="absolute left-4 top-1/2 transform -translate-y-1/2 text-gray-400" size={20} />
            <input
              type="text"
              placeholder="Rechercher un patient..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              className="w-80 pl-12 pr-6 py-3 border-2 border-gray-200 rounded-2xl focus:ring-4 focus:ring-blue-100 focus:border-blue-500"
            />
          </div>
          <button
            onClick={handleOpenForm}
            className="bg-gradient-to-r from-green-500 to-green-600 text-white px-8 py-4 rounded-2xl font-bold shadow-xl hover:shadow-2xl transform hover:scale-105 transition-all"
          >
            <FiUserPlus size={20} className="inline mr-2" />
            Ajouter Patient
          </button>
        </div>
      </div>

      {/* SUCCESS MESSAGE */}
      {successMessage && (
        <div className="bg-green-50 border-2 border-green-200 text-green-800 px-6 py-4 rounded-2xl mb-8 shadow-lg animate-pulse">
          ✅ {successMessage}
        </div>
      )}

      {/* ERROR MESSAGE */}
      {error && (
        <div className="bg-red-50 border-2 border-red-200 text-red-800 px-6 py-4 rounded-2xl mb-8 shadow-lg">
          ⚠️ {error}
        </div>
      )}

      {/* FORMULAIRE MODAL */}
      {showForm && (
        <div className="fixed inset-0 bg-black bg-opacity-50 z-50 flex items-center justify-center p-6">
          <div className="bg-white rounded-3xl p-8 max-w-2xl w-full max-h-[90vh] overflow-y-auto shadow-2xl">
            <div className="flex justify-between items-center mb-8">
              <h2 className="text-3xl font-bold text-gray-900">Ajouter un Patient</h2>
              <button onClick={handleCloseForm} className="text-gray-500 hover:text-gray-700 text-2xl">
                ×
              </button>
            </div>

            {formError && (
              <div className="bg-red-50 border border-red-200 text-red-700 px-4 py-3 rounded-xl mb-6">
                {formError}
              </div>
            )}

            <form onSubmit={handleSubmit} className="space-y-6">
              <div>
                <label className="block text-sm font-semibold text-gray-700 mb-3">Nom Complet *</label>
                <input
                  name="fullName"
                  value={formData.fullName}
                  onChange={handleChange}
                  className="w-full p-4 border-2 border-gray-200 rounded-2xl focus:ring-4 focus:ring-blue-100 focus:border-blue-500"
                  placeholder="Nom Prénom"
                  required
                />
              </div>

              <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                <div>
                  <label className="block text-sm font-semibold text-gray-700 mb-3">Email *</label>
                  <input
                    name="email"
                    type="email"
                    value={formData.email}
                    onChange={handleChange}
                    className="w-full p-4 border-2 border-gray-200 rounded-2xl focus:ring-4 focus:ring-blue-100 focus:border-blue-500"
                    placeholder="patient@exemple.com"
                    required
                  />
                </div>
                <div>
                  <label className="block text-sm font-semibold text-gray-700 mb-3">Mot de passe *</label>
                  <input
                    name="password"
                    type="password"
                    value={formData.password}
                    onChange={handleChange}
                    className="w-full p-4 border-2 border-gray-200 rounded-2xl focus:ring-4 focus:ring-blue-100 focus:border-blue-500"
                    placeholder="********"
                    required
                  />
                </div>
              </div>

              <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
                <div>
                  <label className="block text-sm font-semibold text-gray-700 mb-3">Date de naissance</label>
                  <input
                    name="date_of_birth"
                    type="date"
                    value={formData.date_of_birth}
                    onChange={handleChange}
                    className="w-full p-4 border-2 border-gray-200 rounded-2xl focus:ring-4 focus:ring-blue-100 focus:border-blue-500"
                  />
                </div>
                <div>
                  <label className="block text-sm font-semibold text-gray-700 mb-3">Sexe</label>
                  <select
                    name="sexe"
                    value={formData.sexe}
                    onChange={handleChange}
                    className="w-full p-4 border-2 border-gray-200 rounded-2xl focus:ring-4 focus:ring-blue-100 focus:border-blue-500"
                  >
                    <option value="">Non spécifié</option>
                    <option value="Homme">Homme</option>
                    <option value="Femme">Femme</option>
                  </select>
                </div>
                <div>
                  <label className="block text-sm font-semibold text-gray-700 mb-3">Statut</label>
                  <select
                    name="statut"
                    value={formData.statut}
                    onChange={handleChange}
                    className="w-full p-4 border-2 border-gray-200 rounded-2xl focus:ring-4 focus:ring-blue-100 focus:border-blue-500"
                  >
                    <option value="ACTIF">Actif</option>
                    <option value="INACTIF">Inactif</option>
                  </select>
                </div>
              </div>

              <div className="flex gap-4 pt-6">
                <button
                  type="submit"
                  disabled={submitting}
                  className="flex-1 bg-gradient-to-r from-blue-600 to-blue-700 text-white py-4 px-8 rounded-2xl font-bold hover:shadow-xl transform hover:scale-[1.02] transition-all disabled:opacity-50"
                >
                  {submitting ? 'Création...' : 'Créer Patient'}
                </button>
                <button
                  type="button"
                  onClick={handleCloseForm}
                  className="px-8 py-4 border-2 border-gray-300 text-gray-700 rounded-2xl font-semibold hover:bg-gray-50 hover:shadow-md transition-all"
                >
                  Annuler
                </button>
              </div>
            </form>
          </div>
        </div>
      )}

      {/* MODAL DÉTAIL */}
      {showModal && selectedPatient && (
        <div className="fixed inset-0 bg-black bg-opacity-50 z-50 flex items-center justify-center p-6">
          <div className="bg-white rounded-3xl p-8 max-w-2xl w-full max-h-[90vh] overflow-y-auto shadow-2xl">
            <div className="flex justify-between items-center mb-8">
              <h2 className="text-3xl font-bold text-gray-900">Détails Patient</h2>
              <button onClick={() => setShowModal(false)} className="text-gray-500 hover:text-gray-700 text-2xl">
                ×
              </button>
            </div>
            <div className="space-y-6">
              <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                <div><strong>Nom:</strong> {selectedPatient.fullName}</div>
                <div><strong>Email:</strong> {selectedPatient.email}</div>
                <div><strong>Âge:</strong> {calculateAge(selectedPatient.dateNaissance)}</div>
                <div><strong>Sexe:</strong> {selectedPatient.sexe || 'N/A'}</div>
                <div><strong>Téléphone:</strong> {selectedPatient.telephone || 'N/A'}</div>
                <div><strong>Statut:</strong> 
                  <span className={`ml-2 px-3 py-1 rounded-full text-sm font-semibold ${
                    selectedPatient.statut === 'ACTIF' ? 'bg-green-100 text-green-800' : 'bg-red-100 text-red-800'
                  }`}>
                    {selectedPatient.statut}
                  </span>
                </div>
              </div>
            </div>
          </div>
        </div>
      )}

      {/* TABLEAU PATIENTS */}
      <div className="bg-white rounded-3xl shadow-2xl border border-gray-100 overflow-hidden">
        <div className="p-8 border-b border-gray-100">
          <h3 className="text-2xl font-bold text-gray-900">Liste des Patients</h3>
        </div>
        
        {filteredPatients.length === 0 ? (
          <div className="p-16 text-center text-gray-500">
            <FiUsers size={64} className="mx-auto mb-6 opacity-50" />
            <h3 className="text-2xl font-semibold mb-2">Aucun patient trouvé</h3>
            <p>{searchTerm ? 'Aucun patient ne correspond à votre recherche.' : 'Commencez par ajouter un patient.'}</p>
          </div>
        ) : (
          <div className="overflow-x-auto">
            <table className="w-full">
              <thead className="bg-gradient-to-r from-gray-50 to-gray-100">
                <tr>
                  <th className="px-8 py-6 text-left text-sm font-bold text-gray-900 uppercase tracking-wider">Patient</th>
                  <th className="px-8 py-6 text-left text-sm font-bold text-gray-900 uppercase tracking-wider">Email</th>
                  <th className="px-8 py-6 text-left text-sm font-bold text-gray-900 uppercase tracking-wider">Âge</th>
                  <th className="px-8 py-6 text-left text-sm font-bold text-gray-900 uppercase tracking-wider">Sexe</th>
                  <th className="px-8 py-6 text-left text-sm font-bold text-gray-900 uppercase tracking-wider">Statut</th>
                  <th className="px-8 py-6 text-left text-sm font-bold text-gray-900 uppercase tracking-wider">Actions</th>
                </tr>
              </thead>
              <tbody className="divide-y divide-gray-200">
                {filteredPatients.map((patient) => (
                  <tr key={patient._id} className="hover:bg-gray-50 transition-colors">
                    <td className="px-8 py-6 font-semibold text-gray-900">{patient.fullName}</td>
                    <td className="px-8 py-6 text-gray-700">{patient.email}</td>
                    <td className="px-8 py-6 font-medium">{calculateAge(patient.dateNaissance)}</td>
                    <td className="px-8 py-6">{patient.sexe || 'N/A'}</td>
                    <td className="px-8 py-6">
                      <span className={`px-4 py-2 rounded-full text-sm font-semibold ${
                        patient.statut === 'ACTIF' 
                          ? 'bg-green-100 text-green-800' 
                          : 'bg-red-100 text-red-800'
                      }`}>
                        {patient.statut}
                      </span>
                    </td>
                    <td className="px-8 py-6">
                      <div className="flex gap-2">
                        <button
                          onClick={() => handleView(patient)}
                          className="p-2 text-blue-600 hover:bg-blue-50 rounded-xl transition-all"
                          title="Voir détails"
                        >
                          <FiEye size={18} />
                        </button>
                        <button
                          onClick={() => handleDelete(patient._id)}
                          className="p-2 text-red-600 hover:bg-red-50 rounded-xl transition-all"
                          title="Supprimer"
                        >
                          <FiTrash2 size={18} />
                        </button>
                      </div>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}
      </div>
    </div>
  );
};

export default Patients;
