// src/api/endpoint.js
const BASE_URL = process.env.REACT_APP_API_BASE_URL || "http://localhost:5000/api";

// =================== TOKEN MANAGEMENT ===================
const TOKEN_KEY = "token";

const getAuthToken = () => localStorage.getItem(TOKEN_KEY);
const setAuthToken = (token) => localStorage.setItem(TOKEN_KEY, token);
const removeAuthToken = () => localStorage.removeItem(TOKEN_KEY);



// DÃ©coder le token JWT pour rÃ©cupÃ©rer infos utilisateur
const decodeToken = (token) => {
  if (!token) return null;
  try {
    const base64Url = token.split('.')[1];
    const base64 = base64Url.replace(/-/g, '+').replace(/_/g, '/');
    const jsonPayload = decodeURIComponent(
      atob(base64)
        .split('')
        .map(c => '%' + ('00' + c.charCodeAt(0).toString(16)).slice(-2))
        .join('')
    );
    return JSON.parse(jsonPayload);
  } catch {
    return null;
  }
};

// VÃ©rifier si le token est encore valide
const isTokenValid = (token) => {
  if (!token) return false;
  try {
    const decoded = decodeToken(token);
    return decoded && decoded.exp * 1000 > Date.now();
  } catch {
    return false;
  }
};

// =================== URL BUILDER ===================
const buildUrl = (endpoint) => {
  let cleanEndpoint = endpoint.trim();
  cleanEndpoint = cleanEndpoint.replace(/^\/api\/?/i, '');
  cleanEndpoint = cleanEndpoint.replace(/^\/+/, '').replace(/\/+$/, '');
  return `${BASE_URL}/${cleanEndpoint}`;
};

// =================== HEADERS ===================
const getHeaders = (extraHeaders = {}) => {
  const token = getAuthToken();
  const headers = {
    'Content-Type': 'application/json',
    ...extraHeaders
  };
  if (token && isTokenValid(token)) {
    headers.Authorization = `Bearer ${token}`;
  }
  return headers;
};

// =================== ERROR HANDLER ===================
const handleApiError = async (response) => {
  const status = response.status;

  if (status === 401) {
    removeAuthToken();
    if (typeof window !== 'undefined' && window.location.pathname !== '/login') {
      window.location.href = '/login';
    }
    throw new Error('Session expirÃ©e. Redirection vers login...');
  }

  if (status === 403) {
    const errorText = await response.text();
    throw new Error(errorText || 'ðŸ”’ AccÃ¨s refusÃ©');
  }

  if (status === 404) {
    throw new Error('ðŸ“ Endpoint non trouvÃ© - Backend route manquante');
  }

  let errorData;
  try {
    errorData = await response.json();
  } catch {
    errorData = { message: `Erreur HTTP ${status}` };
  }

  // Gestion des erreurs MongoDB doublon
  if (errorData.errorName === 'MongoServerError' && errorData.code === 11000) {
    throw new Error('âš ï¸ DonnÃ©es en doublon');
  }

  // Gestion des erreurs de validation
  if (errorData.validationErrors) {
    const errors = Object.values(errorData.validationErrors)
      .map(err => err.message)
      .join('; ');
    throw new Error(`Validation: ${errors}`);
  }

  throw new Error(errorData.message || `Erreur ${status}`);
};

// =================== API METHODS ===================
const getData = async (endpoint) => {
  const url = buildUrl(endpoint);
  const response = await fetch(url, { headers: getHeaders() });
  if (!response.ok) throw await handleApiError(response);
  return response.json();
};

const postData = async (endpoint, payload) => {
  const url = buildUrl(endpoint);
  const response = await fetch(url, {
    method: 'POST',
    headers: getHeaders(),
    body: JSON.stringify(payload)
  });
  if (!response.ok) throw await handleApiError(response);
  return response.json();
};

const putData = async (endpoint, payload) => {
  const url = buildUrl(endpoint);
  const response = await fetch(url, {
    method: 'PUT',
    headers: getHeaders(),
    body: JSON.stringify(payload)
  });
  if (!response.ok) throw await handleApiError(response);
  return response.json();
};

const deleteData = async (endpoint) => {
  const url = buildUrl(endpoint);
  const response = await fetch(url, {
    method: 'DELETE',
    headers: getHeaders()
  });
  if (!response.ok) throw await handleApiError(response);
  return response.json();
};

// =================== EXPORT ===================
export {
  TOKEN_KEY,
  getAuthToken,
  setAuthToken,
  removeAuthToken,
  decodeToken,
  isTokenValid,
  getData,
  postData,
  putData,
  deleteData
};

export default {
  getData,
  postData,
  putData,
  deleteData
};
