// src/pages/Composants/PatientDashboard.jsx
import React from "react";
import { Link } from "react-router-dom";

const PatientDashboard = () => {
  return (
    <div className="p-6">
      <h1 className="text-3xl font-bold mb-6">Tableau de bord patient</h1>

      <div className="flex flex-col gap-4 max-w-sm">
        {/* Lien vers Mes Dossiers */}
        <Link
          to="/patient/mes-dossiers"
          className="block p-4 bg-blue-600 text-white rounded hover:bg-blue-700 text-center font-semibold"
        >
          Mes dossiers
        </Link>

        {/* Déconnexion */}
        <Link
          to="/logout"
          className="block p-4 bg-red-600 text-white rounded hover:bg-red-700 text-center font-semibold"
        >
          Déconnexion
        </Link>
      </div>

      <div className="mt-8 text-gray-700">
        <p>Bienvenue sur votre espace patient</p>
      </div>
    </div>
  );
};

export default PatientDashboard;
