import React, { useState, useEffect } from 'react';
import { Link, Routes, Route, useLocation, useParams } from 'react-router-dom';

const MedecinDashboard = () => {
  const location = useLocation();
  const [profile, setProfile] = useState(null);
  const [patients, setPatients] = useState([]);
  const [rendezVous, setRendezVous] = useState([]);
  const [ordonnances, setOrdonnances] = useState([]);
  const [dossiers, setDossiers] = useState([]);
  const [resultatsExamens, setResultatsExamens] = useState([]);
  const [notifications, setNotifications] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showModal, setShowModal] = useState({ open: false, type: null, patientId: null });

  useEffect(() => {
    setTimeout(() => {
      setProfile({
        nom: 'NDIAYE', prenom: 'Moustapha', email: 'moustapha.ndiaye@govsanitaire.sn',
        telephone: '+221 78 987 6543', specialite: 'Cardiologue',
        numeroOrdre: 'SN-MED-2024-001', etablissement: 'CHU de Fann', statut: 'ValidÃ©'
      });
      
      const mockPatients = [
        { id: '1', nom: 'DIOP', prenom: 'Aminata', age: 35, sexe: 'F', telephone: '+221 78 123 4567', 
          email: 'aminata.diop@email.com', groupeSanguin: 'O+', allergies: 'Aucune', derniereConsult: '08/02/2026' },
        { id: '2', nom: 'SOW', prenom: 'Fatou', age: 42, sexe: 'F', telephone: '+221 77 456 7890', 
          email: 'fatou.sow@email.com', groupeSanguin: 'A+', allergies: 'PÃ©nicilline', derniereConsult: '07/02/2026' }
      ];
      
      setPatients(mockPatients);
      setRendezVous([
        { id: '1', patientId: '1', patient: 'Aminata DIOP', date: '10/02/2026 10:00', statut: 'ConfirmÃ©', type: 'consultation' },
        { id: '2', patientId: '2', patient: 'Fatou SOW', date: '09/02/2026 14:00', statut: 'ConfirmÃ©', type: 'urgence' }
      ]);
      
      // âœ… Ajout automatique d'ordonnances mock avec mode d'emploi dÃ©taillÃ©
      setOrdonnances([
        { id: 'o1', patientId: '1', medicament: 'ParacÃ©tamol 1000mg', dosage: '1cp 3x/jour', 
         duree: '5 jours', modeEmploi: 'Prendre aprÃ¨s les repas avec un verre d\'eau. Ne pas dÃ©passer 3g/jour.', 
         dateCreation: '08/02/2026', statut: 'Active' },
        { id: 'o2', patientId: '2', medicament: 'Amlodipine 5mg', dosage: '1cp matin', 
         duree: '30 jours', modeEmploi: 'Prendre le matin avant le petit-dÃ©jeuner. ContrÃ´ler TA hebdo.', 
         dateCreation: '07/02/2026', statut: 'Active' }
      ]);
      
      setDossiers([
        { id: 'd1', patientId: '1', antecedents: 'HTA lÃ©gÃ¨re', diagnostics: 'Palpitations', 
         notes: 'Suivi rÃ©gulier', analyses: 'ECG normal', ordonnancesIds: ['o1'] },
        { id: 'd2', patientId: '2', antecedents: 'DiabÃ¨te type 2', diagnostics: 'Hypertension', 
         notes: 'Bonne observance', analyses: 'Bilan sanguin OK', ordonnancesIds: ['o2'] }
      ]);
      
      setResultatsExamens([
        { id: 'r1', patientId: '1', type: 'scanner', titre: 'Scanner thoracique', date: '07/02/2026', resultat: 'Poumon sain' },
        { id: 'r2', patientId: '2', type: 'analyse', titre: 'Bilan sanguin', date: '06/02/2026', resultat: 'HbA1c: 7.2%' }
      ]);
      
      setLoading(false);
    }, 1000);
  }, []);

  const createDossier = (patientId, data) => {
    const nouveauDossier = { 
      id: Date.now().toString(), 
      patientId, 
      ...data, 
      dateCreation: new Date().toLocaleDateString('fr-FR'), 
      statut: 'Actif',
      ordonnancesIds: [] 
    };
    setDossiers(prev => [...prev, nouveauDossier]);
    setNotifications(prev => [...prev, { 
      id: Date.now(), 
      message: `âœ… Dossier crÃ©Ã© pour le patient`, 
      type: 'success', 
      date: new Date().toLocaleDateString('fr-FR') 
    }]);
    setShowModal({ open: false, type: null, patientId: null });
  };

  const createOrdonnance = (patientId, data) => {
    const nouvelleOrdonnance = { 
      id: Date.now().toString(), 
      patientId, 
      ...data, 
      dateCreation: new Date().toLocaleDateString('fr-FR'), 
      statut: 'Active' 
    };
    
    // âœ… Ajouter l'ordonnance au dossier du patient
    setOrdonnances(prev => [...prev, nouvelleOrdonnance]);
    setDossiers(prev => prev.map(dossier => 
      dossier.patientId === patientId 
        ? { ...dossier, ordonnancesIds: [...dossier.ordonnancesIds, nouvelleOrdonnance.id] }
        : dossier
    ));
    
    setNotifications(prev => [...prev, { 
      id: Date.now(), 
      message: `ðŸ’Š Ordonnance "${data.medicament}" Ã©mise`, 
      type: 'success', 
      date: new Date().toLocaleDateString('fr-FR') 
    }]);
    setShowModal({ open: false, type: null, patientId: null });
  };

  const createRdv = (patientId, data) => {
    const nouveauRdv = { 
      id: Date.now().toString(), 
      patientId, 
      patient: `${patients.find(p => p.id === patientId)?.prenom} ${patients.find(p => p.id === patientId)?.nom}`, 
      ...data, 
      statut: 'ConfirmÃ©' 
    };
    setRendezVous(prev => [...prev, nouveauRdv]);
    setNotifications(prev => [...prev, { 
      id: Date.now(), 
      message: `ðŸ“… RDV crÃ©Ã©`, 
      type: 'info', 
      date: new Date().toLocaleDateString('fr-FR') 
    }]);
    setShowModal({ open: false, type: null, patientId: null });
  };

  const addResultatExamen = (patientId, data) => {
    const nouveauResultat = { 
      id: Date.now().toString(), 
      patientId, 
      ...data, 
      date: new Date().toLocaleDateString('fr-FR') 
    };
    setResultatsExamens(prev => [...prev, nouveauResultat]);
    setNotifications(prev => [...prev, { 
      id: Date.now(), 
      message: `ðŸ©º RÃ©sultat ajoutÃ©`, 
      type: 'success', 
      date: new Date().toLocaleDateString('fr-FR') 
    }]);
    setShowModal({ open: false, type: null, patientId: null });
  };

  if (loading) return <LoadingScreen />;

  // âœ… COMPOSANTS INTERNES pour PatientDetail
  const PatientDetailInner = () => {
    const { id } = useParams();
    const patient = patients.find(p => p.id === id);
    if (!patient) return <NotFoundPage />;

    const patientDossier = dossiers.find(d => d.patientId === id);
    const patientOrdonnances = ordonnances.filter(o => o.patientId === id);
    const patientResultats = resultatsExamens.filter(r => r.patientId === id);
    const patientRdv = rendezVous.filter(r => r.patientId === id);

    // âœ… DossierResume avec ordonnances dÃ©taillÃ©es
    const DossierResume = ({ dossier, ordonnances }) => (
      <div className="bg-white p-8 rounded-2xl shadow-lg border border-gray-200">
        <h3 className="text-2xl font-bold text-gray-900 mb-6">ðŸ“‹ RÃ©sumÃ© mÃ©dical</h3>
        {dossier ? (
          <div className="space-y-6">
            <div className="space-y-2">
              <div><strong>AntÃ©cÃ©dents:</strong> {dossier.antecedents}</div>
              <div><strong>Diagnostic:</strong> {dossier.diagnostics}</div>
              <div><strong>Notes:</strong> {dossier.notes}</div>
            </div>
            
            {/* âœ… SECTION ORDONNANCES DÃ‰TAILLÃ‰ES VISIBLES DANS LE DOSSIER */}
            {ordonnances.length > 0 && (
              <div className="bg-gradient-to-r from-purple-50 to-blue-50 p-6 rounded-xl border-l-4 border-purple-400">
                <h4 className="text-xl font-bold text-purple-800 mb-4 flex items-center">
                  ðŸ’Š <span className="ml-2">Ordonnances actives ({ordonnances.length})</span>
                </h4>
                <div className="space-y-3">
                  {ordonnances.map(o => (
                    <div key={o.id} className="bg-white p-4 rounded-lg shadow-sm border border-gray-100">
                      <div className="font-bold text-lg text-gray-900 mb-1">{o.medicament}</div>
                      <div className="text-sm text-gray-600 mb-2">
                        ðŸ“¦ <strong>Dosage:</strong> {o.dosage} | ðŸ•’ <strong>DurÃ©e:</strong> {o.duree}
                      </div>
                      <div className="text-sm bg-yellow-50 p-3 rounded-lg border-l-4 border-yellow-400">
                        <strong>ðŸ“– Mode d'emploi:</strong>
                        <p className="mt-1">{o.modeEmploi}</p>
                      </div>
                      <div className="text-xs text-gray-500 mt-2">{o.dateCreation}</div>
                    </div>
                  ))}
                </div>
              </div>
            )}
          </div>
        ) : (
          <p className="text-gray-500">Aucun dossier crÃ©Ã©</p>
        )}
      </div>
    );

    // âœ… OrdonnancesResume mise Ã  jour
    const OrdonnancesResume = ({ ordonnances }) => (
      <div className="bg-white p-6 rounded-2xl shadow-lg border border-gray-200">
        <h3 className="text-xl font-bold text-gray-900 mb-4">ðŸ’Š Ordonnances rÃ©centes</h3>
        {ordonnances.length ? (
          ordonnances.slice(0, 3).map(o => (
            <div key={o.id} className="p-4 bg-gradient-to-r from-purple-50 to-pink-50 rounded-lg mb-3 border-l-4 border-purple-400">
              <div className="font-semibold text-lg">{o.medicament}</div>
              <div className="text-sm text-gray-600">{o.dosage} - {o.duree}</div>
            </div>
          ))
        ) : (
          <p className="text-gray-500 text-sm">Aucune ordonnance</p>
        )}
      </div>
    );

    return (
      <div className="max-w-7xl mx-auto space-y-8">
        <div className="bg-white p-8 rounded-2xl shadow-lg border border-gray-200">
          <div className="flex items-start gap-6">
            <div className="w-24 h-24 bg-gradient-to-br from-blue-500 to-blue-600 text-white rounded-full flex items-center justify-center font-bold text-2xl">
              {patient.sexe === 'M' ? 'ðŸ‘¨' : 'ðŸ‘©'}
            </div>
            <div className="flex-1">
              <h1 className="text-3xl font-bold text-gray-900 mb-2">{patient.prenom} {patient.nom}</h1>
              <div className="grid grid-cols-2 md:grid-cols-4 gap-4 text-sm text-gray-600">
                <div><span className="font-semibold">Ã‚ge:</span> {patient.age} ans</div>
                <div><span className="font-semibold">Tel:</span> {patient.telephone}</div>
                <div><span className="font-semibold">Groupe:</span> {patient.groupeSanguin}</div>
                <div><span className="font-semibold">Allergies:</span> {patient.allergies}</div>
              </div>
            </div>
            <div className="flex flex-wrap gap-3">
              <button onClick={() => setShowModal({ open: true, type: 'dossier', patientId: patient.id })} 
                      className="bg-blue-600 text-white font-bold px-6 py-3 rounded-xl hover:bg-blue-700 shadow-lg">
                ðŸ“„ Dossier
              </button>
              <button onClick={() => setShowModal({ open: true, type: 'ordonnance', patientId: patient.id })} 
                      className="bg-purple-600 text-white font-bold px-6 py-3 rounded-xl hover:bg-purple-700 shadow-lg">
                ðŸ’Š Ordonnance
              </button>
              <button onClick={() => setShowModal({ open: true, type: 'rdv', patientId: patient.id })} 
                      className="bg-green-600 text-white font-bold px-6 py-3 rounded-xl hover:bg-green-700 shadow-lg">
                ðŸ“… RDV
              </button>
              <button onClick={() => setShowModal({ open: true, type: 'examen', patientId: patient.id })} 
                      className="bg-orange-600 text-white font-bold px-6 py-3 rounded-xl hover:bg-orange-700 shadow-lg">
                ðŸ©º Examen
              </button>
            </div>
          </div>
        </div>
        <div className="grid lg:grid-cols-2 gap-8">
          <div>
            <DossierResume dossier={patientDossier} ordonnances={patientOrdonnances} />
            <div className="bg-white p-6 rounded-2xl shadow-lg border border-gray-200 mt-6">
              <h3 className="text-xl font-bold text-gray-900 mb-4">ðŸ©º RÃ©sultats rÃ©cents</h3>
              {patientResultats.slice(0, 3).map(r => (
                <div key={r.id} className="p-3 bg-orange-50 rounded-lg mb-2 text-sm">{r.titre}: {r.resultat}</div>
              ))}
            </div>
          </div>
          <div>
            <div className="bg-white p-6 rounded-2xl shadow-lg border border-gray-200">
              <h3 className="text-xl font-bold text-gray-900 mb-4">ðŸ“… Prochains RDV</h3>
              {patientRdv.slice(0, 3).map(r => (
                <div key={r.id} className="p-3 bg-green-50 rounded-lg mb-2 text-sm">{r.date} - {r.type}</div>
              ))}
            </div>
            <OrdonnancesResume ordonnances={patientOrdonnances} />
          </div>
        </div>
      </div>
    );
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-50 to-blue-50">
      <NotificationsMedecin notifications={notifications} />
      
      <aside className="hidden lg:block lg:w-64 lg:h-screen lg:fixed lg:inset-y-0 lg:z-40 bg-white shadow-lg border-r border-gray-200">
        <ProfileMedecin profile={profile} />
        <NavigationMedecin activePath={location.pathname} patientCount={patients.length} rdvCount={rendezVous.length} />
      </aside>

      <main className="lg:ml-64 p-6 lg:p-8 pb-20">
        <Routes>
          <Route path="/" element={<DashboardMedecin profile={profile} patients={patients} rdv={rendezVous} ordonnances={ordonnances.length} />} />
          <Route path="/patients" element={<PatientsPage patients={patients} setShowModal={setShowModal} />} />
          <Route path="/patients/:id" element={<PatientDetailInner />} />
          <Route path="/ordonnances" element={<OrdonnancesPage ordonnances={ordonnances} />} />
          <Route path="/rdv" element={<RdvPage rendezVous={rendezVous} />} />
          <Route path="/examens" element={<ExamensPage resultatsExamens={resultatsExamens} />} />
          <Route path="*" element={<NotFoundPage />} />
        </Routes>
      </main>

      {showModal.open && (
        <div className="fixed inset-0 bg-black/50 z-50 flex items-center justify-center p-4" 
             onClick={() => setShowModal({ open: false, type: null, patientId: null })}>
          <div className="bg-white rounded-2xl shadow-2xl p-8 max-w-2xl w-full max-h-[90vh] overflow-y-auto" 
               onClick={e => e.stopPropagation()}>
            {showModal.type === 'dossier' && <DossierForm patientId={showModal.patientId} onSubmit={createDossier} patients={patients} />}
            {showModal.type === 'ordonnance' && <OrdonnanceForm patientId={showModal.patientId} onSubmit={createOrdonnance} patients={patients} />}
            {showModal.type === 'rdv' && <RdvForm patientId={showModal.patientId} onSubmit={createRdv} patients={patients} />}
            {showModal.type === 'examen' && <ExamenForm patientId={showModal.patientId} onSubmit={addResultatExamen} patients={patients} />}
          </div>
        </div>
      )}
    </div>
  );
};

// âœ… FORMULAIRE ORDONNANCE AMÃ‰LIORÃ‰ avec champ mÃ©dicament libre + mode d'emploi
const OrdonnanceForm = ({ patientId, onSubmit, patients }) => {
  const patient = patients.find(p => p.id === patientId);
  const [formData, setFormData] = useState({ 
    medicament: '', 
    dosage: '', 
    duree: '', 
    modeEmploi: '' 
  });

  return (
    <div>
      <h2 className="text-2xl font-bold text-gray-900 mb-8 flex justify-between items-center">
        ðŸ’Š Nouvelle Ordonnance 
        <button onClick={() => window.location.href = '/medecin/patients'} 
                className="text-xl hover:text-red-500 font-bold">âœ•</button>
      </h2>
      
      <div className="bg-purple-50 p-6 rounded-xl mb-8 border-l-4 border-purple-400">
        <h3 className="font-bold text-lg text-purple-800">Patient: {patient?.prenom} {patient?.nom}</h3>
      </div>
      
      <form onSubmit={(e) => { e.preventDefault(); onSubmit(patientId, formData); }} className="space-y-4">
        {/* âœ… CHAMP MÃ‰DICAMENT LIBRE */}
        <div>
          <label className="block text-sm font-semibold text-gray-700 mb-2">ðŸ’Š MÃ©dicament</label>
          <input 
            type="text" 
            value={formData.medicament} 
            onChange={e => setFormData({...formData, medicament: e.target.value})}
            placeholder="Ex: ParacÃ©tamol 1000mg, Amlodipine 5mg, etc."
            className="w-full p-4 border-2 border-purple-200 rounded-xl focus:border-purple-500 focus:ring-2 focus:ring-purple-200" 
            required 
          />
        </div>
        
        <div className="grid md:grid-cols-2 gap-4">
          <div>
            <label className="block text-sm font-semibold text-gray-700 mb-2">ðŸ“¦ Dosage</label>
            <input 
              type="text" 
              value={formData.dosage} 
              onChange={e => setFormData({...formData, dosage: e.target.value})}
              placeholder="Ex: 1cp 3x/jour"
              className="w-full p-4 border-2 border-gray-200 rounded-xl focus:border-blue-500" 
              required 
            />
          </div>
          <div>
            <label className="block text-sm font-semibold text-gray-700 mb-2">ðŸ•’ DurÃ©e</label>
            <input 
              type="text" 
              value={formData.duree} 
              onChange={e => setFormData({...formData, duree: e.target.value})}
              placeholder="Ex: 7 jours, 1 mois"
              className="w-full p-4 border-2 border-gray-200 rounded-xl focus:border-blue-500" 
              required 
            />
          </div>
        </div>
        
        {/* âœ… CHAMP MODE D'EMPLOI DÃ‰TAILLÃ‰ */}
        <div>
          <label className="block text-sm font-semibold text-gray-700 mb-2">ðŸ“– Mode d'emploi dÃ©taillÃ©</label>
          <textarea 
            rows="4"
            value={formData.modeEmploi} 
            onChange={e => setFormData({...formData, modeEmploi: e.target.value})}
            placeholder="Instructions prÃ©cises pour le patient :
- Prendre aprÃ¨s les repas
- Avec un grand verre d'eau
- Ne pas dÃ©passer 3g/jour
- Stocker Ã  l'abri de l'humiditÃ©
- Ã€ renouveler aprÃ¨s X jours"
            className="w-full p-4 border-2 border-yellow-200 rounded-xl focus:border-yellow-500 focus:ring-2 focus:ring-yellow-200 resize-vertical" 
            required 
          />
        </div>
        
        <button type="submit" 
                className="w-full bg-gradient-to-r from-purple-600 to-purple-700 text-white py-4 rounded-xl font-bold text-lg shadow-lg hover:shadow-xl hover:from-purple-700 hover:to-purple-800 transition-all">
          âš•ï¸ Ã‰mettre l'ordonnance
        </button>
      </form>
    </div>
  );
};

// Tous les autres composants restent identiques...
const NotificationsMedecin = ({ notifications }) => (
  <div className="fixed top-4 right-4 z-50 space-y-2">
    {notifications.slice(-5).map(n => (
      <div key={n.id} className={`p-4 rounded-xl shadow-lg text-white animate-pulse ${
        n.type === 'success' ? 'bg-green-500' : 'bg-blue-500'
      }`}>
        {n.message}
      </div>
    ))}
  </div>
);

const OrdonnancesPage = ({ ordonnances }) => (
  <div className="max-w-6xl mx-auto space-y-8">
    <h2 className="text-3xl font-bold text-gray-900 p-8 bg-white rounded-2xl shadow-lg border">ðŸ’Š Ordonnances ({ordonnances.length})</h2>
    <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-6">
      {ordonnances.map(o => (
        <div key={o.id} className="bg-white p-6 rounded-2xl shadow-lg border hover:shadow-xl transition-all">
          <h3 className="font-bold text-xl text-gray-900 mb-2">{o.medicament}</h3>
          <p className="text-sm text-gray-600 mb-1">ðŸ“¦ {o.dosage}</p>
          <p className="text-sm text-gray-600 mb-3">ðŸ•’ {o.duree}</p>
          <div className="text-xs bg-yellow-50 p-2 rounded-lg mb-3">
            ðŸ“– {o.modeEmploi.substring(0, 80)}...
          </div>
          <span className="px-3 py-1 rounded-full text-xs bg-purple-100 text-purple-800 font-semibold">{o.statut}</span>
        </div>
      ))}
    </div>
  </div>
);

// ... (tous les autres composants restent identiques : ProfileMedecin, NavigationMedecin, etc.)
const ProfileMedecin = ({ profile }) => (
  <div className="p-6 border-b border-gray-200">
    <div className="w-20 h-20 bg-blue-600 text-white rounded-full flex items-center justify-center mx-auto mb-4 font-bold text-xl">ðŸ‘¨â€âš•ï¸</div>
    <h2 className="text-xl font-bold text-gray-900 text-center mb-2">Dr. {profile?.prenom} {profile?.nom}</h2>
    <div className="text-center text-sm bg-blue-50 p-3 rounded-lg">
      <p className="font-semibold text-blue-800">{profile?.specialite}</p>
      <span className="px-3 py-1 rounded-full text-xs bg-green-100 text-green-800">{profile?.statut}</span>
