import React, { useState, useEffect } from 'react';
import axios from 'axios';

const bloodGroups = ['A+', 'A-', 'B+', 'B-', 'AB+', 'AB-', 'O+', 'O-'];

const CreateDossier = () => {
  const [patients, setPatients] = useState([]);
  const [patientId, setPatientId] = useState('');
  const [resumeMedical, setResumeMedical] = useState('');
  const [groupeSanguin, setGroupeSanguin] = useState('');
  const [antecedents, setAntecedents] = useState('');
  const [allergies, setAllergies] = useState('');
  const [contactUrgence, setContactUrgence] = useState({ nom: '', telephone: '', lien: '' });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  // R√©cup√©rer la liste des patients
  useEffect(() => {
    const fetchPatients = async () => {
      try {
        const token = localStorage.getItem('token');
        const res = await axios.get('http://localhost:5000/api/patients', {
          headers: { Authorization: `Bearer ${token}` },
        });
        setPatients(res.data);
      } catch (err) {
        console.error('Erreur lors du chargement des patients', err);
      }
    };
    fetchPatients();
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');

    try {
      const token = localStorage.getItem('token');

      // Transformer les strings en objets { libelle: '...' } pour Mongoose
      const antecedentsObj = antecedents
        .split(',')
        .map((a) => a.trim())
        .filter(Boolean)
        .map((libelle) => ({ libelle }));

      const allergiesObj = allergies
        .split(',')
        .map((a) => a.trim())
        .filter(Boolean)
        .map((libelle) => ({ libelle }));

      const payload = {
        patientId,
        resumeMedical,
        groupeSanguin: groupeSanguin || undefined, // vide devient undefined
        antecedents: antecedentsObj,
        allergies: allergiesObj,
        contactUrgence,
      };

      await axios.post('http://localhost:5000/api/dossiers', payload, {
        headers: { Authorization: `Bearer ${token}` },
      });

      alert('‚úÖ Dossier cr√©√© avec succ√®s');

      // reset formulaire
      setPatientId('');
      setResumeMedical('');
      setGroupeSanguin('');
      setAntecedents('');
      setAllergies('');
      setContactUrgence({ nom: '', telephone: '', lien: '' });
    } catch (err) {
      console.error(err);
      if (err.response?.status === 401) setError('üîê Vous devez √™tre connect√©');
      else if (err.response?.status === 403) setError('‚õî Acc√®s refus√©');
      else setError(err.response?.data?.message || '‚ùå Erreur serveur');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={{ padding: 20 }}>
      <h2>Cr√©er un dossier m√©dical</h2>
      {error && <p style={{ color: 'red' }}>{error}</p>}

      <form onSubmit={handleSubmit}>
        <select value={patientId} onChange={(e) => setPatientId(e.target.value)} required>
          <option value="">-- S√©lectionnez un patient --</option>
          {patients.map((p) => (
            <option key={p._id} value={p._id}>
              {p.fullName} ({p.email})
            </option>
          ))}
        </select>

        <textarea
          placeholder="R√©sum√© m√©dical"
          value={resumeMedical}
          onChange={(e) => setResumeMedical(e.target.value)}
        />

        <select value={groupeSanguin} onChange={(e) => setGroupeSanguin(e.target.value)} required>
          <option value="">-- S√©lectionnez le groupe sanguin --</option>
          {bloodGroups.map((g) => (
            <option key={g} value={g}>{g}</option>
          ))}
        </select>

        <input
          type="text"
          placeholder="Ant√©c√©dents (s√©par√©s par virgule)"
          value={antecedents}
          onChange={(e) => setAntecedents(e.target.value)}
        />

        <input
          type="text"
          placeholder="Allergies (s√©par√©es par virgule)"
          value={allergies}
          onChange={(e) => setAllergies(e.target.value)}
        />

        <input
          type="text"
          placeholder="Contact urgence - Nom"
          value={contactUrgence.nom}
          onChange={(e) => setContactUrgence({ ...contactUrgence, nom: e.target.value })}
        />
        <input
          type="text"
          placeholder="T√©l√©phone urgence"
          value={contactUrgence.telephone}
          onChange={(e) => setContactUrgence({ ...contactUrgence, telephone: e.target.value })}
        />
        <input
          type="text"
          placeholder="Lien (parent, ami...)"
          value={contactUrgence.lien}
          onChange={(e) => setContactUrgence({ ...contactUrgence, lien: e.target.value })}
        />

        <button type="submit" disabled={loading}>
          {loading ? 'Cr√©ation...' : 'Cr√©er le dossier'}
        </button>
      </form>
    </div>
  );
};

export default CreateDossier;
