import React, { useState, useEffect } from 'react';
import { Link, Routes, Route, useLocation, useParams, useNavigate } from 'react-router-dom';
import CreerDossier from './CreerDossier';
import MesPatients from './MesPatients';
import MesDossiers from './MesDossiers';
import MesConsultations from './MesConsultations';
import MesRendezVous from './MesRendezVous';

const MedecinDashboard = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const [profile, setProfile] = useState(null);
  const [stats, setStats] = useState({
    patients: 0,
    dossiers: 0,
    rdvs: 0,
    consultations: 0
  });
  const [loading, setLoading] = useState(true);
  const [notifications, setNotifications] = useState([]);

  useEffect(() => {
    loadDashboardData();
  }, []);

  const loadDashboardData = async () => {
    try {
      setLoading(true);
      
      // Charger le profil du m√©decin
      const profileData = await fetchProfile();
      setProfile(profileData);

      // Charger les stats rapides
      const statsData = await fetchStats();
      setStats(statsData);

      setLoading(false);
    } catch (error) {
      console.error('Erreur chargement dashboard:', error);
      setNotifications(prev => [...prev, {
        id: Date.now(),
        message: '‚ö†Ô∏è Erreur de chargement',
        type: 'error'
      }]);
    } finally {
      setLoading(false);
    }
  };

  // Mock functions - Remplacez par vos vraies API calls
  const fetchProfile = async () => ({
    nom: 'NDIAYE',
    prenom: 'Moustapha',
    email: 'moustapha.ndiaye@govsanitaire.sn',
    telephone: '+221 78 987 6543',
    specialite: 'Cardiologue',
    numeroOrdre: 'SN-MED-2024-001',
    etablissement: 'CHU de Fann',
    statut: 'Valid√©'
  });

  const fetchStats = async () => ({
    patients: 24,
    dossiers: 18,
    rdvs: 5,
    consultations: 12
  });

  const addNotification = (message, type = 'success') => {
    const notif = {
      id: Date.now(),
      message,
      type,
      date: new Date().toLocaleTimeString('fr-FR')
    };
    setNotifications(prev => [notif, ...prev.slice(0, 4)]);
    setTimeout(() => {
      setNotifications(prev => prev.filter(n => n.id !== notif.id));
    }, 5000);
  };

  if (loading) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-slate-50 via-blue-50 to-indigo-100 flex items-center justify-center p-8">
        <div className="text-center max-w-md">
          <div className="w-24 h-24 border-4 border-blue-200 border-t-blue-600 rounded-full animate-spin mx-auto mb-8 shadow-xl"></div>
          <h2 className="text-2xl font-bold text-gray-900 mb-4">Chargement du tableau de bord...</h2>
          <p className="text-gray-600">Pr√©paration de vos donn√©es m√©dicales</p>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-50 via-blue-50 to-indigo-100">
      {/* Notifications Toast */}
      <Notifications notifications={notifications} />

      {/* Sidebar Navigation */}
      <aside className="hidden lg:block lg:w-72 lg:h-screen lg:fixed lg:inset-y-0 lg:z-40 bg-white/80 backdrop-blur-lg shadow-2xl border-r border-blue-100">
        <ProfileCard profile={profile} />
        <NavigationMenu activePath={location.pathname} stats={stats} />
      </aside>

      {/* Mobile Menu Toggle (optionnel) */}
      <div className="lg:hidden fixed top-4 left-4 z-50">
        <button className="p-3 bg-white/90 backdrop-blur-sm rounded-2xl shadow-lg border hover:shadow-xl transition-all">
          ‚ò∞
        </button>
      </div>

      {/* Main Content */}
      <main className="lg:ml-72 p-4 lg:p-8 pb-24 lg:pb-8">
        <Routes>
          <Route path="/" element={<DashboardHome stats={stats} profile={profile} addNotification={addNotification} />} />
          <Route path="/patients" element={<MesPatients />} />
          <Route path="/patients/:id" element={<PatientDetailStub />} />
          <Route path="/dossiers" element={<MesDossiers />} />
          <Route path="/dossier/:id" element={<DossierDetailStub />} />
          <Route path="/creer-dossier" element={<CreerDossier />} />
          <Route path="/consultations" element={<MesConsultations />} />
          <Route path="/rendezvous" element={<MesRendezVous />} />
          <Route path="/ordonnances" element={<OrdonnancesPageStub stats={stats} />} />
          <Route path="*" element={<NotFoundPage />} />
        </Routes>
      </main>
    </div>
  );
};

// Composants optimis√©s et r√©utilisables

const ProfileCard = ({ profile }) => (
  <div className="p-8 border-b border-blue-100 bg-gradient-to-b from-blue-50 to-white">
    <div className="w-20 h-20 bg-gradient-to-br from-blue-500 to-indigo-600 text-white rounded-3xl flex items-center justify-center mx-auto mb-6 shadow-2xl font-bold text-2xl">
      üë®‚Äç‚öïÔ∏è
    </div>
    <h2 className="text-2xl font-bold text-gray-900 text-center mb-3">
      Dr. {profile?.prenom} {profile?.nom}
    </h2>
    <div className="bg-gradient-to-r from-blue-500/10 to-indigo-500/10 p-4 rounded-2xl border border-blue-100 mb-4">
      <p className="font-semibold text-blue-800 text-center text-lg">{profile?.specialite}</p>
      <p className="text-xs text-blue-600 text-center">{profile?.etablissement}</p>
    </div>
    <div className="text-center space-y-1 text-sm">
      <span className="inline-block px-3 py-1 bg-green-100 text-green-800 rounded-full text-xs font-semibold">
        {profile?.statut}
      </span>
      <p className="text-gray-600">{profile?.numeroOrdre}</p>
    </div>
  </div>
);

const NavigationMenu = ({ activePath, stats }) => {
  const navItems = [
    { path: '/medecin', icon: 'üìä', label: 'Dashboard', count: null },
    { path: '/medecin/patients', icon: 'üë•', label: 'Patients', count: stats.patients },
    { path: '/medecin/dossiers', icon: 'üìÅ', label: 'Dossiers', count: stats.dossiers },
    { path: '/medecin/rendezvous', icon: 'üìÖ', label: 'Rendez-vous', count: stats.rdvs },
    { path: '/medecin/consultations', icon: 'ü©∫', label: 'Consultations', count: stats.consultations },
    { path: '/medecin/creer-dossier', icon: '‚ú®', label: 'Nouveau Dossier', count: null },
    { path: '/medecin/ordonnances', icon: 'üíä', label: 'Ordonnances', count: null }
  ];

  return (
    <nav className="p-4 space-y-2 mt-6">
      {navItems.map(({ path, icon, label, count }) => {
        const isActive = activePath === path || activePath.startsWith(path + '/');
        return (
          <Link
            key={path}
            to={path}
            className={`group flex items-center p-4 rounded-2xl font-semibold transition-all duration-300 shadow-sm border hover:shadow-xl hover:-translate-x-1 ${
              isActive
                ? 'bg-gradient-to-r from-blue-500 to-indigo-600 text-white shadow-lg border-transparent'
                : 'bg-white/70 hover:bg-blue-50 text-gray-800 border-gray-200'
            }`}
          >
            <span className={`mr-4 text-2xl transition-transform group-hover:scale-110 ${isActive ? 'text-white' : 'text-blue-600'}`}>
              {icon}
            </span>
            <span className="flex-1">{label}</span>
            {count !== null && (
              <span className={`ml-2 px-3 py-1 rounded-full text-xs font-bold ${
                isActive
                  ? 'bg-white/20 backdrop-blur-sm text-white'
                  : 'bg-blue-100 text-blue-800'
              }`}>
                {count}
              </span>
            )}
          </Link>
        );
      })}
    </nav>
  );
};

const DashboardHome = ({ stats, profile, addNotification }) => (
  <div className="max-w-7xl mx-auto space-y-8">
    {/* Welcome Header */}
    <div className="bg-white/80 backdrop-blur-xl p-8 lg:p-12 rounded-3xl shadow-2xl border border-white/50">
      <div className="flex flex-col lg:flex-row lg:items-center lg:justify-between gap-6">
        <div>
          <h1 className="text-4xl lg:text-5xl font-bold bg-gradient-to-r from-gray-900 to-gray-700 bg-clip-text text-transparent mb-4">
            Bonjour Dr. {profile?.prenom}
          </h1>
          <p className="text-xl text-gray-700 max-w-2xl">
            Bienvenue dans votre tableau de bord m√©dical s√©curis√©. Vous avez{' '}
            <span className="font-bold text-blue-600">{stats.rdvs}</span> rendez-vous √† venir.
          </p>
        </div>
        <Link
          to="/medecin/creer-dossier"
          className="px-10 py-5 bg-gradient-to-r from-emerald-500 to-green-600 text-white font-bold text-lg rounded-2xl shadow-2xl hover:shadow-3xl hover:scale-[1.02] transition-all self-start"
          onClick={() => addNotification('üöÄ Direction cr√©ation de dossier')}
        >
          ‚ú® Nouveau Dossier
        </Link>
      </div>
    </div>

    {/* Stats Cards */}
    <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
      <StatCard icon="üë•" title={stats.patients} label="Patients" color="blue" to="/medecin/patients" />
      <StatCard icon="üìÅ" title={stats.dossiers} label="Dossiers" color="indigo" to="/medecin/dossiers" />
      <StatCard icon="üìÖ" title={stats.rdvs} label="RDV" color="emerald" to="/medecin/rendezvous" />
      <StatCard icon="ü©∫" title={stats.consultations} label="Consultations" color="purple" to="/medecin/consultations" />
    </div>

    {/* Quick Actions */}
    <div className="bg-white/80 backdrop-blur-xl p-8 rounded-3xl shadow-2xl border border-white/50">
      <h3 className="text-2xl font-bold text-gray-900 mb-8">‚ö° Actions rapides</h3>
      <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-6">
        <QuickAction
          icon="üìÑ"
          title="Nouveau Dossier"
          description="Cr√©er dossier m√©dical complet"
          to="/medecin/creer-dossier"
          color="blue"
          onClick={() => addNotification('üìù Formulaire dossier ouvert')}
        />
        <QuickAction
          icon="üë•"
          title="Voir Patients"
          description="G√©rer vos patients"
          to="/medecin/patients"
          color="green"
        />
        <QuickAction
          icon="üìÅ"
          title="Dossiers M√©dicaux"
