import React, { useState, useEffect } from 'react';
import { Link, Routes, Route, useLocation } from 'react-router-dom';

const PatientDashboard = () => {
  const location = useLocation();
  const [profile, setProfile] = useState(null);
  const [prochainsRDV, setProchainsRDV] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showFormRDV, setShowFormRDV] = useState(false);

  // DonnÃ©es mock
  useEffect(() => {
    setTimeout(() => {
      setProfile({
        nom: 'Aminata DIOP', prenom: 'Aminata', email: 'aminata.diop@email.com',
        telephone: '+221 78 123 4567', age: 35, sexe: 'FÃ©minin', 
        groupeSanguin: 'O+', allergies: 'Aucune connue'
      });
      setProchainsRDV([
        { _id: '1', medecin: { nom: 'Dr. Moustapha NDIAYE' }, specialite: 'Cardiologue', 
          date: '15/02/2026 14:00', statut: 'ConfirmÃ©' }
      ]);
      setLoading(false);
    }, 1000);
  }, []);

  if (loading) {
    return <LoadingScreen />;
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 via-indigo-50 to-purple-50">
      {/* Sidebar */}
      <aside className="hidden lg:block lg:w-72 lg:h-screen lg:fixed lg:inset-y-0 lg:z-40 bg-white/95 backdrop-blur-xl shadow-2xl border-r-4 border-blue-200/60">
        <ProfileSidebar profile={profile} />
        <Navigation activePath={location.pathname} />
      </aside>

      {/* Contenu principal */}
      <main className={`lg:ml-72 p-6 lg:p-8 pb-24 transition-all duration-300 ${showFormRDV ? 'lg:ml-72 overflow-hidden' : ''}`}>
        <Routes>
          <Route path="/" element={<DashboardHome profile={profile} rdv={prochainsRDV} />} />
          <Route path="/rdv" element={<RDVPage rdv={prochainsRDV} />} />
          <Route path="/rdv/nouveau" element={<NouveauRDVForm setShowForm={setShowFormRDV} />} />
          <Route path="/dossier" element={<DossierPage />} />
          <Route path="/ordonnances" element={<OrdonnancesPage />} />
          <Route path="*" element={<NotFoundPage />} />
        </Routes>
      </main>

      {/* Overlay Formulaire Modal */}
      {showFormRDV && (
        <div className="fixed inset-0 bg-black/50 backdrop-blur-sm z-50 lg:hidden flex items-center justify-center p-4">
          <NouveauRDVFormMobile setShowForm={setShowFormRDV} />
        </div>
      )}
    </div>
  );
};

// Composants
const ProfileSidebar = ({ profile }) => (
  <div className="p-8 sticky top-0 bg-gradient-to-b from-blue-50/90 to-white/80 border-b-2 border-blue-100/50">
    <div className="w-24 h-24 bg-gradient-to-br from-blue-500 to-indigo-600 rounded-2xl flex items-center justify-center mx-auto mb-6 shadow-xl ring-4 ring-white/50">
      <span className="text-3xl text-white">ðŸ‘¤</span>
    </div>
    <h2 className="text-3xl font-black text-gray-900 text-center mb-4">{profile?.nom}</h2>
    <div className="text-center text-xl space-y-1 bg-blue-50/80 p-4 rounded-xl border border-blue-200/50">
      <p className="font-bold text-blue-700">{profile?.age} ans â€¢ {profile?.sexe}</p>
      <p className="text-blue-600 text-sm">{profile?.groupeSanguin} â€¢ {profile?.allergies}</p>
    </div>
  </div>
);

const Navigation = ({ activePath }) => (
  <nav className="p-6 mt-8 space-y-3 h-[calc(100vh-300px)] overflow-y-auto">
    <NavLink href="/patient" icon="ðŸ " label="Dashboard" active={activePath === '/patient'} color="blue" />
    <NavLink href="/patient/rdv" icon="ðŸ“…" label="Rendez-vous" active={activePath === '/patient/rdv'} color="emerald" />
    <NavLink href="/patient/dossier" icon="ðŸ“„" label="Dossier" active={activePath === '/patient/dossier'} color="purple" />
    <NavLink href="/patient/ordonnances" icon="ðŸ’Š" label="Ordonnances" active={activePath === '/patient/ordonnances'} color="orange" />
  </nav>
);

const NavLink = ({ href, icon, label, active, color }) => (
  <Link to={href} className={`flex items-center p-6 rounded-3xl w-full font-bold text-xl transition-all duration-300 group hover:shadow-xl hover:-translate-x-2 border-2 ${
    active ? `bg-gradient-to-r from-${color}-500 to-${color}-600 text-white shadow-2xl shadow-${color}-200` : `text-gray-700 hover:bg-${color}-50 hover:border-${color}-200`
  }`}>
    <span className={`mr-6 text-3xl ${active ? 'text-white' : ''}`}>{icon}</span>
    {label}
  </Link>
);

const DashboardHome = ({ profile, rdv }) => (
  <div className="max-w-7xl mx-auto space-y-16">
    <section className="bg-white/90 backdrop-blur-xl rounded-4xl shadow-2xl border-4 border-blue-200/50 p-12 lg:p-16 text-center">
