import React from 'react';
import { Link, Routes, Route, useLocation } from 'react-router-dom';
import { 
  usePatientProfile, 
  usePatientRDV, 
  usePatientDossier, 
  usePatientOrdonnances 
} from '../hooks/usePatientData';

const PatientDashboard = () => {
  const location = useLocation();
  const { profile, loading: profileLoading } = usePatientProfile();
  const { rdv: prochainsRDV, loading: rdvLoading } = usePatientRDV();
  const { dossier, loading: dossierLoading } = usePatientDossier();
  const { ordonnances, loading: ordonnancesLoading } = usePatientOrdonnances();

  if (profileLoading) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-blue-50 to-indigo-100 flex items-center justify-center p-8">
        <div className="text-center max-w-md">
          <div className="w-28 h-28 border-4 border-blue-200 border-t-blue-500 rounded-full animate-spin mx-auto mb-12"></div>
          <h1 className="text-5xl font-black text-gray-900 mb-6 animate-pulse">Chargement...</h1>
          <p className="text-2xl text-blue-600 font-semibold">R√©cup√©ration de votre dossier patient</p>
          <div className="mt-8 space-y-2">
            <div className="h-2 bg-blue-200 rounded-full animate-pulse"></div>
            <div className="h-2 bg-blue-200 rounded-full animate-pulse w-3/4"></div>
            <div className="h-2 bg-blue-200 rounded-full animate-pulse w-1/2"></div>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 via-indigo-50 to-purple-50">
      {/* Sidebar Navigation */}
      <div className="hidden lg:block lg:w-80 lg:fixed lg:inset-y-0 lg:z-50 bg-white/95 backdrop-blur-2xl shadow-2xl border-r-4 border-blue-200/50 h-screen overflow-y-auto">
        <div className="p-8 border-b-4 border-blue-100/50 bg-gradient-to-b from-blue-50/80 to-white/60 sticky top-0 z-10 backdrop-blur-xl">
          <div className="w-28 h-28 bg-gradient-to-br from-blue-500 via-indigo-500 to-purple-500 rounded-3xl flex items-center justify-center mx-auto mb-8 shadow-2xl ring-4 ring-white/50 hover:scale-105 transition-all duration-300">
            <span className="text-4xl text-white drop-shadow-lg">üë§</span>
          </div>
          <h2 className="text-4xl font-black text-gray-900 text-center mb-6 leading-tight break-words">
            {profile?.nom} {profile?.prenom}
          </h2>
          <div className="text-center space-y-2 p-6 bg-gradient-to-r from-blue-50/80 to-indigo-50/80 rounded-3xl backdrop-blur-sm border border-blue-200/50">
            <p className="text-2xl font-bold text-blue-700">
              {profile?.age} ans ‚Ä¢ {profile?.sexe}
            </p>
            <div className="flex items-center justify-center gap-4 text-xl text-gray-600 flex-wrap">
              <span className="font-semibold text-lg">{profile?.groupeSanguin}</span>
              <span>‚Ä¢</span>
              <span className="text-sm bg-gray-100 px-3 py-1 rounded-full">{profile?.allergies || 'Aucune'}</span>
            </div>
          </div>
        </div>
        
        <nav className="p-8 space-y-4 mt-8">
          {[
            { path: '/patient', icon: 'üè†', label: 'Dashboard', color: 'blue' },
            { path: '/patient/rdv', icon: 'üìÖ', label: 'Rendez-vous', color: 'emerald' },
            { path: '/patient/dossier', icon: 'üìÑ', label: 'Dossier m√©dical', color: 'purple' },
            { path: '/patient/ordonnances', icon: 'üíä', label: 'Ordonnances', color: 'orange' }
          ].map(({ path, icon, label, color }, i) => (
            <Link 
              key={i}
              to={path}
              className={`flex items-center p-8 rounded-3xl w-full transition-all duration-500 font-bold text-2xl group hover:shadow-2xl hover:-translate-x-4 hover:scale-[1.02] border-2 ${
                location.pathname === path 
                  ? `bg-gradient-to-r from-${color}-500 to-${color}-600 text-white shadow-3xl shadow-${color}-200/50 ring-4 ring-${color}-300/30 scale-105`
                  : `text-gray-700 hover:bg-gradient-to-r hover:from-${color}-50 hover:to-${color}-100 hover:border-${color}-200 hover:shadow-xl`
              }`}
            >
              <span className="mr-8 text-4xl group-hover:scale-110 transition-transform duration-300">{icon}</span>
              <span className="flex-1">{label}</span>
              {location.pathname === path && (
                <span className="w-3 h-3 bg-white rounded-full animate-ping"></span>
              )}
            </Link>
          ))}
        </nav>
      </div>

      {/* Contenu principal */}
      <main className="lg:ml-80 p-8 pb-24 transition-all duration-500">
        <Routes>
          {/* DASHBOARD PRINCIPAL */}
          <Route path="/" element={
            <DashboardHome profile={profile} prochainsRDV={prochainsRDV} dossier={dossier} ordonnances={ordonnances} />
          } />
          <Route path="/rdv" element={<RDVPage rdv={prochainsRDV} />} />
          <Route path="/dossier" element={<DossierPage dossier={dossier} />} />
          <Route path="/ordonnances" element={<OrdonnancesPage ordonnances={ordonnances} />} />
          <Route path="*" element={<NotFound />} />
        </Routes>
      </main>
    </div>
  );
};

// Composants pages
const DashboardHome = ({ profile, prochainsRDV, dossier, ordonnances }) => (
  <div className="space-y-20">
    {/* Header */}
    <div className="bg-white/90 backdrop-blur-xl rounded-4xl shadow-3xl border-4 border-blue-200/50 p-16 max-w-7xl mx-auto">
      <div className="text-center mb-20">
        <h1 className="text-7xl font-black bg-gradient-to-r from-gray-900 via-blue-900 to-indigo-900 bg-clip-text text-transparent mb-8 leading-tight">
          Bienvenue {profile?.nom} üëã
        </h1>
        <p className="text-4xl text-blue-700 font-semibold bg-blue-50/50 px-8 py-4 rounded-3xl inline-block backdrop-blur-sm">
          {profile?.email}
        </p>
      </div>
      
      {/* Stats rapides */}
      <div className="grid grid-cols-1 md:grid-cols-2 xl:grid-cols-4 gap-10">
        <StatCard icon="üì±" title={profile?.telephone || 'Non renseign√©'} subtitle="Contact" color="blue" />
        <StatCard icon="ü©∫" title={profile?.groupeSanguin || 'NR'} subtitle="Groupe sanguin" color="emerald" />
        <StatCard icon="‚ö†Ô∏è" title={profile?.allergies || 'Aucune'} subtitle="Allergies" color="purple" />
        <StatCard icon="‚úì" title="V√©rifi√©" subtitle="Compte" color="emerald" verified />
      </div>
    </div>

    {/* RDV + Sant√© */}
    <div className="grid grid-cols-1 lg:grid-cols-2 gap-12 max-w-7xl mx-auto">
      <RDVSection rdv={prochainsRDV} />
      <ResumeSanteSection dossier={dossier} ordonnances={ordonnances} />
    </div>

    {/* Actions */}
    <ActionsGrid prochainsRDV={prochainsRDV} ordonnances={ordonnances} />
  </div>
);

const StatCard = ({ icon, title, subtitle, color, verified }) => (
  <div className={`group p-14 bg-gradient-to-br from-${color}-50 to-${color}-100 rounded-4xl border-4 border-${color}-100/50 hover:shadow-4xl hover:-translate-y-4 transition-all duration-500 cursor-pointer backdrop-blur-xl`}>
    <div className={`text-7xl mb-8 group-hover:scale-110 transition-transform`}>{verified ? <VerifiedIcon /> : icon}</div>
    <p className={`text-6xl font-black text-${color}-800 mb-6 leading-tight break-words group-hover:text-${color}-900 transition-all`}>{title}</p>
    <p className={`text-3xl font-bold text-${color}-600`}>{subtitle}</p>
  </div>
);

const VerifiedIcon = () => (
  <div className="w-32 h-32 bg-gradient-to-br from-emerald-400 to-teal-500 rounded-4xl flex items-center justify-center shadow-2xl group-hover:scale-110 transition-all">
    <span className="text-5xl text-white font-bold">‚úì</span>
  </div>
);

const RDVSection = ({ rdv }) => (
  <div className="bg-white/90 backdrop-blur-xl rounded-4xl shadow-3xl border-4 border-emerald-200/50 p-16 order-2 lg:order-1">
    <h2 className="text-6xl font-black text-gray-900 mb-16 flex items-center gap-8">
      üìÖ Rendez-vous √† venir
      <span className="text-4xl bg-emerald-100 text-emerald-800 px-10 py-6 rounded-4xl font-bold shadow-2xl">
        {rdv?.length || 0}
      </span>
    </h2>
    <div className="space-y-8">
      {rdv?.map(item => (
        <div key={item._id} className="group p-12 bg-gradient-to-r from-emerald-50/80 to-blue-50/80 rounded-4xl border-4 border-emerald-200/50 hover:shadow-4xl hover:-translate-y-4 transition-all backdrop-blur-xl cursor-pointer">
          {/* Contenu RDV */}
          <p>Rendez-vous {item.medecin?.nom}</p>
        </div>
      ))}
    </div>
  </div>
);

const ResumeSanteSection = ({ dossier, ordonnances }) => (
  <div className="bg-white/90 backdrop-blur-xl rounded-4xl shadow-3xl border-4 border-purple-200/50 p-16 order-1 lg:order-2">
    <h2 className="text-6xl font-black text-gray-900 mb-16">üìä R√©sum√© sant√©</h2>
    {/* Contenu sant√© */}
  </div>
);

const ActionsGrid = ({ prochainsRDV, ordonnances }) => (
  <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-12 max-w-7xl mx-auto">
    <ActionCard to="/patient/rdv/nouveau" icon="üìÖ" title="Nouveau RDV" subtitle="Prendre rendez-vous" color="emerald" />
    <ActionCard to="/patient/dossier" icon="üìÑ" title="Dossier m√©dical" subtitle="Lecture seule" color="purple" />
    <ActionCard to="/patient/ordonnances" icon="üíä" title={`${ordonnances?.filter(o => o.statut === 'Active').length || 0}`} subtitle="Ordonnances actives" color="orange" />
  </div>
);

const ActionCard = ({ to, icon, title, subtitle, color }) => (
  <Link to={to} className={`group relative p-20 bg-gradient-to-br from-${color}-500 to-${color}-600 text-white rounded-4xl shadow-3xl hover:shadow-4xl hover:scale-[1.05] transition-all duration-700 overflow-hidden border-4 border-transparent hover:border-${color}-400/50`}>
    <div className="absolute inset-0 bg-white/20 backdrop-blur-sm opacity-0 group-hover:opacity-100 transition-all duration-500"></div>
    <span className="text-8xl block mb-12 relative z-10 group-hover:scale-125 transition-transform duration-500">{icon}</span>
    <h3 className="text-6xl font-black mb-8 relative z-10">{title}</h3>
    <p className="text-4xl opacity-95 relative z-10 font-semibold">{subtitle}</p>
  </Link>
);

const RDVPage = ({ rdv }) => <div className="p-20 bg-white/90 max-w-6xl mx-auto rounded-4xl shadow-3xl"><h1 className="text-7xl font-black text-gray-900 mb-20 text-center">üìÖ Rendez-vous</h1></div>;
const DossierPage = ({ dossier }) => <div className="p-20 bg-white/90 max-w-6xl mx-auto rounded-4xl shadow-3xl"><h1 className="text-7xl font-black text-gray-900 mb-20 text-center">üìÑ Dossier m√©dical</h1></div>;
const OrdonnancesPage = ({ ordonnances }) => <div className="p-20 bg-white/90 max-w-6xl mx-auto rounded-4xl shadow-3xl"><h1 className="text-7xl font-black text-gray-900 mb-20 text-center">üíä Ordonnances</h1></div>;
const NotFound = () => (
  <div className="max-w-4xl mx-auto p-32 bg-white/50 backdrop-blur-xl rounded-4xl shadow-2xl text-center">
    <h1 className="text-7xl font-black text-gray-400 mb-16">Page non trouv√©e</h1>
    <Link to="/patient" className="px-20 py-12 bg-blue-600 text-white font-black text-4xl rounded-4xl hover:bg-blue-700 shadow-3xl hover:shadow-4xl transition-all inline-block">
      ‚Üê Retour Dashboard
    </Link>
  </div>
);

export default PatientDashboard;
