// src/pages/Composants/MyDossiers.jsx
import React, { useEffect, useState } from "react";
import { getData } from "../../api/endpoint"; // <-- chemin corrigé

const MyDossiers = () => {
  const [dossiers, setDossiers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  const fetchDossiers = async () => {
    try {
      const res = await getData("/patient/dossiers"); // endpoint backend
      setDossiers(res);
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchDossiers();
  }, []);

  if (loading) return <p>Chargement des dossiers...</p>;
  if (error) return <p style={{ color: "red" }}>{error}</p>;
  if (dossiers.length === 0) return <p>Aucun dossier trouvé.</p>;

  return (
    <div style={{ maxWidth: 800, margin: "20px auto" }}>
      <h2>Mes Dossiers Médicaux</h2>
      {dossiers.map((dossier) => (
        <div key={dossier._id} style={{ border: "1px solid gray", padding: "15px", marginBottom: "10px", borderRadius: "8px" }}>
          <p><strong>Médecin :</strong> {dossier.medecin.fullName} ({dossier.medecin.email})</p>
          <p><strong>Résumé médical :</strong> {dossier.resumeMedical}</p>
          <p><strong>Groupe sanguin :</strong> {dossier.groupeSanguin}</p>
          <p><strong>Antécédents :</strong> {dossier.antecedents.map(a => a.libelle).join(", ")}</p>
          <p><strong>Allergies :</strong> {dossier.allergies.map(a => a.libelle).join(", ")}</p>
          <p><strong>Contact urgence :</strong> {dossier.contactUrgence.nom} - {dossier.contactUrgence.telephone} ({dossier.contactUrgence.lien})</p>
          <p><strong>Statut :</strong> {dossier.statut}</p>
        </div>
      ))}
    </div>
  );
};

export default MyDossiers;
