import React, { useEffect, useState } from 'react';
import { getData } from '../api/endpoint';

const MedecinDossiers = () => {
  const [dossiers, setDossiers] = useState([]);
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchDossiers = async () => {
      setLoading(true);
      setError('');

      try {
        const data = await getData('/api/dossiers');
        setDossiers(Array.isArray(data) ? data : []);
      } catch (err) {
        console.error('Erreur lors du chargement des dossiers :', err);

        // üëâ Gestion propre des erreurs venant du backend
        if (err?.message?.includes('Acc√®s refus√©')) {
          setError("‚õî Acc√®s refus√©. Cette section est r√©serv√©e aux administrateurs.");
        } else if (err?.status === 403) {
          setError("‚õî Vous n'avez pas les droits n√©cessaires.");
        } else {
          setError("‚ùå Impossible de charger les dossiers pour le moment.");
        }
      } finally {
        setLoading(false);
      }
    };

    fetchDossiers();
  }, []);

  // ‚è≥ Chargement
  if (loading) {
    return (
      <div style={{ padding: '20px' }}>
        <p>‚è≥ Chargement des dossiers m√©dicaux...</p>
      </div>
    );
  }

  return (
    <div style={{ padding: '20px' }}>
      <h2>ü©∫ Dossiers des patients</h2>

      {/* ‚ùå Erreur */}
      {error && (
        <div
          style={{
            backgroundColor: '#fdecea',
            color: '#b71c1c',
            padding: '15px',
            borderRadius: '6px',
            borderLeft: '5px solid #b71c1c',
            marginBottom: '20px',
          }}
        >
          {error}
        </div>
      )}

      {/* üìÇ Aucun dossier */}
      {!error && dossiers.length === 0 && (
        <p>üì≠ Aucun dossier m√©dical disponible.</p>
      )}

      {/* ‚úÖ Liste des dossiers */}
      {!error &&
        dossiers.map((dossier) => (
          <div
            key={dossier._id}
            style={{
              border: '1px solid #ddd',
              borderRadius: '10px',
              padding: '16px',
              marginBottom: '14px',
              backgroundColor: '#f9fbff',
            }}
          >
            <p><strong>üë§ Patient :</strong> {dossier.patientName || '‚Äî'}</p>
            <p><strong>üìã Motif :</strong> {dossier.motif || '‚Äî'}</p>
            <p><strong>üî¨ Diagnostic :</strong> {dossier.diagnostic || '‚Äî'}</p>
          </div>
        ))}
    </div>
  );
};

export default MedecinDossiers;
