import React, { useState, useEffect } from 'react';
import axios from 'axios';

// ‚úÖ Configuration Axios avec token automatique
const api = axios.create({
  baseURL: process.env.REACT_APP_API_BASE_URL || 'http://localhost:5000/api',
});

// Intercepteur pour ajouter le token automatiquement
api.interceptors.request.use((config) => {
  const token = localStorage.getItem('token');
  if (token) {
    config.headers.Authorization = `Bearer ${token}`;
  }
  config.headers['Content-Type'] = 'application/json';
  return config;
});

const CreateDossier = () => {
  const [formData, setFormData] = useState({
    patientId: '',
    motif: '',
    description: ''
  });
  const [patients, setPatients] = useState([]);
  const [loading, setLoading] = useState(false);
  const [loadingPatients, setLoadingPatients] = useState(true);
  const [error, setError] = useState('');
  const [selectedPatient, setSelectedPatient] = useState(null);

  // ‚úÖ CHARGEMENT PATIENTS avec Axios
  useEffect(() => {
    const fetchPatients = async () => {
      try {
        setLoadingPatients(true);
        const response = await api.get('/patients');
        console.log('üë• Patients:', response.data);
        setPatients(response.data);
      } catch (error) {
        console.error('‚ùå Patients:', error.response?.data || error.message);
        setError('Erreur chargement patients');
      } finally {
        setLoadingPatients(false);
      }
    };
    fetchPatients();
  }, []);

  // ‚úÖ D√âTAIL PATIENT S√âLECTIONN√â
  useEffect(() => {
    const patient = patients.find(p => p._id === formData.patientId);
    setSelectedPatient(patient);
  }, [formData.patientId, patients]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
    setError('');
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!formData.patientId || !formData.motif) {
      setError('‚ùå Patient et motif requis');
      return;
    }

    try {
      setLoading(true);
      console.log('üì§ Envoi:', formData);
      
      const response = await api.post('/dossiers', formData);
      console.log('‚úÖ Cr√©√©:', response.data);
      
      alert('‚úÖ Dossier cr√©√© ! ID: ' + response.data.dossierId);
      
      // Reset partiel
      setFormData(prev => ({
        ...prev,
        motif: '',
        description: ''
      }));
      setError('');
      
    } catch (error) {
      console.error('‚ùå Erreur:', error.response?.data || error.message);
      
      // Gestion erreurs backend pr√©cises
      const message = error.response?.data?.message || error.message || 'Erreur cr√©ation';
      setError(message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={{ 
      padding: '20px', 
      maxWidth: '700px', 
      margin: '0 auto',
      fontFamily: 'system-ui, -apple-system, sans-serif'
    }}>
      <h2 style={{ color: '#1a365d', marginBottom: '30px' }}>
        üìÅ Nouveau dossier m√©dical
      </h2>

      {error && (
        <div style={{
          backgroundColor: '#fee2e2',
          color: '#dc2626',
          padding: '15px 20px',
          borderRadius: '8px',
          marginBottom: '20px',
          borderLeft: '4px solid #dc2626',
          fontWeight: '500'
        }}>
          {error}
        </div>
      )}

      <form onSubmit={handleSubmit}>
        {/* PATIENT */}
        <div style={{ marginBottom: '25px' }}>
          <label style={{ 
            display: 'block', 
            marginBottom: '10px', 
            fontWeight: 'bold', 
            fontSize: '16px',
            color: '#374151'
          }}>
            üë§ Patient <span style={{ color: '#dc2626' }}>*</span>
          </label>
          
          {loadingPatients ? (
            <div style={{ 
              padding: '20px', 
              textAlign: 'center', 
              color: '#6b7280',
              backgroundColor: '#f9fafb',
              borderRadius: '8px'
            }}>
              ‚è≥ Chargement patients...
            </div>
          ) : patients.length === 0 ? (
            <div style={{ 
              padding: '20px', 
              color: '#dc2626', 
              textAlign: 'center',
              backgroundColor: '#fef2f2',
              borderRadius: '8px'
            }}>
              ‚ùå Aucun patient trouv√©
            </div>
          ) : (
            <>
              <select
                name="patientId"
                value={formData.patientId}
                onChange={handleChange}
                required
                style={{
                  width: '100%',
                  padding: '15px',
                  border: `2px solid ${formData.patientId ? '#10b981' : '#d1d5db'}`,
                  borderRadius: '8px',
                  fontSize: '16px',
                  backgroundColor: 'white',
                  transition: 'all 0.2s',
                  appearance: 'none',
                  backgroundImage: 'url("data:image/svg+xml,%3csvg xmlns=\'http://www.w3.org/2000/svg\' fill=\'none\' viewBox=\'0 0 20 20\'%3e%3cpath stroke=\'%236b7280\' stroke-linecap=\'round\' stroke-linejoin=\'round\' stroke-width=\'1.5\' d=\'m6 8 4 4 4-4\'/%3e%3c/svg%3e")',
                  backgroundPosition: 'right 12px center',
                  backgroundRepeat: 'no-repeat',
                  backgroundSize: '16px'
                }}
              >
                <option value="">üîç S√©lectionnez un patient...</option>
                {patients.map((patient) => (
                  <option key={patient._id} value={patient._id}>
                    {patient.nom?.toUpperCase()} {patient.prenom} 
                    {patient.telephone && ` ‚Ä¢ üìû ${patient.telephone}`}
                  </option>
                ))}
              </select>

              {selectedPatient && (
                <div style={{
                  marginTop: '12px',
                  padding: '12px 16px',
                  backgroundColor: '#d1fae5',
                  borderRadius: '6px',
                  borderLeft: '4px solid #10b981',
                  fontSize: '14px',
                  color: '#065f46'
                }}>
                  ‚úÖ S√©lectionn√©: <strong>{selectedPatient.nom} {selectedPatient.prenom}</strong>
                  {selectedPatient.telephone && ` ‚Ä¢ ${selectedPatient.telephone}`}
                </div>
              )}
            </>
          )}
        </div>

        {/* MOTIF */}
        <div style={{ marginBottom: '20px' }}>
          <label style={{ 
            display: 'block', 
            marginBottom: '8px', 
            fontWeight: 'bold',
            color: '#374151'
          }}>
            üìã Motif de consultation <span style={{ color: '#dc2626' }}>*</span>
          </label>
          <input
            type="text"
            name="motif"
            placeholder="Ex: Consultation annuelle, Douleurs abdominales..."
            value={formData.motif}
            onChange={handleChange}
            required
            style={{
              width: '100%',
              padding: '15px',
              border: `2px solid ${formData.motif ? '#10b981' : '#d1d5db'}`,
              borderRadius: '8px',
              fontSize: '16px',
              transition: 'all 0.2s'
            }}
          />
        </div>

        {/* DESCRIPTION */}
        <div style={{ marginBottom: '30px' }}>
          <label style={{ 
            display: 'block', 
            marginBottom: '8px', 
            fontWeight: 'bold',
            color: '#374151'
          }}>
            üìù Description d√©taill√©e
          </label>
          <textarea
            name="description"
            placeholder="Sympt√¥mes, ant√©c√©dents, observations..."
            value={formData.description}
            onChange={handleChange}
            rows={4}
            style={{
              width: '100%',
              padding: '15px',
              border: '2px solid #d1d5db',
              borderRadius: '8px',
              fontSize: '16px',
              fontFamily: 'inherit',
              resize: 'vertical',
              lineHeight: '1.5',
              transition: 'border-color 0.2s'
            }}
          />
        </div>

        {/* BOUTON */}
        <button 
          type="submit" 
          disabled={loading || !formData.patientId || !formData.motif}
          style={{
            width: '100%',
            padding: '18px',
            backgroundColor: loading 
              ? '#9ca3af' 
              : (!formData.patientId || !formData.motif ? '#6b7280' : '#10b981'),
            color: 'white',
            border: 'none',
            borderRadius: '10px',
            fontSize: '18px',
            fontWeight: 'bold',
            cursor: loading ? 'not-allowed' : 'pointer',
            transition: 'all 0.2s'
          }}
        >
          {loading ? '‚è≥ Cr√©ation...' : 
           !formData.patientId ? 'üë§ S√©lectionnez un patient' :
           !formData.motif ? 'üìã Ajoutez un motif' : 
           '‚úÖ Cr√©er le dossier'}
        </button>
      </form>

      {/* DEBUG */}
      {process.env.NODE_ENV === 'development' && (
        <details style={{ 
          marginTop: '25px', 
          padding: '15px', 
          background: '#f8fafc', 
          borderRadius: '8px',
          border: '1px solid #e2e8f0'
        }}>
          <summary style={{ cursor: 'pointer', fontWeight: 'bold', color: '#475569' }}>
            üîç Debug
          </summary>
          <pre style={{ 
            fontSize: '12px', 
            maxHeight: '200px', 
            overflow: 'auto',
            marginTop: '10px'
          }}>
{JSON.stringify({ 
  patientsCount: patients.length, 
  selectedId: formData.patientId,
  formData 
}, null, 2)}
          </pre>
        </details>
      )}
    </div>
  );
};

export default CreateDossier;
