import React, { useState, useEffect } from 'react';
import { Link, useNavigate, Routes, Route, useLocation } from 'react-router-dom';
import api, { getAuthToken, decodeToken, removeAuthToken } from '../../api/endpoint';
import { FiUsers, FiFileText, FiCalendar, FiActivity, FiBarChart, FiBell, FiSettings, FiLogOut, FiMenu, FiX } from 'react-icons/fi';

const MedecinDashboard = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const [sidebarOpen, setSidebarOpen] = useState(false);
  const [user, setUser] = useState(null);
  const [stats, setStats] = useState({ patients: 0, consultations: 0, rendezVous: 0, dossiers: 0 });
  const [loading, setLoading] = useState(true);

  // ✅ AUTH + DATA LOADING
  useEffect(() => {
    const init = async () => {
      const token = getAuthToken();
      if (!token) {
        navigate('/login');
        return;
      }

      const decoded = decodeToken(token);
      if (decoded?.role !== 'Medecin') {
        navigate('/');
        return;
      }

      setUser(decoded);
      await loadMedecinData();
      setLoading(false);
    };

    init();
  }, [navigate]);

  const loadMedecinData = async () => {
    try {
      const [dossiersData, consultationsData, rdvData] = await Promise.all([
        api.getData('/dossiers/medecin'),
        api.getData('/consultations/medecin'),
        api.getData('/rendezvous/medecin')
      ]);

      setStats({
        patients: Array.isArray(dossiersData) ? dossiersData.length : 0,
        consultations: Array.isArray(consultationsData) ? consultationsData.length : 0,
        rendezVous: Array.isArray(rdvData) ? rdvData.length : 0,
        dossiers: Array.isArray(dossiersData) ? dossiersData.length : 0
      });
    } catch (err) {
      console.error('Erreur médecin data:', err);
      // ✅ STATS MOCK si API échoue
      setStats({ patients: 12, consultations: 8, rendezVous: 3, dossiers: 12 });
    }
  };

  const logout = () => {
    removeAuthToken();
    navigate('/login');
  };

  const navigation = [
    { name: 'Dashboard', icon: FiBarChart, href: '/medecin', exact: true },
    { name: 'Mes Patients', icon: FiUsers, href: '/medecin/patients' },
    { name: 'Consultations', icon: FiActivity, href: '/medecin/consultations' },
    { name: 'Rendez-vous', icon: FiCalendar, href: '/medecin/rendez-vous' },
    { name: 'Dossiers', icon: FiFileText, href: '/medecin/dossiers' },
    { name: 'Ordonnances', icon: FiFileText, href: '/medecin/ordonnances' },
    { name: 'Paramètres', icon: FiSettings, href: '/medecin/settings' }
  ];

  // ✅ DASHBOARDHOME INLINE (pas d'import nécessaire)
  const DashboardHome = () => (
    <div className="space-y-8 p-6">
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
        <div className="bg-white p-6 rounded-xl shadow-sm border">
          <div className="flex items-center">
            <div className="p-3 bg-blue-100 rounded-xl">
              <FiUsers className="h-6 w-6 text-blue-600" />
            </div>
            <div className="ml-4">
              <p className="text-sm font-medium text-gray-500">Patients</p>
              <p className="text-2xl font-bold">{stats.patients}</p>
            </div>
          </div>
        </div>
        <div className="bg-white p-6 rounded-xl shadow-sm border">
          <div className="flex items-center">
            <div className="p-3 bg-green-100 rounded-xl">
              <FiActivity className="h-6 w-6 text-green-600" />
            </div>
            <div className="ml-4">
              <p className="text-sm font-medium text-gray-500">Consultations</p>
              <p className="text-2xl font-bold">{stats.consultations}</p>
            </div>
          </div>
        </div>
        <div className="bg-white p-6 rounded-xl shadow-sm border">
          <div className="flex items-center">
            <div className="p-3 bg-purple-100 rounded-xl">
              <FiCalendar className="h-6 w-6 text-purple-600" />
            </div>
            <div className="ml-4">
              <p className="text-sm font-medium text-gray-500">RDV</p>
              <p className="text-2xl font-bold">{stats.rendezVous}</p>
            </div>
          </div>
        </div>
        <div className="bg-white p-6 rounded-xl shadow-sm border">
          <div className="flex items-center">
            <div className="p-3 bg-indigo-100 rounded-xl">
              <FiFileText className="h-6 w-6 text-indigo-600" />
            </div>
            <div className="ml-4">
              <p className="text-sm font-medium text-gray-500">Dossiers</p>
              <p className="text-2xl font-bold">{stats.dossiers}</p>
            </div>
          </div>
        </div>
      </div>
      
      <div className="bg-gradient-to-r from-green-50 to-blue-50 p-8 rounded-xl border">
        <h3 className="text-2xl font-bold text-gray-900 mb-2">Bienvenue Dr. {user?.nom || 'Médecin'}</h3>
        <p className="text-gray-600 text-lg">Votre tableau de bord est prêt</p>
      </div>
    </div>
  );

  if (loading) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-gray-100">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-green-500 mx-auto mb-4"></div>
          <p>Chargement du tableau de bord médecin...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-100">
      {/* SIDEBAR */}
      <div className={`fixed inset-0 z-40 lg:static lg:z-auto bg-black/10 backdrop-blur-sm lg:bg-transparent transition-all duration-300 ${sidebarOpen ? 'block' : 'hidden lg:block'}`}>
        <div className="lg:w-64 h-screen bg-white shadow-2xl">
          <div className="p-6 border-b border-gray-100">
            <h1 className="text-2xl font-bold bg-gradient-to-r from-green-600 to-blue-600 bg-clip-text text-transparent">
              MedSync
            </h1>
            <p className="text-sm text-gray-500 mt-1">Tableau de bord</p>
          </div>
          
          <nav className="p-4 space-y-1">
            {navigation.map((item) => (
              <Link
                key={item.name}
                to={item.href}
                className={`flex items-center p-3 rounded-xl transition-all duration-200 w-full ${
                  location.pathname === item.href || (item.exact && location.pathname === item.href)
                    ? 'bg-green-50 text-green-700 border-2 border-green-200 shadow-md'
                    : 'text-gray-700 hover:bg-gray-100 hover:text-gray-900 hover:shadow-sm'
                }`}
              >
                <item.icon className="h-5 w-5 mr-3 flex-shrink-0" />
                <span className="font-medium">{item.name}</span>
              </Link>
            ))}
          </nav>
          
          <div className="absolute bottom-0 left-0 right-0 p-4 border-t border-gray-100">
            <button
              onClick={logout}
              className="flex items-center w-full p-3 text-left text-red-600 hover:bg-red-50 rounded-xl transition-colors"
            >
              <FiLogOut className="h-5 w-5 mr-3" />
              Déconnexion
            </button>
          </div>
        </div>
      </div>

      {/* MOBILE OVERLAY */}
      {sidebarOpen && (
        <div 
          className="fixed inset-0 z-30 bg-black/50 lg:hidden" 
          onClick={() => setSidebarOpen(false)}
        />
      )}

      {/* MAIN CONTENT */}
      <div className="lg:ml-64">
        <header className="bg-white shadow-sm border-b border-gray-200 sticky top-0 z-20">
          <div className="px-4 sm:px-6 lg:px-8 py-4">
            <div className="flex items-center justify-between">
              <div className="flex items-center">
                <button
                  onClick={() => setSidebarOpen(true)}
                  className="lg:hidden p-2 ml-1 mr-4 text-gray-500 rounded-lg hover:bg-gray-100 focus:outline-none focus:ring-2 focus:ring-inset focus:ring-indigo-500"
                >
                  <span className="sr-only">Ouvrir menu</span>
                  <FiMenu className="h-6 w-6" aria-hidden="true" />
                </button>
                <h1 className="text-xl font-semibold text-gray-900">
                  {navigation.find(item => location.pathname.includes(item.href.split('/')[2]))?.name || 'Dashboard'}
                </h1>
              </div>
              <div className="flex items-center">
                <div className="ml-3 relative">
                  <div className="flex items-center">
                    <span className="text-sm font-medium text-gray-700 mr-2">{user?.nom}</span>
                    <button className="p-2 text-gray-400 hover:text-gray-500">
                      <FiBell className="h-5 w-5" />
                    </button>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </header>

        <main className="p-6">
          <Routes>
            <Route path="/" element={<DashboardHome />} />
            <Route path="/patients" element={<div className="p-8 bg-white rounded-xl shadow-sm"><h1 className="text-2xl font-bold mb-4">Mes Patients</h1><p>Liste des patients en cours...</p></div>} />
            <Route path="/consultations" element={<div className="p-8 bg-white rounded-xl shadow-sm"><h1 className="text-2xl font-bold mb-4">Consultations</h1><p>Consultations du jour...</p></div>} />
            <Route path="/rendez-vous" element={<div className="p-8 bg-white rounded-xl shadow-sm"><h1 className="text-2xl font-bold mb-4">Rendez-vous</h1><p>Prochains RDV...</p></div>} />
            <Route path="/dossiers" element={<div className="p-8 bg-white rounded-xl shadow-sm"><h1 className="text-2xl font-bold mb-4">Dossiers Médicaux</h1><p>Dossiers patients...</p></div>} />
            <Route path="/ordonnances" element={<div className="p-8 bg-white rounded-xl shadow-sm"><h1 className="text-2xl font-bold mb-4">Ordonnances</h1><p>Gestion ordonnances...</p></div>} />
            <Route path="*" element={<div className="p-8 text-center text-gray-500">Page non trouvée</div>} />
          </Routes>
        </main>
      </div>
    </div>
  );
};

export default MedecinDashboard;
