import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";

const PatientDashboard = () => {
  const [user, setUser] = useState(null);
  const [dossiers, setDossiers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const navigate = useNavigate();

  const getToken = () => localStorage.getItem("token");

  const fetchUser = async () => {
    try {
      const token = getToken();
      if (!token) throw new Error("Non connecté");

      const res = await axios.get("http://localhost:5000/api/users/me", {
        headers: { Authorization: `Bearer ${token}` },
      });

      if (res.data.role !== "Patient") {
        navigate("/login");
        return;
      }

      setUser(res.data);
    } catch (err) {
      console.error("Erreur récupération infos patient :", err);
      setError("Impossible de récupérer vos informations. Veuillez vous reconnecter.");
      navigate("/login");
    }
  };

  const fetchDossiers = async () => {
    setLoading(true);
    try {
      const token = getToken();
      if (!token) throw new Error("Non connecté");

      const res = await axios.get("http://localhost:5000/api/patient/dossiers", {
        headers: { Authorization: `Bearer ${token}` },
      });

      setDossiers(res.data);
    } catch (err) {
      console.error("Erreur récupération dossiers :", err);
      setError("Impossible de récupérer vos dossiers pour le moment.");
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    const init = async () => {
      await fetchUser();
      await fetchDossiers();
    };
    init();
  }, []);

  if (loading) return <div style={{ padding: "40px", textAlign: "center" }}>Chargement...</div>;

  return (
    <div style={{ padding: "20px" }}>
      <h1 style={{ marginBottom: "20px" }}>Bienvenue, {user?.fullName}</h1>

      {error && (
        <div style={{
          backgroundColor: "#fee",
          color: "#c33",
          padding: "12px",
          borderRadius: "8px",
          marginBottom: "20px",
          border: "1px solid #fcc",
        }}>
          {error}
        </div>
      )}

      <div style={{ marginBottom: "20px" }}>
        <button
          onClick={fetchDossiers}
          style={{
            padding: "10px 20px",
            borderRadius: "8px",
            backgroundColor: "#4CAF50",
            color: "white",
            border: "none",
            cursor: "pointer"
          }}
        >
          Actualiser mes dossiers
        </button>
      </div>

      {dossiers.length === 0 ? (
        <p>Aucun dossier trouvé.</p>
      ) : (
        <div style={{ display: "grid", gridTemplateColumns: "repeat(auto-fit, minmax(250px, 1fr))", gap: "20px" }}>
          {dossiers.map((dossier) => (
            <div key={dossier._id} style={{
              padding: "15px",
              border: "1px solid #ddd",
              borderRadius: "10px",
              backgroundColor: "#f9f9f9",
              boxShadow: "0 2px 6px rgba(0,0,0,0.1)"
            }}>
              <h3 style={{ marginBottom: "10px" }}>Résumé</h3>
              <p>{dossier.resumeMedical}</p>
              <p><strong>Médecin:</strong> {dossier.medecin?.fullName || "N/A"}</p>
              <p><strong>Date:</strong> {new Date(dossier.createdAt).toLocaleDateString()}</p>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default PatientDashboard;
